            "export_interval_seconds": {
              "type": "integer",
              "minimum": 10,
              "maximum": 300,
              "default": 60,
              "description": "Metrics export interval"
            },
            "metrics_endpoint": {
              "type": "string",
              "format": "uri",
              "description": "Prometheus metrics endpoint"
            }
          }
        },
        "logging_config": {
          "type": "object",
