            },
            "structured_logging": {
              "type": "boolean",
              "default": true,
              "description": "Enable structured JSON logging"
            },
            "audit_log_retention_days": {
              "type": "integer",
              "minimum": 30,
              "maximum": 2555,
              "default": 2555,
