            },
            "cumulative_hash_validation": {
              "type": "boolean",
              "default": true,
              "description": "Validate cumulative hash consistency"
            }
          }
        },
        "deduplication": {
          "type": "object",
          "properties": {
            "dedup_cache_size": {
              "type": "integer",
              "minimum": 1000,
              "maximum": 1000000,
              "default": 100000,
              "description": "Size of deduplication cache"
            },
            "dedup_cache_ttl_hours": {
              "type": "integer",
              "minimum": 1,
              "maximum": 168,
              "default": 24,
              "description": "Deduplication cache TTL"
            }
          }
        },
        "forensic_replay": {
          "type": "object",
          "properties": {
            "enabled": {
              "type": "boolean",
              "default": true,
              "description": "Enable forensic replay capability"
            },
            "checkpoint_retention_days": {
              "type": "integer",
              "minimum": 90,
              "maximum": 2555,
              "default": 2555,
              "description": "Checkpoint retention period"
            },
            "wal_retention_days": {
              "type": "integer",
              "minimum": 30,
              "maximum": 365,
              "default": 90,
              "description": "Write-ahead log retention period"
            }
          }
        }
      }
    }
  }
}

