              "properties": {
                "connection_timeout_ms": {
                  "type": "integer",
                  "default": 5000
                },
                "read_timeout_ms": {
                  "type": "integer",
                  "default": 30000
                }
              }
            }
          }
        }
      }
    },
    "monitoring_config": {
      "type": "object",
      "required": ["metrics_export", "logging_config", "alerting_config"],
      "properties": {
        "metrics_export": {
          "type": "object",
          "required": ["enabled", "export_interval_seconds", "metrics_endpoint"],
          "properties": {
            "enabled": {
              "type": "boolean",
              "default": true,
              "description": "Enable metrics export"
            },
            "export_interval_seconds": {
              "type": "integer",
              "minimum": 10,
              "maximum": 300,
              "default": 60,
              "description": "Metrics export interval"
            },
            "metrics_endpoint": {
              "type": "string",
              "format": "uri",
              "description": "Prometheus metrics endpoint"
            }
          }
        },
        "logging_config": {
          "type": "object",
          "required": ["log_level", "structured_logging", "audit_log_retention_days"],
          "properties": {
            "log_level": {
              "type": "string",
              "enum": ["DEBUG", "INFO", "WARNING", "ERROR"],
              "default": "INFO",
              "description": "Minimum log level"
            },
            "structured_logging": {
              "type": "boolean",
              "default": true,
              "description": "Enable structured JSON logging"
            },
            "audit_log_retention_days": {
              "type": "integer",
              "minimum": 30,
              "maximum": 2555,
              "default": 2555,
              "description": "Audit log retention period (7 years default)"
            }
          }
        },
        "alerting_config": {
          "type": "object",
          "required": ["enabled", "alert_rules"],
          "properties": {
            "enabled": {
              "type": "boolean",
              "default": true,
              "description": "Enable alerting system"
            },
            "alert_rules": {
              "type": "array",
              "items": {
                "type": "object",
                "required": ["rule_name", "condition", "severity", "notification_channels"],
                "properties": {
                  "rule_name": {
                    "type": "string",
                    "description": "Human-readable alert rule name"
                  },
                  "condition": {
                    "type": "string",
                    "description": "Alert condition expression"
                  },
                  "severity": {
                    "type": "string",
                    "enum": ["LOW", "MEDIUM", "HIGH", "CRITICAL"],
                    "description": "Alert severity level"
                  },
                  "notification_channels": {
                    "type": "array",
                    "items": {
                      "type": "string"
                    },
                    "description": "List of notification channels"
                  }
                }
              }
            }
          }
        }
      }
    },
    "quality_assurance": {
      "type": "object",
      "properties": {
        "checkpoint_validation": {
          "type": "object",
          "properties": {
            "signature_verification": {
              "type": "boolean",
              "default": true,
              "description": "Verify checkpoint signatures"
            },
            "chain_integrity_check": {
              "type": "boolean", 
              "default": true,
              "description": "Verify checkpoint chain integrity"
            },
            "cumulative_hash_validation": {
              "type": "boolean",
              "default": true,
              "description": "Validate cumulative hash consistency"
            }
          }
        },
        "deduplication": {
          "type": "object",
          "properties": {
            "dedup_cache_size": {
              "type": "integer",
              "minimum": 1000,
              "maximum": 1000000,
              "default": 100000,
              "description": "Size of deduplication cache"
            },
            "dedup_cache_ttl_hours": {
              "type": "integer",
              "minimum": 1,
              "maximum": 168,
              "default": 24,
              "description": "Deduplication cache TTL"
            }
          }
        },
        "forensic_replay": {
          "type": "object",
          "properties": {
            "enabled": {
              "type": "boolean",
              "default": true,
              "description": "Enable forensic replay capability"
            },
            "checkpoint_retention_days": {
              "type": "integer",
              "minimum": 90,
              "maximum": 2555,
              "default": 2555,
              "description": "Checkpoint retention period"
            },
            "wal_retention_days": {
              "type": "integer",
              "minimum": 30,
              "maximum": 365,
              "default": 90,
              "description": "Write-ahead log retention period"
            }
          }
        }
      }
    }
  }
}

