// Omar-Claude Internal Tools - Frontend Application

class WorkspaceBrowser {
    constructor() {
        this.currentFile = null;
        this.searchMode = false;
        this.init();
    }

    async init() {
        await this.loadWorkspaceStructure();
        this.setupEventListeners();
        this.updateStatus('Ready - Workspace loaded');
    }

    async loadWorkspaceStructure() {
        try {
            const response = await fetch('/api/structure');
            const structure = await response.json();
            this.renderFileTree(structure);
        } catch (error) {
            console.error('Error loading workspace structure:', error);
            this.updateStatus('Error loading workspace');
        }
    }

    renderFileTree(structure) {
        const fileTree = document.getElementById('fileTree');
        fileTree.innerHTML = '';

        if (structure.children && structure.children.length > 0) {
            structure.children.forEach(child => {
                fileTree.appendChild(this.createTreeItem(child));
            });
        }
    }

    createTreeItem(item) {
        const element = document.createElement('div');

        if (item.type === 'directory') {
            element.className = 'tree-item directory';
            element.textContent = `ðŸ“ ${item.name}`;
            element.onclick = () => this.toggleDirectory(element);

            if (item.children && item.children.length > 0) {
                const childContainer = document.createElement('div');
                childContainer.className = 'tree-children';
                childContainer.style.display = 'block'; // Show by default for better usability

                item.children.forEach(child => {
                    childContainer.appendChild(this.createTreeItem(child));
                });

                element.appendChild(childContainer);
            }
        } else if (item.type === 'file') {
            element.className = 'tree-item file';
            element.textContent = `ðŸ“„ ${item.name}`;
            element.onclick = () => this.selectFile(item.path, element);
        }

        return element;
    }

    toggleDirectory(element) {
        const childContainer = element.querySelector('.tree-children');
        if (childContainer) {
            const isVisible = childContainer.style.display !== 'none';
            childContainer.style.display = isVisible ? 'none' : 'block';

            // Update folder icon
            const text = element.firstChild.textContent;
            element.firstChild.textContent = isVisible 
                ? text.replace('ðŸ“', 'ðŸ“‚') 
                : text.replace('ðŸ“‚', 'ðŸ“');
        }
    }

    async selectFile(filePath, element) {
        // Update selection
        document.querySelectorAll('.tree-item.selected').forEach(el => {
            el.classList.remove('selected');
        });
        element.classList.add('selected');

        this.currentFile = filePath;
        this.updateStatus(`Loading ${filePath}...`);

        try {
            const response = await fetch(`/api/content/${filePath}`);
            const content = await response.json();

            if (response.ok) {
                this.displayContent(content);
                this.updateStatus(`Viewing ${filePath}`);
            } else {
                this.displayError(`Error loading ${filePath}: ${content.error}`);
                this.updateStatus('Error loading file');
            }
        } catch (error) {
            console.error('Error loading file:', error);
            this.displayError(`Failed to load ${filePath}`);
            this.updateStatus('Error loading file');
        }
    }

    displayContent(content) {
        const viewer = document.getElementById('contentViewer');

        viewer.innerHTML = `
            <div class="file-header">
                <h2>ðŸ“„ ${content.path}</h2>
                <p><em>Last modified: ${new Date().toLocaleDateString()}</em></p>
            </div>
            <div class="markdown-content">
                ${content.html}
            </div>
        `;
    }

    displayError(message) {
        const viewer = document.getElementById('contentViewer');
        viewer.innerHTML = `
            <div class="error">
                <h2>âš ï¸ Error</h2>
                <p>${message}</p>
            </div>
        `;
    }

    async performSearch(query) {
        if (!query.trim()) {
            this.exitSearchMode();
            return;
        }

        this.updateStatus(`Searching for "${query}"...`);

        try {
            const response = await fetch('/api/search', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ query })
            });

            const results = await response.json();
            this.displaySearchResults(results, query);
            this.updateStatus(`Found ${results.length} results for "${query}"`);
        } catch (error) {
            console.error('Search error:', error);
            this.updateStatus('Search failed');
        }
    }

    displaySearchResults(results, query) {
        this.searchMode = true;

        const fileTree = document.getElementById('fileTree');
        const searchResults = document.getElementById('searchResults');

        fileTree.style.display = 'none';
        searchResults.style.display = 'block';
        searchResults.innerHTML = '';

        if (results.length === 0) {
            searchResults.innerHTML = `
                <div class="search-result">
                    <div class="search-result-title">No results found</div>
                    <p>No matches found for "${query}"</p>
                </div>
            `;
            return;
        }

        // Sort by relevance
        results.sort((a, b) => b.relevance - a.relevance);

        results.forEach(result => {
            const resultElement = document.createElement('div');
            resultElement.className = 'search-result';
            resultElement.onclick = () => this.selectFileFromSearch(result.file);

            let matchesHtml = '';
            if (result.matches && result.matches.length > 0) {
                matchesHtml = result.matches.map(match => `
                    <div class="search-result-match">
                        Line ${match.line}: ${match.text}
                    </div>
                `).join('');
            }

            resultElement.innerHTML = `
                <div class="search-result-title">${result.title}</div>
                <div style="color: #888; font-size: 0.8rem; margin-bottom: 0.5rem;">${result.file}</div>
                ${matchesHtml}
            `;

            searchResults.appendChild(resultElement);
        });
    }

    async selectFileFromSearch(filePath) {
        this.exitSearchMode();

        // Find and select the file in the tree
        const fileElements = document.querySelectorAll('.tree-item.file');
        for (let element of fileElements) {
            if (element.textContent.includes(filePath.split('/').pop().replace('.md', ''))) {
                await this.selectFile(filePath, element);
                break;
            }
        }
    }

    exitSearchMode() {
        this.searchMode = false;
        document.getElementById('fileTree').style.display = 'block';
        document.getElementById('searchResults').style.display = 'none';
        document.getElementById('searchInput').value = '';
    }

    setupEventListeners() {
        const searchInput = document.getElementById('searchInput');
        const searchButton = document.getElementById('searchButton');

        searchButton.onclick = () => {
            this.performSearch(searchInput.value);
        };

        searchInput.onkeypress = (e) => {
            if (e.key === 'Enter') {
                this.performSearch(searchInput.value);
            }
        };

        // Exit search mode when input is cleared
        searchInput.oninput = (e) => {
            if (!e.target.value.trim() && this.searchMode) {
                this.exitSearchMode();
            }
        };

        // Keyboard shortcuts
        document.onkeydown = (e) => {
            if (e.ctrlKey && e.key === 'k') {
                e.preventDefault();
                searchInput.focus();
            }

            if (e.key === 'Escape' && this.searchMode) {
                this.exitSearchMode();
            }
        };
    }

    updateStatus(message) {
        document.getElementById('status').textContent = message;
    }
}

// Initialize the application
document.addEventListener('DOMContentLoaded', () => {
    new WorkspaceBrowser();
});

