input.content:
workspace "Omar-Claude Center of Excellence" "Human-AI Collaboration Architecture for $1M Business Vision" {

    model {
        # External Systems
        googleDrive = softwareSystem "Google Drive" "Cloud storage and synchronization" "External"
        git = softwareSystem "Git Repository" "Version control and collaboration" "External" 
        claudeCode = softwareSystem "Claude Code" "AI development environment" "External"

        # Core System
        omarclaude = softwareSystem "Omar-Claude CoE System" "Center of Excellence for Human-AI Collaboration" {

            # Human Actor
            omar = person "Omar El Mountassir" "Agentic Solo Founder, CEO & Strategic Decision Maker"

            # AI Agent
            claude = person "Claude CTO" "Chief Technology Officer AI Agent, Technical Implementation"

            # Core Containers
            workspace = container "Workspace Architecture" "Everything-in-One Living Place structure" "File System" {
                readme = component "README.md" "Central entry point and status"
                claudemd = component "CLAUDE.md" "AI instructions and workflow protocols" 
                profile = component "Profile System" "Omar context and collaboration patterns"
                contexts = component "Contexts Architecture" "C4 Model strategic intelligence"
                collaboration = component "Collaboration Hub" "Patterns, learnings, insights"
                pipelines = component "Pipelines Framework" "Business intelligence automation"
                shared = component "_shared System" "Bidirectional collaboration space"
                projects = component "Projects Management" "Active and archived implementations"
                requirements = component "Requirements Structure" "Organizational specifications"
            }

            # Pipeline Containers  
            dataPipeline = container "Data Pipeline" "Analytics and Business Intelligence" "Python/Node.js" {
                rawData = component "Raw Data Ingestion" "APIs, scraping, Omar exports"
                processor = component "Data Processor" "Cleaning, validation, transformation"
                analytics = component "Analytics Engine" "Insights, metrics, predictions"
                exports = component "Results Exporter" "Dashboards, reports, APIs"
            }

            contentPipeline = container "Content Pipeline" "Content creation and publishing automation" "Future" {
                ideation = component "Content Ideation" "Topic research and planning"
                creation = component "Content Creator" "Writing, editing, optimization"
                publishing = component "Content Publisher" "Distribution and promotion"
            }

            # Meta-Architecture System
            metaArch = container "Meta-Architecture Framework" "Self-evolving autonomous system" "JSON/DSL" {
                dnaTemplates = component "DNA Templates" "Self-replicating patterns"
                evolution = component "Evolution Engine" "Autonomous optimization"
                agents = component "Sub-Agents Profiles" "Specialized AI capabilities"
                hooks = component "Intelligent Hooks" "Event-driven automation"
            }
        }

        # Relationships - Human Interactions
        omar -> workspace "Provides strategic direction and requirements"
        omar -> shared "Deposits content for analysis via inbox/"
        omar -> collaboration "Reviews learnings and provides feedback"

        # Relationships - AI Interactions  
        claude -> workspace "Implements technical solutions and maintains structure"
        claude -> pipelines "Develops and optimizes automation pipelines"
        claude -> metaArch "Evolves system capabilities autonomously"
        claude -> shared "Processes content and generates insights"

        # Relationships - System Integration
        workspace -> googleDrive "Synchronizes content and backups"
        workspace -> git "Version controls all changes and evolution"
        pipelines -> shared "Retrieves data from exports/ and inbox/"
        shared -> pipelines "Feeds processed content to raw/ ingestion"
        metaArch -> workspace "Optimizes structure based on usage patterns"

        # External Integrations
        claude -> claudeCode "Operates within development environment"
        dataPipeline -> googleDrive "Accesses datasets and exports results"
        collaboration -> git "Tracks pattern evolution and learnings"
    }

    views {
        # System Landscape
        systemLandscape "SystemLandscape" {
            include *
            autoLayout
            title "Omar-Claude Center of Excellence - System Landscape"
            description "High-level view of Human-AI collaboration ecosystem for $1M business vision"
        }

        # System Context
        systemContext omarclaude "SystemContext" {
            include *
            autoLayout
            title "Omar-Claude CoE - System Context"  
            description "Core system boundaries and external integrations"
        }

        # Container View
        container omarclaude "ContainerView" {
            include *
            autoLayout
            title "Omar-Claude CoE - Container Architecture"
            description "Major containers and their relationships within the system"
        }

        # Workspace Components
        component workspace "WorkspaceComponents" {
            include *
            autoLayout
            title "Workspace Architecture - Components"
            description "Everything-in-One Living Place component structure"
        }

        # Data Pipeline Components
        component dataPipeline "DataPipelineComponents" {
            include *
            autoLayout  
            title "Data Pipeline - Components"
            description "Analytics and Business Intelligence pipeline components"
        }

        # Meta-Architecture Components
        component metaArch "MetaArchComponents" {
            include *
            autoLayout
            title "Meta-Architecture Framework - Components" 
            description "Self-evolving autonomous system components"
        }

        # Dynamic Views
        dynamic omarclaude "ContentProcessingFlow" "Content Processing Workflow" {
            omar -> shared "1. Deposits content in inbox/"
            shared -> claude "2. Auto-detects and analyzes content"
            claude -> pipelines "3. Extracts data to appropriate pipeline"
            pipelines -> analytics "4. Processes and generates insights"
            analytics -> contexts "5. Places insights in architecture"  
            shared -> omar "6. Makes results available in exports/"
            autoLayout
            title "Content Processing Flow"
            description "How content flows from Omar through the system to generate business value"
        }

        dynamic omarclaude "ArchitectureEvolution" "Architecture Evolution Process" {
            omar -> collaboration "1. Provides feedback on system performance" 
            collaboration -> metaArch "2. Triggers evolution analysis"
            metaArch -> dnaTemplates "3. Generates improved patterns"
            dnaTemplates -> workspace "4. Applies structural optimizations"
            workspace -> git "5. Commits evolutionary changes"
            git -> omar "6. Notifies of system improvements"
            autoLayout
            title "Architecture Evolution"
            description "How the system continuously evolves based on usage and feedback"
        }

        # Deployment View
        deploymentEnvironment "Production" {
            deploymentNode "Omar Workspace" "Windows 11 Development Environment" "Windows" {
                deploymentNode "Local Drive" "C:/Users/omarm/Mon Drive/Omar-Claude-Workspace/" "File System" {
                    containerInstance workspace
                    containerInstance metaArch
                }
                deploymentNode "Pipeline Runtime" "Python/Node.js Environment" "Runtime" {
                    containerInstance dataPipeline
                }
            }
            deploymentNode "Cloud Storage" "Google Drive" "Cloud" {
                softwareSystemInstance googleDrive
            }
            deploymentNode "Version Control" "Git Remote" "Cloud" {
                softwareSystemInstance git
            }
            deploymentNode "AI Platform" "Anthropic Claude" "Cloud" {
                softwareSystemInstance claudeCode
            }
            autoLayout
            title "Production Deployment"
            description "Production deployment architecture"
        }

        # Styling
        styles {
            element "Person" {
                color #ffffff
                fontSize 22
                shape Person
            }
            element "Omar" {
                background #1f77b4
                color #ffffff
            }
            element "Claude" {
                background #ff7f0e  
                color #ffffff
            }
            element "Software System" {
                background #1168bd
                color #ffffff
            }
            element "External" {
                background #999999
                color #ffffff
            }
            element "Container" {
                background #438dd5
                color #ffffff
            }
            element "Component" {
                background #85bbf0
                color #000000
            }
            element "Future" {
                background #cccccc
                color #000000
                opacity 50
            }
        }
    }
}

