#!/usr/bin/env node

const express = require('express');
const path = require('path');
const fs = require('fs');
const { marked } = require('marked');
const matter = require('gray-matter');

const app = express();
const PORT = 3000;
const WORKSPACE_ROOT = path.resolve('../../..');

// Middleware
app.use(express.static(path.join(__dirname, '../browser')));
app.use(express.json());

// API Routes

// Get workspace structure
app.get('/api/structure', (req, res) => {
  try {
    const structure = getWorkspaceStructure(WORKSPACE_ROOT);
    res.json(structure);
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// Get file content
app.get('/api/content/*', (req, res) => {
  try {
    const filePath = req.params[0];
    const fullPath = path.join(WORKSPACE_ROOT, filePath);

    // Security check - ensure path is within workspace
    if (!fullPath.startsWith(path.resolve(WORKSPACE_ROOT))) {
      return res.status(403).json({ error: 'Access denied' });
    }

    if (!fs.existsSync(fullPath)) {
      return res.status(404).json({ error: 'File not found' });
    }

    const content = fs.readFileSync(fullPath, 'utf8');
    const parsed = matter(content);

    res.json({
      path: filePath,
      content: content,
      data: parsed.data,
      body: parsed.content,
      html: marked(parsed.content)
    });
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// Search functionality
app.post('/api/search', (req, res) => {
  try {
    const { query } = req.body;
    const results = searchWorkspace(WORKSPACE_ROOT, query);
    res.json(results);
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

// Helper Functions

function getWorkspaceStructure(rootPath) {
  const structure = {
    name: 'Omar-Claude-Workspace',
    path: '',
    type: 'directory',
    children: []
  };

  const importantPaths = [
    'contexts',
    'collaboration',
    'profile',
    'requirements',
    'projects/current'
  ];

  importantPaths.forEach(dirPath => {
    const fullPath = path.join(rootPath, dirPath);
    if (fs.existsSync(fullPath)) {
      structure.children.push(buildDirectoryTree(fullPath, dirPath));
    }
  });

  return structure;
}

function buildDirectoryTree(fullPath, relativePath) {
  const stats = fs.statSync(fullPath);
  const name = path.basename(fullPath);

  if (stats.isFile() && name.endsWith('.md')) {
    return {
      name,
      path: relativePath,
      type: 'file',
      size: stats.size,
      modified: stats.mtime
    };
  } else if (stats.isDirectory()) {
    const children = [];
    const items = fs.readdirSync(fullPath);

    items.forEach(item => {
      const itemPath = path.join(fullPath, item);
      const itemRelativePath = path.join(relativePath, item);

      try {
        const child = buildDirectoryTree(itemPath, itemRelativePath);
        if (child) {
          children.push(child);
        }
      } catch (error) {
        // Skip files/directories we can't access
        console.log(`Skipping ${itemPath}: ${error.message}`);
      }
    });

    return {
      name,
      path: relativePath,
      type: 'directory',
      children: children.sort((a, b) => {
        if (a.type !== b.type) {
          return a.type === 'directory' ? -1 : 1;
        }
        return a.name.localeCompare(b.name);
      })
    };
  }

  return null;
}

function searchWorkspace(rootPath, query) {
  const results = [];
  const searchPaths = [
    'contexts',
    'collaboration',
    'profile',
    'requirements'
  ];

  searchPaths.forEach(dirPath => {
    const fullPath = path.join(rootPath, dirPath);
    if (fs.existsSync(fullPath)) {
      searchDirectory(fullPath, dirPath, query, results);
    }
  });

  return results.slice(0, 50); // Limit results
}

function searchDirectory(fullPath, relativePath, query, results) {
  if (!fs.existsSync(fullPath)) return;

  const items = fs.readdirSync(fullPath);

  items.forEach(item => {
    const itemPath = path.join(fullPath, item);
    const itemRelativePath = path.join(relativePath, item);
    const stats = fs.statSync(itemPath);

    if (stats.isFile() && item.endsWith('.md')) {
      try {
        const content = fs.readFileSync(itemPath, 'utf8');
        const parsed = matter(content);
        const lowerQuery = query.toLowerCase();

        // Search in title, content, and frontmatter
        const titleMatch = item.toLowerCase().includes(lowerQuery);
        const contentMatch = parsed.content.toLowerCase().includes(lowerQuery);
        const frontmatterMatch = JSON.stringify(parsed.data).toLowerCase().includes(lowerQuery);

        if (titleMatch || contentMatch || frontmatterMatch) {
          // Find context lines
          const lines = parsed.content.split('\n');
          const matchingLines = [];

          lines.forEach((line, index) => {
            if (line.toLowerCase().includes(lowerQuery)) {
              matchingLines.push({
                line: index + 1,
                text: line.trim(),
                context: lines.slice(Math.max(0, index - 1), index + 2).join(' ')
              });
            }
          });

          results.push({
            file: itemRelativePath,
            title: item.replace('.md', ''),
            matches: matchingLines.slice(0, 3), // Limit matches per file
            relevance: (titleMatch ? 10 : 0) + matchingLines.length
          });
        }
      } catch (error) {
        console.log(`Error searching ${itemPath}: ${error.message}`);
      }
    } else if (stats.isDirectory()) {
      searchDirectory(itemPath, itemRelativePath, query, results);
    }
  });
}

// Serve main interface
app.get('/', (req, res) => {
  res.sendFile(path.join(__dirname, '../browser/index.html'));
});

// Start server
app.listen(PORT, () => {
  console.log(`🛠️  Internal Tools Server running at http://localhost:${PORT}`);
  console.log(`📁 Workspace: ${WORKSPACE_ROOT}`);
  console.log(`🔍 Ready for content browsing and search`);
});

module.exports = app;

