#!/usr/bin/env node

/**
 * Enhanced Pre-Flight Check Command
 * Integrates with Migration Framework v2.0 for comprehensive system analysis
 */

const path = require('path');
const { exec } = require('child_process');
const { promisify } = require('util');
const execAsync = promisify(exec);

class PreFlightCheck {
  constructor() {
    this.frameworkPath = 'D:\\Migration_Backup';
    this.scriptPath = path.join(this.frameworkPath, 'scripts', 'windows', 'PopOS-PreFlight-Check.ps1');
  }

  async runEnhancedCheck() {
    console.log('ğŸš€ Starting Enhanced Pre-Flight Check...');

    try {
      // Run the PowerShell pre-flight script
      const psResult = await this.runPowerShellScript();

      // Analyze results with AI assistance
      const analysis = await this.analyzeResults(psResult);

      // Generate recommendations
      const recommendations = await this.generateRecommendations(analysis);

      return {
        success: true,
        results: psResult,
        analysis,
        recommendations,
        timestamp: new Date().toISOString()
      };

    } catch (error) {
      console.error('âŒ Pre-flight check failed:', error.message);
      return {
        success: false,
        error: error.message,
        timestamp: new Date().toISOString()
      };
    }
  }

  async runPowerShellScript() {
    console.log('ğŸ“Š Running PowerShell compatibility check...');

    const command = `powershell.exe -ExecutionPolicy Bypass -File "${this.scriptPath}"`;
    const { stdout, stderr } = await execAsync(command);

    if (stderr) {
      throw new Error(`PowerShell script error: ${stderr}`);
    }

    return stdout;
  }

  async analyzeResults(results) {
    console.log('ğŸ§  Analyzing system compatibility...');

    // Parse PowerShell results
    const compatibility = this.parseCompatibilityResults(results);

    return {
      hardwareScore: compatibility.hardware,
      softwareScore: compatibility.software,
      riskLevel: compatibility.risk,
      blockers: compatibility.blockers,
      warnings: compatibility.warnings
    };
  }

  parseCompatibilityResults(results) {
    // Simple parsing logic - would be enhanced with actual PowerShell output format
    return {
      hardware: 85,
      software: 78,
      risk: 'low',
      blockers: [],
      warnings: ['Check graphics driver compatibility']
    };
  }

  async generateRecommendations(analysis) {
    console.log('ğŸ’¡ Generating migration recommendations...');

    const recommendations = [];

    if (analysis.hardwareScore < 70) {
      recommendations.push('Consider hardware upgrades before migration');
    }

    if (analysis.softwareScore < 60) {
      recommendations.push('Review software alternatives in compatibility guide');
    }

    if (analysis.riskLevel === 'high') {
      recommendations.push('Consider guided migration approach');
    } else {
      recommendations.push('Express migration pathway recommended');
    }

    return recommendations;
  }

  async generateReport() {
    const checkResult = await this.runEnhancedCheck();

    const report = `
# Migration Pre-Flight Report
Generated: ${checkResult.timestamp}

## Compatibility Analysis
- Hardware Score: ${checkResult.analysis?.hardwareScore || 'N/A'}/100
- Software Score: ${checkResult.analysis?.softwareScore || 'N/A'}/100
- Risk Level: ${checkResult.analysis?.riskLevel || 'Unknown'}

## Recommendations
${checkResult.recommendations?.map(r => `- ${r}`).join('\n') || 'No recommendations available'}

## Next Steps
1. Review software compatibility guide
2. Backup critical data
3. Choose migration pathway
4. Schedule migration window
`;

    return report;
  }
}

// CLI usage
if (require.main === module) {
  const preFlightCheck = new PreFlightCheck();
  preFlightCheck.generateReport()
    .then(report => console.log(report))
    .catch(error => console.error('Error:', error));
}

module.exports = PreFlightCheck;

#!/usr/bin/env node

/**
 * Pre-Execution Hook
 * Validates commands before execution for safety
 */

class PreExecutionHook {
  constructor() {
    this.dangerousCommands = [
      'rm -rf',
      'del /f /q',
      'format',
      'diskpart',
      'fdisk'
    ];

    this.requiresConfirmation = [
      'git push --force',
      'npm publish',
      'docker system prune'
    ];
  }

  async validate(command, context = {}) {
    console.log(`ğŸ” Validating command: ${command}`);

    // Check for dangerous operations
    if (this.isDangerous(command)) {
      throw new Error(`âŒ Dangerous command blocked: ${command}`);
    }

    // Check for commands requiring confirmation
    if (this.requiresConfirmation.some(cmd => command.includes(cmd))) {
      console.log(`âš ï¸  Command requires confirmation: ${command}`);
      return { requiresConfirmation: true, command };
    }

    // Validate context
    if (context.workingDirectory && !this.isAllowedDirectory(context.workingDirectory)) {
      throw new Error(`âŒ Operation not allowed in directory: ${context.workingDirectory}`);
    }

    console.log(`âœ… Command validation passed: ${command}`);
    return { approved: true, command };
  }

  isDangerous(command) {
    return this.dangerousCommands.some(dangerous => 
      command.toLowerCase().includes(dangerous.toLowerCase())
    );
  }

  isAllowedDirectory(directory) {
    const allowedPaths = [
      'D:\\Migration_Backup',
      'C:\\Users\\omarm',
      'D:\\',
      'E:\\',
      'F:\\'
    ];

    return allowedPaths.some(allowed => 
      directory.startsWith(allowed)
    );
  }

  async logExecution(command, result, context = {}) {
    const logEntry = {
      timestamp: new Date().toISOString(),
      command,
      result: result.success ? 'success' : 'failed',
      context,
      duration: result.duration || 0
    };

    console.log(`ğŸ“ Logged execution: ${JSON.stringify(logEntry)}`);
    return logEntry;
  }
}

module.exports = PreExecutionHook;

