    };

    // Adjust for risk factors
    const adjustedTimelines = this.adjustForRisk(baseTimelines, riskAssessment);

    // Add buffer based on migration mode
    const finalTimelines = this.addTimelineBuffers(adjustedTimelines);

    return {
