    const weights = {
      hardware: 0.3,
      software: 0.25,
      drivers: 0.2,
      workflows: 0.15,
      security: 0.1
    };

    let score = 0;
    for (const [category, weight] of Object.entries(weights)) {
      if (compatibility[category] && compatibility[category].score) {
        score += compatibility[category].score * weight;
      }
    }

    return Math.round(score);
  }

  assessMigrationDifficulty(compatibility) {
    const score = compatibility.overallCompatibility;
    if (score >= 80) return 'easy';
    if (score >= 60) return 'moderate';
    return 'challenging';
