    const difficulty = this.assessMigrationDifficulty(compatibility);

    switch (difficulty) {
      case 'easy': return 'express';
      case 'moderate': return 'guided';
      case 'challenging': return 'conservative';
      default: return 'guided';
    }
  }

  calculateOverallRisk(risks) {
    const riskLevels = Object.values(risks).map(risk => {
      if (typeof risk === 'string') {
        switch (risk) {
          case 'low': return 1;
          case 'medium': return 2;
          case 'high': return 3;
          default: return 2;
        }
      }
      return 2;
    });

    const averageRisk = riskLevels.reduce((a, b) => a + b, 0) / riskLevels.length;

    if (averageRisk <= 1.5) return 'low';
    if (averageRisk <= 2.5) return 'medium';
    return 'high';
  }

