        console.log('\nâœ… Migration plan generated successfully!');
        console.log(`ğŸ“Š Timeline: ${result.plan.timeline.totalDuration}`);
        console.log(`âš ï¸ Risk Level: ${result.plan.riskAssessment.overallRisk}`);
        console.log(`ğŸ’¡ Recommendation: ${result.plan.summary.recommendation}`);
      } else {
        console.error('âŒ Migration planning failed:', result.error);
      }
    })
    .catch(error => console.error('Error:', error));
}

module.exports = MigrationPlan;

#!/usr/bin/env node

/**
 * Script Generator Command
 * Generates automation scripts for migration tasks
 * Reusable for any migration scenario with configurable templates
 */

const fs = require('fs');
const path = require('path');

class ScriptGenerator {
  constructor(options = {}) {
    this.frameworkPath = options.frameworkPath || 'D:\\Migration_Backup';
    this.sourceOS = options.sourceOS || 'Windows';
    this.targetOS = options.targetOS || 'Pop!_OS';
    this.scriptType = options.scriptType || 'bash';
    this.userProfile = options.userProfile || 'general';

    // Script templates and configurations
    this.templates = {
      'pre-flight': this.getPreFlightTemplate(),
      'data-backup': this.getDataBackupTemplate(),
      'system-analysis': this.getSystemAnalysisTemplate(),
      'driver-install': this.getDriverInstallTemplate(),
      'software-install': this.getSoftwareInstallTemplate(),
      'configuration': this.getConfigurationTemplate(),
      'optimization': this.getOptimizationTemplate(),
      'validation': this.getValidationTemplate(),
      'rollback': this.getRollbackTemplate()
    };
  }

  async generateScript(scriptName, options = {}) {
    console.log(`ğŸ“ Generating ${scriptName} script...`);

    try {
      const template = this.templates[scriptName];
      if (!template) {
        throw new Error(`Unknown script template: ${scriptName}`);
      }

      const script = await this.buildScript(template, options);
      const filename = await this.saveScript(script, scriptName, options);

      return {
        success: true,
        scriptName,
        filename,
        script,
        instructions: await this.generateInstructions(scriptName),
        dependencies: await this.identifyDependencies(script)
      };

    } catch (error) {
      console.error(`âŒ Script generation failed: ${error.message}`);
      return {
        success: false,
        error: error.message,
        scriptName
      };
    }
  }

  async generateScriptSuite(suiteType, options = {}) {
    console.log(`ğŸ“¦ Generating ${suiteType} script suite...`);

    const suites = {
      'complete-migration': [
        'pre-flight', 'data-backup', 'system-analysis',
        'driver-install', 'software-install', 'configuration',
        'optimization', 'validation'
      ],
      'pre-migration': ['pre-flight', 'data-backup', 'system-analysis'],
      'post-migration': ['driver-install', 'software-install', 'configuration', 'optimization'],
      'maintenance': ['optimization', 'validation'],
      'recovery': ['rollback', 'validation']
    };

    const scripts = suites[suiteType];
    if (!scripts) {
      throw new Error(`Unknown script suite: ${suiteType}`);
    }

    const results = {};
    const generatedScripts = [];

    for (const scriptName of scripts) {
      const result = await this.generateScript(scriptName, options);
      results[scriptName] = result;
      if (result.success) {
        generatedScripts.push(result.filename);
      }
    }

    // Generate master script to run all scripts in sequence
    const masterScript = await this.generateMasterScript(suiteType, generatedScripts, options);

    return {
      success: true,
      suiteType,
      scripts: results,
      masterScript,
      totalScripts: scripts.length,
      successfulScripts: generatedScripts.length,
      instructions: await this.generateSuiteInstructions(suiteType)
    };
  }

  async buildScript(template, options = {}) {
    let script = template.content;

    // Replace placeholders with actual values
    const replacements = {
      '{{FRAMEWORK_PATH}}': this.frameworkPath,
      '{{SOURCE_OS}}': this.sourceOS,
      '{{TARGET_OS}}': this.targetOS,
      '{{USER_PROFILE}}': this.userProfile,
      '{{TIMESTAMP}}': new Date().toISOString(),
      '{{SCRIPT_VERSION}}': '1.0.0',
      ...options.customReplacements || {}
    };

    for (const [placeholder, value] of Object.entries(replacements)) {
      script = script.replace(new RegExp(placeholder, 'g'), value);
    }

    // Add common functions and error handling
    script = await this.addCommonFunctions(script);
    script = await this.addErrorHandling(script);
    script = await this.addLogging(script);

    return {
      content: script,
      metadata: {
        template: template.name,
        generated: new Date().toISOString(),
        framework: 'Migration Framework v2.0',
        options
      }
    };
  }

  async saveScript(script, scriptName, options = {}) {
    const scriptsDir = path.join(this.frameworkPath, 'scripts', 'generated');

    // Ensure directory exists
    if (!fs.existsSync(scriptsDir)) {
      fs.mkdirSync(scriptsDir, { recursive: true });
    }

    const extension = this.scriptType === 'powershell' ? '.ps1' : '.sh';
    const timestamp = new Date().toISOString().split('T')[0];
    const filename = `${scriptName}-${timestamp}${extension}`;
    const filepath = path.join(scriptsDir, filename);

    // Write script content
    fs.writeFileSync(filepath, script.content);

    // Write metadata
    const metadataFile = filepath.replace(extension, '.meta.json');
    fs.writeFileSync(metadataFile, JSON.stringify(script.metadata, null, 2));

    // Make executable (on Unix-like systems)
    if (extension === '.sh') {
      try {
        fs.chmodSync(filepath, '755');
      } catch (error) {
        console.warn(`âš ï¸ Could not make script executable: ${error.message}`);
      }
    }

    console.log(`ğŸ’¾ Script saved: ${filepath}`);
    return filename;
  }

  async generateMasterScript(suiteType, scriptFiles, options = {}) {
    const masterContent = this.buildMasterScriptContent(suiteType, scriptFiles);
    const masterScript = {
      content: masterContent,
      metadata: {
        type: 'master-script',
        suite: suiteType,
        scripts: scriptFiles,
        generated: new Date().toISOString()
      }
    };

    const filename = await this.saveScript(masterScript, `master-${suiteType}`, options);
    return { filename, content: masterContent };
  }

  buildMasterScriptContent(suiteType, scriptFiles) {
    const scriptDir = path.join(this.frameworkPath, 'scripts', 'generated').replace(/\\/g, '/');

    return `#!/bin/bash
# Master Migration Script - ${suiteType}
# Generated: ${new Date().toISOString()}
# Framework: Migration Framework v2.0

set -e  # Exit on error
set -u  # Exit on undefined variable

SCRIPT_DIR="${scriptDir}"
LOG_FILE="\$SCRIPT_DIR/master-${suiteType}-\$(date +%Y%m%d-%H%M%S).log"

echo "ğŸš€ Starting ${suiteType} migration suite..." | tee "\$LOG_FILE"
echo "ğŸ“ Script directory: \$SCRIPT_DIR" | tee -a "\$LOG_FILE"
echo "ğŸ“ Log file: \$LOG_FILE" | tee -a "\$LOG_FILE"

# Function to run script with error handling
run_script() {
    local script_name="\$1"
    echo "â–¶ï¸ Running \$script_name..." | tee -a "\$LOG_FILE"

    if [ -f "\$SCRIPT_DIR/\$script_name" ]; then
        if bash "\$SCRIPT_DIR/\$script_name" 2>&1 | tee -a "\$LOG_FILE"; then
            echo "âœ… \$script_name completed successfully" | tee -a "\$LOG_FILE"
        else
            echo "âŒ \$script_name failed with exit code \$?" | tee -a "\$LOG_FILE"
            echo "ğŸ›‘ Stopping execution due to error" | tee -a "\$LOG_FILE"
            exit 1
        fi
    else
        echo "âš ï¸ Script not found: \$script_name" | tee -a "\$LOG_FILE"
    fi
    echo "" | tee -a "\$LOG_FILE"
}

# Execute scripts in sequence
${scriptFiles.map(script => `run_script "${script}"`).join('\n')}

echo "ğŸ‰ ${suiteType} migration suite completed successfully!" | tee -a "\$LOG_FILE"
echo "ğŸ“Š Total scripts executed: ${scriptFiles.length}" | tee -a "\$LOG_FILE"
echo "ğŸ“ Full log available at: \$LOG_FILE" | tee -a "\$LOG_FILE"
`;
  }

