#!/usr/bin/env node

/**
 * Framework Sync Command
 * Keeps Migration Framework updated and synchronized
 * Handles version management, updates, and consistency checks
 */

const fs = require('fs');
const path = require('path');

class FrameworkSync {
  constructor(options = {}) {
    this.frameworkPath = options.frameworkPath || 'D:\\Migration_Backup';
    this.syncMode = options.mode || 'check'; // check, update, force, repair
    this.backupBeforeSync = options.backup !== false;
    this.remoteRepository = options.repository || null;
    this.autoFix = options.autoFix || false;
  }

  async syncFramework(options = {}) {
    console.log('ğŸ”„ Starting Framework Sync process...');

    try {
      // Phase 1: Pre-sync validation
      const validation = await this.validateFramework();

      // Phase 2: Check for updates
      const updateCheck = await this.checkForUpdates();

      // Phase 3: Backup if needed
      const backup = this.backupBeforeSync ? await this.createBackup() : null;

      // Phase 4: Perform sync operation
      const syncResult = await this.performSync(validation, updateCheck, options);

      // Phase 5: Post-sync validation
      const postValidation = await this.validateFramework();

      // Phase 6: Generate sync report
      const report = await this.generateSyncReport(validation, updateCheck, syncResult, postValidation);

      return {
        success: true,
        validation,
        updateCheck,
        backup,
        syncResult,
        postValidation,
        report,
        recommendations: await this.generateRecommendations(report)
      };

    } catch (error) {
      console.error('âŒ Framework sync failed:', error.message);
      return {
        success: false,
        error: error.message,
        partialResults: await this.gatherPartialResults()
      };
    }
  }

  async validateFramework() {
    console.log('ğŸ“‹ Validating framework structure...');

    const validation = {
      structure: await this.validateStructure(),
      files: await this.validateFiles(), 
      configuration: await this.validateConfiguration(),
      dependencies: await this.validateDependencies(),
      permissions: await this.validatePermissions(),
      integrity: await this.validateIntegrity()
    };

    return {
      ...validation,
      overallHealth: this.calculateHealthScore(validation),
      issues: this.identifyIssues(validation),
      warnings: this.identifyWarnings(validation)
    };
  }

  async checkForUpdates() {
    console.log('ğŸ” Checking for framework updates...');

    const currentVersion = await this.getCurrentVersion();
    const latestVersion = await this.getLatestVersion();
    const changelog = await this.getChangelog(currentVersion, latestVersion);

    return {
      currentVersion,
      latestVersion,
      updateAvailable: this.compareVersions(currentVersion, latestVersion) < 0,
      changelog,
      updateType: this.determineUpdateType(currentVersion, latestVersion),
      compatibility: await this.checkUpdateCompatibility(currentVersion, latestVersion)
    };
  }

  async createBackup() {
    console.log('ğŸ’¾ Creating framework backup...');

    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const backupDir = path.join(this.frameworkPath, 'backups', `framework-backup-${timestamp}`);

    // Create backup directory
    fs.mkdirSync(backupDir, { recursive: true });

    // Backup critical framework files
    const criticalPaths = [
      '.claude',
      'INDEX.md',
      'ROADMAP.md',
      'VERSION',
      'scripts',
      'guides',
      'resources',
      'templates'
    ];

    const backupResults = [];

    for (const criticalPath of criticalPaths) {
      const sourcePath = path.join(this.frameworkPath, criticalPath);
      const targetPath = path.join(backupDir, criticalPath);

      try {
        if (fs.existsSync(sourcePath)) {
          await this.copyRecursively(sourcePath, targetPath);
          backupResults.push({ path: criticalPath, status: 'success' });
        } else {
          backupResults.push({ path: criticalPath, status: 'missing' });
        }
      } catch (error) {
        backupResults.push({ path: criticalPath, status: 'failed', error: error.message });
      }
    }

    // Generate backup manifest
    const manifest = {
      timestamp,
      frameworkVersion: await this.getCurrentVersion(),
      backupPath: backupDir,
      results: backupResults,
      totalSize: await this.calculateDirectorySize(backupDir)
    };

    fs.writeFileSync(
      path.join(backupDir, 'backup-manifest.json'),
      JSON.stringify(manifest, null, 2)
    );

    return manifest;
  }

  async performSync(validation, updateCheck, options = {}) {
    console.log('âš¡ Performing framework synchronization...');

    const syncOperations = [];

    // Fix structural issues
    if (validation.issues.length > 0) {
      syncOperations.push(await this.fixStructuralIssues(validation.issues));
    }

    // Update framework files if updates available
    if (updateCheck.updateAvailable && (this.syncMode === 'update' || this.syncMode === 'force')) {
      syncOperations.push(await this.updateFrameworkFiles(updateCheck));
    }

    // Repair corrupted files
    if (validation.integrity.corrupted.length > 0) {
      syncOperations.push(await this.repairCorruptedFiles(validation.integrity.corrupted));
    }

    // Sync configurations
    syncOperations.push(await this.syncConfigurations());

    // Update dependencies
    syncOperations.push(await this.updateDependencies());

    // Regenerate derived files
    syncOperations.push(await this.regenerateDerivedFiles());

    return {
      operations: syncOperations,
      totalOperations: syncOperations.length,
      successfulOperations: syncOperations.filter(op => op.success).length,
      failedOperations: syncOperations.filter(op => !op.success),
      duration: this.calculateSyncDuration()
    };
  }

  async validateStructure() {
    const requiredStructure = {
      '.claude': {
        type: 'directory',
        required: true,
        children: {
          'agents': { type: 'directory', required: true },
          'commands': { type: 'directory', required: true },
          'hooks': { type: 'directory', required: true },
          'templates': { type: 'directory', required: true },
          'status': { type: 'directory', required: true },
          'logs': { type: 'directory', required: true },
          'settings.local.json': { type: 'file', required: true }
        }
      },
      'scripts': { type: 'directory', required: true },
      'guides': { type: 'directory', required: true },
      'resources': { type: 'directory', required: true },
      'INDEX.md': { type: 'file', required: true },
      'VERSION': { type: 'file', required: true }
    };

    return await this.validateStructureRecursive(this.frameworkPath, requiredStructure);
  }

  async validateFiles() {
    const criticalFiles = [
      'INDEX.md',
      'VERSION',
      'ROADMAP.md',
      '.claude/settings.local.json',
      '.claude/agents/migration-specialist.js',
      '.claude/commands/pre-flight.js'
    ];

    const fileValidation = {};

    for (const file of criticalFiles) {
      const filePath = path.join(this.frameworkPath, file);
      fileValidation[file] = {
        exists: fs.existsSync(filePath),
        readable: await this.isFileReadable(filePath),
        size: await this.getFileSize(filePath),
        lastModified: await this.getFileLastModified(filePath),
        checksum: await this.calculateFileChecksum(filePath)
      };
    }

    return fileValidation;
  }

  async validateConfiguration() {
    const configValidation = {
      claudeSettings: await this.validateClaudeSettings(),
      mcpServers: await this.validateMcpServers(),
      frameworkConfig: await this.validateFrameworkConfig(),
      environmentVariables: await this.validateEnvironmentVariables()
    };

    return configValidation;
  }

  async validateDependencies() {
    const dependencies = {
      nodejs: await this.checkNodeJsVersion(),
      npm: await this.checkNpmVersion(),
      git: await this.checkGitVersion(),
      claudeCode: await this.checkClaudeCodeVersion(),
      mcpServers: await this.checkMcpServers()
    };

    return {
      ...dependencies,
      allSatisfied: Object.values(dependencies).every(dep => dep.satisfied),
      missingDependencies: Object.entries(dependencies)
        .filter(([name, dep]) => !dep.satisfied)
        .map(([name]) => name)
    };
  }

  async updateFrameworkFiles(updateCheck) {
    console.log('ğŸ“¥ Updating framework files...');

    if (!updateCheck.updateAvailable) {
      return { success: true, message: 'No updates available', filesUpdated: 0 };
    }

    const updateOperations = [];

    // Update VERSION file
    updateOperations.push(await this.updateVersionFile(updateCheck.latestVersion));

    // Update documentation files
    updateOperations.push(await this.updateDocumentationFiles());

    // Update agent templates
    updateOperations.push(await this.updateAgentTemplates());

    // Update command templates  
    updateOperations.push(await this.updateCommandTemplates());

    return {
      success: updateOperations.every(op => op.success),
      operations: updateOperations,
      filesUpdated: updateOperations.filter(op => op.success).length,
      version: updateCheck.latestVersion
    };
  }

  async generateSyncReport(validation, updateCheck, syncResult, postValidation) {
    const report = {
      timestamp: new Date().toISOString(),
      framework: 'Migration Framework v2.0',
      syncMode: this.syncMode,

      summary: {
        preValidationScore: validation.overallHealth,
        postValidationScore: postValidation.overallHealth,
        improvementScore: postValidation.overallHealth - validation.overallHealth,
        operationsPerformed: syncResult.totalOperations,
        operationsSuccessful: syncResult.successfulOperations,
        operationsFailed: syncResult.failedOperations.length
      },

      details: {
        validation,
        updateCheck,
        syncResult,
        postValidation
      },

      changes: await this.identifyChanges(validation, postValidation),
      metrics: await this.calculateSyncMetrics(syncResult)
    };

    // Save report
    const reportPath = path.join(this.frameworkPath, '.claude', 'logs', 
      `sync-report-${new Date().toISOString().split('T')[0]}.json`);
    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

    return report;
  }

  async generateRecommendations(report) {
    const recommendations = [];

    // Based on health improvement
    if (report.summary.improvementScore > 10) {
      recommendations.push('Framework health significantly improved - consider regular syncing');
    } else if (report.summary.improvementScore < 0) {
      recommendations.push('Framework health declined - investigate failed operations');
    }

    // Based on failed operations
    if (report.summary.operationsFailed > 0) {
      recommendations.push('Some operations failed - review error logs and retry if necessary');
    }

    // Based on update availability
    if (report.details.updateCheck.updateAvailable && this.syncMode === 'check') {
      recommendations.push('Updates available - run sync with update mode to apply');
    }

    // Based on missing dependencies
    if (!report.details.validation.dependencies.allSatisfied) {
      recommendations.push('Missing dependencies detected - install required software');
    }

    return recommendations;
  }

  // Helper methods
  calculateHealthScore(validation) {
    const weights = {
      structure: 0.3,
      files: 0.25,
      configuration: 0.2,
      dependencies: 0.15,
      permissions: 0.05,
      integrity: 0.05
    };

    let score = 0;
    for (const [category, weight] of Object.entries(weights)) {
      if (validation[category] && validation[category].score !== undefined) {
        score += validation[category].score * weight;
      }
    }

    return Math.round(score);
  }

  identifyIssues(validation) {
    const issues = [];

    // Check each validation category for issues
    Object.entries(validation).forEach(([category, result]) => {
      if (result && result.issues) {
        issues.push(...result.issues.map(issue => ({ category, ...issue })));
      }
    });

    return issues;
  }

  identifyWarnings(validation) {
    const warnings = [];

    Object.entries(validation).forEach(([category, result]) => {
      if (result && result.warnings) {
        warnings.push(...result.warnings.map(warning => ({ category, ...warning })));
      }
    });

    return warnings;
  }

  compareVersions(version1, version2) {
    const v1Parts = version1.split('.').map(Number);
    const v2Parts = version2.split('.').map(Number);

    for (let i = 0; i < Math.max(v1Parts.length, v2Parts.length); i++) {
      const v1Part = v1Parts[i] || 0;
      const v2Part = v2Parts[i] || 0;

      if (v1Part < v2Part) return -1;
      if (v1Part > v2Part) return 1;
    }

    return 0;
  }

  // Placeholder implementations for actual operations
  async getCurrentVersion() {
    try {
      const versionFile = path.join(this.frameworkPath, 'VERSION');
      if (fs.existsSync(versionFile)) {
        return fs.readFileSync(versionFile, 'utf8').trim();
      }
    } catch (error) {
      console.warn('Could not read VERSION file');
    }
    return '2.0.0';
  }

