  async evaluateOverallSecurity(securityAssessment) {
    const assessments = Object.values(securityAssessment);

    // Calculate overall security score
    const securityScore = this.calculateOverallSecurityScore(securityAssessment);

    // Identify violations
    const violations = this.aggregateViolations(securityAssessment);

    // Determine approval
    const approved = this.determineApproval(securityScore, violations);

    // Assess risk level
    const riskLevel = this.assessOverallRisk(securityAssessment);

    return {
      approved,
      securityScore,
      riskLevel,
      violations,
      critical: violations.filter(v => v.severity === 'critical'),
      high: violations.filter(v => v.severity === 'high'),
      medium: violations.filter(v => v.severity === 'medium'),
      low: violations.filter(v => v.severity === 'low'),
      requiresReview: riskLevel === 'high' || violations.some(v => v.severity === 'critical')
    };
  }

  async generateSecurityReport(securityContext, securityAssessment, overallResult) {
    const report = {
      metadata: {
        checkId: securityContext.checkId,
        command: securityContext.command,
        phase: securityContext.phase,
        timestamp: securityContext.timestamp,
        securityLevel: this.securityLevel,
        framework: 'Migration Framework v2.0'
      },

      summary: {
        approved: overallResult.approved,
        securityScore: overallResult.securityScore,
        riskLevel: overallResult.riskLevel,
        totalViolations: overallResult.violations.length,
        criticalViolations: overallResult.critical.length,
        requiresReview: overallResult.requiresReview
      },

      assessments: securityAssessment,
      violations: overallResult.violations,

      analysis: {
        commandAnalysis: await this.analyzeCommandSecurity(securityContext.command),
        riskAnalysis: await this.analyzeSecurityRisk(securityAssessment),
        complianceAnalysis: await this.analyzeCompliance(securityAssessment.policyCheck)
      }
    };

    // Save security report
    const reportPath = await this.saveSecurityReport(report);

    return {
      ...report,
      reportPath,
      archived: true
    };
  }

  async handleViolations(violations, securityContext) {
    console.log('ğŸš¨ Handling security violations...');

    const handlingResults = [];

    for (const violation of violations) {
      const result = await this.handleViolation(violation, securityContext);
      handlingResults.push(result);
    }

    // Alert if enabled
    if (this.alertingEnabled && violations.some(v => v.severity === 'critical')) {
      await this.sendSecurityAlert(violations, securityContext);
    }

    // Quarantine if recommended
    const criticalViolations = violations.filter(v => v.severity === 'critical');
    if (this.quarantineEnabled && criticalViolations.length > 0) {
      await this.quarantineCommand(securityContext.command, criticalViolations);
    }

    return {
      handled: handlingResults,
      alerted: this.alertingEnabled,
      quarantined: this.quarantineEnabled && criticalViolations.length > 0,
      recommendations: await this.generateViolationRecommendations(violations)
    };
  }

  // Helper methods for security analysis
  classifyCommand(command) {
    if (command.includes('rm ') || command.includes('del ')) return 'destructive';
    if (command.includes('sudo') || command.includes('runas')) return 'privileged';
    if (command.includes('curl') || command.includes('wget')) return 'network';
    if (command.includes('chmod') || command.includes('chown')) return 'permission';
    return 'standard';
  }

  assessPrivilegeRequirement(command) {
    const privilegedKeywords = ['sudo', 'runas', 'admin', 'root', 'elevated'];
    return privilegedKeywords.some(keyword => command.toLowerCase().includes(keyword)) ? 'elevated' : 'standard';
  }

  assessCommandRisk(command) {
    const highRiskPatterns = [
      /rm\s+-rf/,
      /del\s+\/[sq]/,
      /format\s+/,
      /fdisk/,
      /diskpart/
    ];

    if (highRiskPatterns.some(pattern => pattern.test(command))) return 'high';
    if (command.includes('sudo') || command.includes('admin')) return 'medium';
    return 'low';
  }

  calculateOverallSecurityScore(securityAssessment) {
    const weights = {
      commandAnalysis: 0.25,
      permissionCheck: 0.20,
      threatAnalysis: 0.20,
      policyCheck: 0.15,
      resourceSecurity: 0.10,
      dataProtection: 0.10
    };

    let score = 0;
    for (const [category, weight] of Object.entries(weights)) {
      if (securityAssessment[category] && securityAssessment[category].score !== undefined) {
        score += securityAssessment[category].score * weight;
      }
    }

    return Math.round(score);
  }

  determineApproval(securityScore, violations) {
    const criticalViolations = violations.filter(v => v.severity === 'critical');

    // Block if critical violations
    if (criticalViolations.length > 0) return false;

    // Approve based on security level and score
    switch (this.securityLevel) {
      case 'minimal': return securityScore >= 40;
      case 'standard': return securityScore >= 60;
      case 'strict': return securityScore >= 80;
      case 'paranoid': return securityScore >= 95;
      default: return securityScore >= 60;
    }
  }

  generateCheckId() {
    return `sec-${Date.now()}-${crypto.randomBytes(4).toString('hex')}`;
  }

