            duration: duration,
            autonomyLevel: autonomyLevel,
            startTime: new Date().toISOString(),
            status: 'active'
        };

        if (mode === 'continuous') {
            return await this.runContinuousEvolution(evolutionCycle);
        } else if (mode === 'scheduled') {
            return await this.runScheduledEvolution(evolutionCycle);
        } else {
            return await this.runSingleCycleEvolution(evolutionCycle);
        }
    }

