#!/usr/bin/env node

/**
 * Configuration Loader for Pop!_OS Migration Framework
 * Centralized configuration management with environment overrides
 */

const fs = require('fs');
const path = require('path');

class ConfigLoader {
  constructor(options = {}) {
    this.configPath = options.configPath || path.join(__dirname, 'framework-config.json');
    this.envPrefix = options.envPrefix || 'POPOS_MIGRATION_';
    this.cache = null;
    this.overrides = {};
  }

  /**
   * Load and parse configuration with environment overrides
   */
  load() {
    if (this.cache) {
      return this.cache;
    }

    try {
      // Load base configuration
      const configData = fs.readFileSync(this.configPath, 'utf8');
      const baseConfig = JSON.parse(configData);

      // Apply environment overrides
      const envConfig = this.loadEnvironmentOverrides();

      // Merge configurations
      this.cache = this.deepMerge(baseConfig, envConfig, this.overrides);

      return this.cache;
    } catch (error) {
      throw new Error(`Failed to load configuration: ${error.message}`);
    }
  }

  /**
   * Get configuration value by path (e.g., 'migration.scoring.baseline.overallScore')
   */
  get(keyPath, defaultValue = null) {
    const config = this.load();
    return this.getNestedValue(config, keyPath, defaultValue);
  }

