#!/usr/bin/env node

/**
 * AgenticOS Configuration Loader
 * Advanced configuration management for Living Software systems
 * Supports autonomous evolution, parallel agents, and dynamic adaptation
 */

const fs = require('fs');
const path = require('path');

class AgenticOSConfigLoader {
  constructor(options = {}) {
    this.configPath = options.configPath || path.join(__dirname, 'agentidos-config.json');
    this.envPrefix = options.envPrefix || 'AGENTIDOS_';
    this.cache = null;
    this.overrides = {};
    this.evolutionMetrics = new Map();
    this.agentConfigs = new Map();
  }

  /**
   * Load and parse configuration with environment overrides and autonomous adaptation
   */
  load() {
    if (this.cache) {
      return this.cache;
    }

    try {
      // Load base configuration
      const configData = fs.readFileSync(this.configPath, 'utf8');
      const baseConfig = JSON.parse(configData);

      // Apply environment overrides
      const envConfig = this.loadEnvironmentOverrides();

      // Apply autonomous evolution adjustments
      const evolutionConfig = this.loadEvolutionAdjustments();

      // Merge configurations
      this.cache = this.deepMerge(baseConfig, envConfig, evolutionConfig, this.overrides);

      return this.cache;
    } catch (error) {
      throw new Error(`Failed to load AgenticOS configuration: ${error.message}`);
    }
  }

  /**
   * Get configuration value by path with autonomous adaptation
   */
  get(keyPath, defaultValue = null) {
    const config = this.load();
    const value = this.getNestedValue(config, keyPath, defaultValue);

    // Track configuration access for autonomous optimization
    this.trackConfigUsage(keyPath, value);

    return value;
  }

  /**
   * Get agent-specific configuration
   */
  getAgentConfig(agentName, mode = 'standard') {
    const config = this.load();
    const agentConfigs = config.agents || {};
    const agentConfig = agentConfigs[agentName] || {};

    // Apply mode-specific overrides
    const modeConfig = agentConfig.modes?.[mode] || {};

    return this.deepMerge(agentConfig.base || {}, modeConfig);
  }

  /**
   * Get timeout configuration for different operation types
   */
  getTimeout(operationType) {
    const timeouts = this.get('system.timeouts', {});
    return timeouts[operationType] || timeouts.default || 120000;
  }

  /**
   * Get parallel agent configuration
   */
  getParallelConfig() {
    return this.get('parallel', {
      maxAgents: 4,
      worktreeBase: './parallel-agents',
      isolationLevel: 'git-worktree',
      resourceAllocation: 'balanced'
    });
  }

  /**
   * Get autonomous evolution parameters
   */
  getEvolutionConfig() {
    return this.get('evolution', {
      learningRate: 0.1,
      adaptationThreshold: 0.05,
      autonomyLevel: 'supervised',
      feedbackLoops: true
    });
  }

  /**
   * Get localized string with autonomous language adaptation
   */
  getString(key, params = {}) {
    const strings = this.get('localization.strings', {});
    let string = strings[key] || key;

    // Simple parameter substitution
    Object.keys(params).forEach(param => {
      string = string.replace(new RegExp(`{${param}}`, 'g'), params[param]);
    });

    return string;
  }

  /**
   * Get software alternatives for cross-platform compatibility
   */
  getSoftwareAlternative(softwareName) {
    const alternatives = this.get('software.alternatives', {});
    return alternatives[softwareName] || null;
  }

  /**
   * Check if feature is enabled with autonomous feature flag management
   */
  isEnabled(featureName) {
    const features = this.get('features', {});
    const isEnabled = features[featureName] || false;

    // Track feature usage for autonomous optimization
    this.trackFeatureUsage(featureName, isEnabled);

    return isEnabled;
  }

  /**
   * Get commander directive configuration
   */
  getCommanderConfig() {
    return this.get('commander', {
      recognition: true,
      directiveTracking: true,
      performanceMetrics: true,
      strategicAlignment: true
    });
  }

  /**
   * Load environment overrides with AgenticOS prefix
   */
  loadEnvironmentOverrides() {
    const envConfig = {};

    Object.keys(process.env).forEach(key => {
      if (key.startsWith(this.envPrefix)) {
        const configKey = key.substring(this.envPrefix.length).toLowerCase();
        const configPath = configKey.replace(/_/g, '.');
        this.setNestedValue(envConfig, configPath, process.env[key]);
      }
    });

    return envConfig;
  }

  /**
   * Load autonomous evolution adjustments based on performance data
   */
  loadEvolutionAdjustments() {
    const evolutionConfig = {};

    // Apply learned optimizations
    this.evolutionMetrics.forEach((metrics, configPath) => {
      if (metrics.usageCount > 100 && metrics.performance < 0.8) {
        // Autonomous optimization: adjust configuration based on performance
        const optimizedValue = this.optimizeConfigValue(configPath, metrics);
        if (optimizedValue !== null) {
          this.setNestedValue(evolutionConfig, configPath, optimizedValue);
        }
      }
    });

    return evolutionConfig;
  }

  /**
   * Track configuration usage for autonomous optimization
   */
  trackConfigUsage(keyPath, value) {
    if (!this.evolutionMetrics.has(keyPath)) {
      this.evolutionMetrics.set(keyPath, {
        usageCount: 0,
        lastUsed: Date.now(),
        performance: 1.0,
        value: value
      });
    }

    const metrics = this.evolutionMetrics.get(keyPath);
    metrics.usageCount++;
    metrics.lastUsed = Date.now();
    metrics.value = value;
  }

  /**
   * Track feature usage for autonomous feature flag management
   */
  trackFeatureUsage(featureName, isEnabled) {
    // Implementation for tracking feature usage patterns
    // This enables autonomous feature flag optimization
  }

  /**
   * Optimize configuration value based on performance metrics
   */
  optimizeConfigValue(configPath, metrics) {
    // Autonomous optimization logic
    // Returns optimized value or null if no optimization needed
    return null;
  }

  /**
   * Set configuration override for runtime adjustment
   */
  setOverride(keyPath, value) {
    this.setNestedValue(this.overrides, keyPath, value);
    this.cache = null; // Force reload
  }

  /**
   * Validate configuration integrity
   */
  validate() {
    const config = this.load();
    const errors = [];

    // Required configuration validation
    const required = [
      'system.name',
      'agents',
      'commands',
      'parallel.maxAgents'
    ];

    required.forEach(path => {
      if (this.getNestedValue(config, path) === null) {
        errors.push(`Missing required configuration: ${path}`);
      }
    });

    return {
      valid: errors.length === 0,
      errors: errors
    };
  }

  /**
   * Get nested value from object using dot notation
   */
  getNestedValue(obj, path, defaultValue = null) {
    const keys = path.split('.');
    let current = obj;

    for (const key of keys) {
      if (current === null || current === undefined || !(key in current)) {
        return defaultValue;
      }
      current = current[key];
    }

    return current;
  }

  /**
   * Set nested value in object using dot notation
   */
  setNestedValue(obj, path, value) {
    const keys = path.split('.');
    let current = obj;

    for (let i = 0; i < keys.length - 1; i++) {
      const key = keys[i];
      if (!(key in current) || typeof current[key] !== 'object') {
        current[key] = {};
      }
      current = current[key];
    }

    current[keys[keys.length - 1]] = value;
  }

  /**
   * Deep merge objects
   */
  deepMerge(...objects) {
    const result = {};

    objects.forEach(obj => {
      if (obj && typeof obj === 'object') {
        Object.keys(obj).forEach(key => {
          if (obj[key] && typeof obj[key] === 'object' && !Array.isArray(obj[key])) {
            result[key] = this.deepMerge(result[key] || {}, obj[key]);
          } else {
            result[key] = obj[key];
          }
        });
      }
    });

    return result;
  }

  /**
   * Clear cache and reload configuration
   */
  reload() {
    this.cache = null;
    return this.load();
  }

  /**
   * Export configuration for backup or analysis
   */
  export() {
    return {
      config: this.load(),
      metrics: Array.from(this.evolutionMetrics.entries()),
      timestamp: new Date().toISOString()
    };
  }
}

// Create singleton instance
const config = new AgenticOSConfigLoader();

module.exports = { AgenticOSConfigLoader, config };

