    const envConfig = {};

    Object.keys(process.env).forEach(key => {
      if (key.startsWith(this.envPrefix)) {
        const configKey = key.substring(this.envPrefix.length).toLowerCase();
        const configPath = configKey.replace(/_/g, '.');
        this.setNestedValue(envConfig, configPath, process.env[key]);
      }
    });

    return envConfig;
  }

  /**
   * Load autonomous evolution adjustments based on performance data
   */
  loadEvolutionAdjustments() {
    const evolutionConfig = {};

    // Apply learned optimizations
    this.evolutionMetrics.forEach((metrics, configPath) => {
      if (metrics.usageCount > 100 && metrics.performance < 0.8) {
        // Autonomous optimization: adjust configuration based on performance
        const optimizedValue = this.optimizeConfigValue(configPath, metrics);
        if (optimizedValue !== null) {
          this.setNestedValue(evolutionConfig, configPath, optimizedValue);
        }
      }
    });

    return evolutionConfig;
  }

  /**
   * Track configuration usage for autonomous optimization
   */
  trackConfigUsage(keyPath, value) {
    if (!this.evolutionMetrics.has(keyPath)) {
      this.evolutionMetrics.set(keyPath, {
        usageCount: 0,
        lastUsed: Date.now(),
        performance: 1.0,
        value: value
      });
    }

    const metrics = this.evolutionMetrics.get(keyPath);
    metrics.usageCount++;
    metrics.lastUsed = Date.now();
    metrics.value = value;
  }

  /**
   * Track feature usage for autonomous feature flag management
   */
  trackFeatureUsage(featureName, isEnabled) {
    // Implementation for tracking feature usage patterns
    // This enables autonomous feature flag optimization
  }

  /**
   * Optimize configuration value based on performance metrics
   */
  optimizeConfigValue(configPath, metrics) {
    // Autonomous optimization logic
    // Returns optimized value or null if no optimization needed
    return null;
  }

  /**
   * Set configuration override for runtime adjustment
   */
  setOverride(keyPath, value) {
    this.setNestedValue(this.overrides, keyPath, value);
    this.cache = null; // Force reload
  }

  /**
   * Validate configuration integrity
   */
  validate() {
    const config = this.load();
    const errors = [];

    // Required configuration validation
    const required = [
      'system.name',
      'agents',
      'commands',
      'parallel.maxAgents'
    ];

    required.forEach(path => {
      if (this.getNestedValue(config, path) === null) {
        errors.push(`Missing required configuration: ${path}`);
      }
    });

    return {
      valid: errors.length === 0,
      errors: errors
    };
  }

  /**
   * Get nested value from object using dot notation
   */
  getNestedValue(obj, path, defaultValue = null) {
    const keys = path.split('.');
    let current = obj;

    for (const key of keys) {
      if (current === null || current === undefined || !(key in current)) {
        return defaultValue;
      }
      current = current[key];
    }

    return current;
  }

  /**
   * Set nested value in object using dot notation
   */
  setNestedValue(obj, path, value) {
    const keys = path.split('.');
    let current = obj;

    for (let i = 0; i < keys.length - 1; i++) {
      const key = keys[i];
      if (!(key in current) || typeof current[key] !== 'object') {
        current[key] = {};
      }
      current = current[key];
    }

    current[keys[keys.length - 1]] = value;
  }

  /**
   * Deep merge objects
   */
  deepMerge(...objects) {
    const result = {};

    objects.forEach(obj => {
      if (obj && typeof obj === 'object') {
        Object.keys(obj).forEach(key => {
          if (obj[key] && typeof obj[key] === 'object' && !Array.isArray(obj[key])) {
            result[key] = this.deepMerge(result[key] || {}, obj[key]);
          } else {
            result[key] = obj[key];
          }
        });
      }
    });

    return result;
  }

  /**
   * Clear cache and reload configuration
   */
  reload() {
    this.cache = null;
    return this.load();
  }

  /**
   * Export configuration for backup or analysis
   */
  export() {
    return {
      config: this.load(),
      metrics: Array.from(this.evolutionMetrics.entries()),
      timestamp: new Date().toISOString()
    };
  }
}

