#!/usr/bin/env node

/**
 * AGENTIDOS TEST SUITE
 * Comprehensive testing for Living Software capabilities
 * Tests autonomous evolution, parallel compute, and agentic patterns
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

class AgenticOSTestSuite {
    constructor() {
        this.basePath = process.cwd();
        this.configPath = path.join(this.basePath, '.claude', 'config');
        this.results = {
            totalTests: 0,
            passed: 0,
            failed: 0,
            warnings: 0,
            details: []
        };

        this.startTime = Date.now();
    }

    async runAllTests() {
        console.log('ğŸŒŸ AGENTIDOS LIVING SOFTWARE TEST SUITE');
        console.log('=====================================');
        console.log(`ğŸ• Start Time: ${new Date().toISOString()}`);
        console.log('');

        try {
            // Core System Tests
            await this.testCoreSystem();

            // Configuration Tests
            await this.testConfiguration();

            // Agentic Patterns Tests
            await this.testAgenticPatterns();

            // Autonomous Evolution Tests
            await this.testAutonomousEvolution();

            // Parallel Compute Tests
            await this.testParallelCompute();

            // Commander Directive Compliance Tests
            await this.testCommanderCompliance();

            // Strategic Evolution Tests
            await this.testStrategicEvolution();

            // Living Software Validation
            await this.testLivingSoftware();

        } catch (error) {
            this.logError('Test Suite Execution', error.message);
        }

        return this.generateReport();
    }

    async testCoreSystem() {
        console.log('ğŸ”§ Testing Core System...');

        // Test CLAUDE.md existence and content
        this.test('CLAUDE.md Exists', () => {
            return fs.existsSync(path.join(this.basePath, 'CLAUDE.md'));
        });

        // Test Mission Board availability
        this.test('Mission Board Available', () => {
            return fs.existsSync(path.join(this.basePath, 'MISSION-BOARD.html'));
        });

        // Test .claude directory structure
        this.test('.claude Directory Structure', () => {
            const requiredDirs = ['agents', 'commands', 'hooks', 'config', 'templates'];
            const claudeBase = path.join(this.basePath, '.claude');

            return requiredDirs.every(dir => 
                fs.existsSync(path.join(claudeBase, dir))
            );
        });

        // Test strategic-evolution structure
        this.test('Strategic Evolution Structure', () => {
            const strategicBase = path.join(this.basePath, 'strategic-evolution');
            const requiredDirs = ['commander-directives', 'context-capture', 'agentic-patterns'];

            return requiredDirs.every(dir => 
                fs.existsSync(path.join(strategicBase, dir))
            );
        });
    }

    async testConfiguration() {
        console.log('âš™ï¸ Testing Configuration System...');

        // Test configuration file existence
        this.test('AgenticOS Config Exists', () => {
            return fs.existsSync(path.join(this.configPath, 'agentidos-config.json'));
        });

        // Test configuration loader
        this.test('Config Loader Available', () => {
            return fs.existsSync(path.join(this.configPath, 'agentidos-config-loader.js'));
        });

        // Test configuration loading
        this.test('Configuration Loading', () => {
            try {
                const { config } = require(path.join(this.configPath, 'agentidos-config-loader.js'));
                const systemName = config.get('system.name');
                return systemName === 'AgenticOS';
            } catch (error) {
                return false;
            }
        });

        // Test agent configuration
        this.test('Agent Configuration', () => {
            try {
                const { config } = require(path.join(this.configPath, 'agentidos-config-loader.js'));
                const agentConfig = config.getAgentConfig('autonomous-evolution-engine');
                return agentConfig && agentConfig.specialty === 'autonomous-evolution';
            } catch (error) {
                return false;
            }
        });
    }

    async testAgenticPatterns() {
        console.log('âš¡ Testing 6 Critical Agentic Patterns...');

        // Test Plan Mode Mastery
        this.test('Plan Mode Configuration', () => {
            try {
                const { config } = require(path.join(this.configPath, 'agentidos-config-loader.js'));
                const planMode = config.get('patterns.planMode');
                return planMode && planMode.specPrompts && planMode.aiPlanDrafting;
            } catch (error) {
                return false;
            }
        });

        // Test Context Management
        this.test('Context Management', () => {
            try {
                const { config } = require(path.join(this.configPath, 'agentidos-config-loader.js'));
                const contextMgmt = config.get('patterns.contextManagement');
                return contextMgmt && contextMgmt.claudeMdPersistence;
            } catch (error) {
                return false;
            }
        });

        // Test Custom Slash Commands
        this.test('Custom Slash Commands', () => {
            const commandsDir = path.join(this.basePath, '.claude', 'commands');
            const requiredCommands = ['agentic-deploy.js', 'parallel-deploy.js'];

            return requiredCommands.every(cmd => 
                fs.existsSync(path.join(commandsDir, cmd))
            );
        });

        // Test Hooks System
        this.test('Hooks System', () => {
            const hooksDir = path.join(this.basePath, '.claude', 'hooks');
            const requiredHooks = ['PreToolUse.js'];

            return requiredHooks.every(hook => 
                fs.existsSync(path.join(hooksDir, hook))
            );
        });

        // Test Parallel Architecture
        this.test('Parallel Architecture Command', () => {
            return fs.existsSync(path.join(this.basePath, '.claude', 'commands', 'parallel-deploy.js'));
        });

        // Test MCP Optimization
        this.test('MCP Configuration', () => {
            try {
                const { config } = require(path.join(this.configPath, 'agentidos-config-loader.js'));
                const mcp = config.get('mcp');
                return mcp && mcp.promptOptimization && mcp.workflowEncapsulation;
            } catch (error) {
                return false;
            }
        });
    }

    async testAutonomousEvolution() {
        console.log('ğŸ§  Testing Autonomous Evolution...');

        // Test Evolution Engine
        this.test('Autonomous Evolution Engine', () => {
            return fs.existsSync(path.join(this.basePath, '.claude', 'agents', 'autonomous-evolution-engine.js'));
        });

        // Test Evolution Configuration
        this.test('Evolution Configuration', () => {
            try {
                const { config } = require(path.join(this.configPath, 'agentidos-config-loader.js'));
                const evolution = config.getEvolutionConfig();
                return evolution && evolution.feedbackLoops && evolution.learningRate > 0;
            } catch (error) {
                return false;
            }
        });

        // Test Reinforcement Learning Config
