#!/usr/bin/env node

/**
 * Test script for migration agents
 */

console.log('ğŸ§ª Testing Migration Framework Agents...\n');

async function testAgents() {
    try {
        // Test Windows Analyst
        console.log('ğŸ“Š Testing Windows Analyst...');
        const WindowsAnalyst = require('./agents/windows-analyst.js');
        const analyst = new WindowsAnalyst({
            analysisDepth: 'basic',
            targetOS: 'popos'
        });

        console.log('âœ… Windows Analyst loaded successfully');
        console.log(`   Name: ${analyst.name}`);
        console.log(`   Capabilities: ${analyst.capabilities.join(', ')}`);

        // Test a basic analysis
        const result = await analyst.performTask('system-profile');
        console.log('âœ… System profile analysis completed');
        console.log(`   Success: ${result.success}`);

        console.log('\nğŸ“ˆ Testing Migration Readiness Assessment...');
        const readiness = await analyst.assessMigrationReadiness();
        console.log('âœ… Migration readiness assessment completed');
        console.log(`   Overall Score: ${readiness.overallScore}/100`);
        console.log(`   Timeline: ${readiness.timeline.total} days`);
        console.log(`   Blockers: ${readiness.blockers.length}`);

    } catch (error) {
        console.error('âŒ Agent test failed:', error.message);
        console.error('Stack:', error.stack);
    }
}

async function testCommands() {
    try {
        console.log('\nâš¡ Testing Commands...');

        // Test migration plan command
        console.log('ğŸ“‹ Testing Migration Plan Command...');
        const MigrationPlan = require('./commands/migration-plan.js');
        console.log('âœ… Migration Plan command loaded');

        // Test script generator
        console.log('ğŸ”§ Testing Script Generator...');
        const ScriptGenerator = require('./commands/script-generator.js');
        console.log('âœ… Script Generator command loaded');

    } catch (error) {
        console.error('âŒ Command test failed:', error.message);
    }
}

async function testPowerShellScript() {
