      classification: this.classification,
      autonomyLevel: this.autonomyLevel,
      message,
      ...data
    };

    console.log(`[${level.toUpperCase()}] Living Software Hook ${this.hookType}: ${message}`);

    // Optional: Persistent learning storage
    if (this.learningEnabled) {
      this.appendToLearningLog(logEntry);
    }
  }

  appendToLearningLog(logEntry) {
    // Store learning data for evolution
    try {
      const learningFile = path.join(this.frameworkPath, '.claude', 'logs', `${this.hookType}-learning.json`);
      const existingData = fs.existsSync(learningFile) ? 
        JSON.parse(fs.readFileSync(learningFile, 'utf8')) : [];

      existingData.push(logEntry);
      fs.writeFileSync(learningFile, JSON.stringify(existingData, null, 2));
    } catch (error) {
      console.error('Failed to write to learning log:', error.message);
    }
  }
