        networkConfig: ipconfig.stdout,
        activeConnections: netstat.stdout,
        timestamp: new Date().toISOString()
      };
    } catch (error) {
      console.warn('âš ï¸ Could not gather network info:', error.message);
      return { error: error.message };
    }
  }

  async gatherEventLogs() {
    console.log('  ğŸ“œ Collecting recent event logs...');

    try {
      const systemErrors = await execAsync('wevtutil qe System /c:50 /rd:true /f:text /q:"*[System[Level=2]]"');
      const applicationErrors = await execAsync('wevtutil qe Application /c:50 /rd:true /f:text /q:"*[System[Level=2]]"');

      return {
        systemErrors: systemErrors.stdout,
        applicationErrors: applicationErrors.stdout,
        timestamp: new Date().toISOString()
      };
    } catch (error) {
      console.warn('âš ï¸ Could not gather event logs:', error.message);
      return { error: error.message };
    }
  }

  async performDiagnostics(systemData, options) {
    console.log('ğŸ” Performing diagnostic analysis...');

    const diagnostics = {
      issues: [],
      warnings: [],
      recommendations: []
    };

    // Analyze hardware issues
    if (systemData.hardware && !systemData.hardware.error) {
      diagnostics.issues.push(...this.analyzeHardwareIssues(systemData.hardware));
    }

    // Analyze driver issues
    if (systemData.drivers && !systemData.drivers.error) {
      diagnostics.issues.push(...this.analyzeDriverIssues(systemData.drivers));
    }

    // Analyze performance issues
    if (systemData.performance && !systemData.performance.error) {
      diagnostics.warnings.push(...this.analyzePerformanceIssues(systemData.performance));
    }

    // Analyze network issues
    if (systemData.networking && !systemData.networking.error) {
      diagnostics.issues.push(...this.analyzeNetworkIssues(systemData.networking));
    }

    return diagnostics;
  }

  analyzeHardwareIssues(hardwareData) {
    const issues = [];

    // Example hardware issue detection
    if (hardwareData.gpu && hardwareData.gpu.length === 0) {
      issues.push({
        type: 'hardware',
        severity: 'high',
        component: 'graphics',
        description: 'No graphics card detected',
        solution: 'Check graphics card connection and drivers'
      });
    }

    return issues;
  }

  analyzeDriverIssues(driverData) {
    const issues = [];

    if (driverData.problemDevices && driverData.problemDevices.length > 0) {
      driverData.problemDevices.forEach(device => {
        if (device.Status !== 'OK') {
          issues.push({
            type: 'driver',
            severity: 'medium',
            component: device.Name,
            description: `Device has status: ${device.Status}`,
            solution: 'Update or reinstall device driver'
          });
        }
      });
    }

    return issues;
  }

  analyzePerformanceIssues(performanceData) {
    const warnings = [];

    // Example performance analysis
    if (performanceData.runningProcesses) {
      const highMemoryProcesses = performanceData.runningProcesses.filter(
        process => parseInt(process.WorkingSetSize) > 500000000 // 500MB
      );

      if (highMemoryProcesses.length > 0) {
        warnings.push({
          type: 'performance',
          severity: 'low',
          description: `${highMemoryProcesses.length} processes using high memory`,
          solution: 'Consider closing unnecessary applications'
        });
      }
    }

    return warnings;
  }

  analyzeNetworkIssues(networkData) {
    const issues = [];

    // Example network issue detection
    if (networkData.networkConfig.includes('Media disconnected')) {
      issues.push({
        type: 'network',
        severity: 'medium',
        component: 'network adapter',
        description: 'Network adapter appears disconnected',
        solution: 'Check network cable connections and adapter status'
      });
    }

    return issues;
  }

  async generateSolutions(diagnosticResults) {
    console.log('ğŸ’¡ Generating solutions...');

    const solutions = {
      automated: [],
      manual: [],
      manualSteps: []
    };

    diagnosticResults.issues.forEach(issue => {
      const solution = this.generateSolutionForIssue(issue);
      if (solution.automated) {
        solutions.automated.push(solution);
      } else {
        solutions.manual.push(solution);
        solutions.manualSteps.push(solution.description);
      }
    });

    return solutions;
  }

  generateSolutionForIssue(issue) {
    const solutionMap = {
      'driver': {
        automated: false,
        description: `Update driver for ${issue.component}`,
        steps: [
          'Open Device Manager',
          `Right-click on ${issue.component}`,
          'Select "Update driver"',
          'Choose "Search automatically for drivers"'
        ]
      },
      'hardware': {
        automated: false,
        description: `Check hardware connection for ${issue.component}`,
        steps: [
          'Power down the system',
          `Check physical connection of ${issue.component}`,
          'Reseat connections if necessary',
          'Power on and test'
        ]
      },
      'network': {
        automated: true,
        description: 'Reset network adapter',
        command: 'netsh winsock reset && netsh int ip reset'
      }
    };

    return solutionMap[issue.type] || {
      automated: false,
      description: issue.solution,
      steps: ['Manual intervention required']
    };
  }

  async executeAutomatedFixes(solutions, options) {
    console.log('ğŸ”§ Executing automated fixes...');

    const results = {
      applied: [],
      failed: [],
      skipped: []
    };

    if (!options.automated) {
      console.log('  â„¹ï¸ Automated fixes disabled. Use --auto-fix to enable.');
      results.skipped = solutions.automated;
      return results;
    }

    for (const solution of solutions.automated) {
      try {
        if (solution.command) {
          await execAsync(solution.command);
          results.applied.push(solution);
          console.log(`  âœ… Applied: ${solution.description}`);
        }
      } catch (error) {
        results.failed.push({ solution, error: error.message });
        console.log(`  âŒ Failed: ${solution.description} - ${error.message}`);
      }
    }

    return results;
  }

  async generateTroubleshootReport(systemData, diagnostics, solutions, fixResults) {
    return {
      timestamp: new Date().toISOString(),
      systemData,
      diagnostics,
      solutions,
      fixResults,
      summary: {
        totalIssues: diagnostics.issues.length,
        totalWarnings: diagnostics.warnings.length,
        automatedFixesApplied: fixResults.applied.length,
        manualStepsRequired: solutions.manualSteps.length
      }
    };
  }

  async displayResults(report) {
    console.log('\nğŸ”§ Troubleshooting Results');
    console.log('==========================');
    console.log(`Issues Found: ${report.summary.totalIssues}`);
    console.log(`Warnings: ${report.summary.totalWarnings}`);
    console.log(`Automated Fixes Applied: ${report.summary.automatedFixesApplied}`);
    console.log(`Manual Steps Required: ${report.summary.manualStepsRequired}`);

    if (report.diagnostics.issues.length > 0) {
      console.log('\nâŒ Issues Found:');
      report.diagnostics.issues.forEach((issue, index) => {
        console.log(`  ${index + 1}. [${issue.severity.toUpperCase()}] ${issue.description}`);
        console.log(`     Solution: ${issue.solution}`);
      });
    }

    if (report.solutions.manualSteps.length > 0) {
      console.log('\nğŸ“‹ Manual Steps Required:');
      report.solutions.manualSteps.forEach((step, index) => {
        console.log(`  ${index + 1}. ${step}`);
      });
    }

    if (report.summary.totalIssues === 0) {
      console.log('\nâœ… No issues found! System appears to be functioning normally.');
    }
  }

  // Utility methods
  extractValue(text, key) {
    const regex = new RegExp(`${key}[:\\s]+(.+)`, 'i');
    const match = text.match(regex);
    return match ? match[1].trim() : 'Not found';
  }

  parseWmicOutput(csvText) {
    const lines = csvText.split('\n').filter(line => line.trim() && !line.startsWith('Node'));
    return lines.map(line => {
      const values = line.split(',');
      return values.reduce((obj, value, index) => {
        obj[`field${index}`] = value.trim();
        return obj;
      }, {});
    });
  }

  parseDriverQuery(csvText) {
    const lines = csvText.split('\n').filter(line => line.trim());
    const headers = lines[0].split(',').map(h => h.replace(/"/g, '').trim());

    return lines.slice(1).map(line => {
      const values = line.split(',').map(v => v.replace(/"/g, '').trim());
      return headers.reduce((obj, header, index) => {
        obj[header] = values[index] || '';
        return obj;
      }, {});
    });
  }
}

// Export for use as module or run directly
if (require.main === module) {
  const troubleshooter = new TroubleshootCommand();
  troubleshooter.execute(process.argv.slice(2))
    .then(result => {
      process.exit(result.success ? 0 : 1);
    })
    .catch(error => {
      console.error('Troubleshooting failed:', error);
      process.exit(1);
    });
}

module.exports = TroubleshootCommand;
