#!/usr/bin/env powershell
<#
.SYNOPSIS
Complete hardware inventory for Linux distro decision analysis

.DESCRIPTION
Comprehensive PowerShell script that catalogs all hardware components 
for scientific Ubuntu LTS vs Pop!_OS compatibility analysis.
Generates detailed JSON output for Phase 2 compatibility scoring.

.PARAMETER OutputPath
Path where to save the JSON hardware inventory file

.PARAMETER ShowProgress
Display progress information during hardware detection

.EXAMPLE
.\Get-SystemInventory.ps1 -OutputPath "hw-inventory.json" -ShowProgress

.NOTES
Target System: Acer Predator PTX17-71 Workstation
Generated: 2025-08-06
Phase: 1 - Hardware Inventory
#>

param(
    [Parameter(Mandatory=$false)]
    [string]$OutputPath = "hw-inventory.json",

    [Parameter(Mandatory=$false)]
    [switch]$ShowProgress = $false
)

# Initialize logging
$ErrorActionPreference = "Continue"
$LogEntries = @()

function Write-Log {
    param([string]$Message, [string]$Level = "INFO")
    $LogEntry = @{
        Timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
        Level = $Level
        Message = $Message
    }
    $script:LogEntries += $LogEntry
    if ($ShowProgress) {
        Write-Host "[$Level] $Message" -ForegroundColor $(if($Level -eq "ERROR"){"Red"}elseif($Level -eq "WARN"){"Yellow"}else{"Green"})
    }
}

function Get-SafeValue {
    param($Value, $Default = "Unknown")
    if ($null -eq $Value -or $Value -eq "" -or $Value -eq "N/A") {
        return $Default
    }
    return $Value
}

function Redact-Sensitive {
    param([string]$Value)
    if ($Value -match "^([A-F0-9]{2}[:-]){5}[A-F0-9]{2}$") {
        return "XX:XX:XX:XX:XX:XX"  # MAC address
    }
    if ($Value -match "^[A-Z0-9]{10,}$") {
        return "REDACTED-SERIAL"    # Serial numbers
    }
    return $Value
}

Write-Log "Starting comprehensive hardware inventory..."

# Initialize hardware profile structure
$HardwareProfile = @{
    metadata = @{
        scan_date = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
        scan_duration_seconds = 0
        detection_coverage = @{}
        errors = @()
    }
    system = @{}
    cpu = @{}
    memory = @{}
    gpu = @()
    storage = @{
        drives = @()
        controllers = @()
        encryption = @()
    }
    network = @{
        wifi = @()
        bluetooth = @()
        ethernet = @()
    }
    display_video = @{
        internal_display = @{}
        video_outputs = @()
        external_displays = @()
    }
    connectivity = @{
        usb_controllers = @()
        card_readers = @()
        expansion = @()
    }
    input_multimedia = @{
        keyboard = @{}
        pointing = @()
        audio = @{
            codecs = @()
            speakers = @{}
            microphones = @()
        }
        cameras = @()
        biometric = @()
    }
    system_management = @{
        thermal = @{
            sensors = @()
            fan_control = @{}
        }
        power = @{
            battery = @{}
            power_profiles = @()
        }
        acpi = @{}
        monitoring = @()
    }
    security = @{
        secure_boot = @{}
        tpm = @{}
        bitlocker = @()
    }
}

$StartTime = Get-Date

# === SYSTEM INFORMATION ===
Write-Log "Detecting system information..."
try {
    $SystemInfo = Get-CimInstance -ClassName Win32_ComputerSystem -ErrorAction Stop
    $BiosInfo = Get-CimInstance -ClassName Win32_BIOS -ErrorAction Stop

    $HardwareProfile.system = @{
        manufacturer = Get-SafeValue $SystemInfo.Manufacturer
        model = Get-SafeValue $SystemInfo.Model
        total_physical_memory_gb = [math]::Round($SystemInfo.TotalPhysicalMemory / 1GB, 2)
        number_of_processors = Get-SafeValue $SystemInfo.NumberOfProcessors
        bios_version = Get-SafeValue $BiosInfo.SMBIOSBIOSVersion
        bios_date = Get-SafeValue $BiosInfo.ReleaseDate
        firmware_type = if ($env:firmware_type -eq "UEFI") { "UEFI" } else { "Legacy" }
    }
    $HardwareProfile.metadata.detection_coverage.system = "100%"
    Write-Log "System: $($HardwareProfile.system.manufacturer) $($HardwareProfile.system.model)"
} catch {
    Write-Log "Error detecting system info: $($_.Exception.Message)" "ERROR"
    $HardwareProfile.metadata.errors += "System detection failed"
    $HardwareProfile.metadata.detection_coverage.system = "0%"
}

# === CPU INFORMATION ===
Write-Log "Detecting CPU information..."
try {
    $CpuInfo = Get-CimInstance -ClassName Win32_Processor -ErrorAction Stop | Select-Object -First 1

    $HardwareProfile.cpu = @{
        name = Get-SafeValue $CpuInfo.Name
        manufacturer = Get-SafeValue $CpuInfo.Manufacturer  
        cores = Get-SafeValue $CpuInfo.NumberOfCores
        logical_processors = Get-SafeValue $CpuInfo.NumberOfLogicalProcessors
        threads = Get-SafeValue $CpuInfo.ThreadCount
        architecture = Get-SafeValue $CpuInfo.Architecture
        max_clock_speed_mhz = Get-SafeValue $CpuInfo.MaxClockSpeed
        current_clock_speed_mhz = Get-SafeValue $CpuInfo.CurrentClockSpeed
        cache_l3_kb = Get-SafeValue $CpuInfo.L3CacheSize
        virtualization = if ($CpuInfo.VirtualizationFirmwareEnabled) { "Enabled" } else { "Disabled" }
    }
    $HardwareProfile.metadata.detection_coverage.cpu = "100%"
    Write-Log "CPU: $($HardwareProfile.cpu.name) ($($HardwareProfile.cpu.cores)C/$($HardwareProfile.cpu.logical_processors)T)"
} catch {
    Write-Log "Error detecting CPU: $($_.Exception.Message)" "ERROR"
    $HardwareProfile.metadata.errors += "CPU detection failed"
    $HardwareProfile.metadata.detection_coverage.cpu = "0%"
}

# === MEMORY INFORMATION ===
Write-Log "Detecting memory configuration..."
try {
    $MemoryModules = Get-CimInstance -ClassName Win32_PhysicalMemory -ErrorAction Stop
    $MemoryArray = @()

    foreach ($Module in $MemoryModules) {
        $MemoryArray += @{
            capacity_gb = [math]::Round($Module.Capacity / 1GB, 0)
            speed_mhz = Get-SafeValue $Module.ConfiguredClockSpeed
            manufacturer = Get-SafeValue (Redact-Sensitive $Module.Manufacturer)
            part_number = Get-SafeValue (Redact-Sensitive $Module.PartNumber)
            memory_type = Get-SafeValue $Module.MemoryType
            form_factor = Get-SafeValue $Module.FormFactor
            locator = Get-SafeValue $Module.DeviceLocator
        }
    }

    $HardwareProfile.memory = @{
        total_gb = [math]::Round(($MemoryModules | Measure-Object -Property Capacity -Sum).Sum / 1GB, 0)
        modules_count = $MemoryModules.Count
        modules = $MemoryArray
        configuration = "$($MemoryModules.Count)x$([math]::Round($MemoryModules[0].Capacity / 1GB, 0))GB"
        speed_mhz = Get-SafeValue $MemoryModules[0].ConfiguredClockSpeed
    }
    $HardwareProfile.metadata.detection_coverage.memory = "100%"
    Write-Log "Memory: $($HardwareProfile.memory.total_gb)GB ($($HardwareProfile.memory.configuration))"
} catch {
    Write-Log "Error detecting memory: $($_.Exception.Message)" "ERROR"
    $HardwareProfile.metadata.errors += "Memory detection failed" 
    $HardwareProfile.metadata.detection_coverage.memory = "0%"
}

# === GPU INFORMATION ===
Write-Log "Detecting graphics adapters..."
try {
    $GraphicsAdapters = Get-CimInstance -ClassName Win32_VideoController -ErrorAction Stop
    $GpuArray = @()

    foreach ($Adapter in $GraphicsAdapters) {
        if ($Adapter.Name -notmatch "Remote|Basic|Microsoft") {
            $GpuArray += @{
                name = Get-SafeValue $Adapter.Name
                adapter_ram_mb = Get-SafeValue $Adapter.AdapterRAM
                adapter_ram_gb = if ($Adapter.AdapterRAM) { [math]::Round($Adapter.AdapterRAM / 1GB, 1) } else { "Unknown" }
                driver_version = Get-SafeValue $Adapter.DriverVersion
                driver_date = Get-SafeValue $Adapter.DriverDate
                video_architecture = Get-SafeValue $Adapter.VideoArchitecture
                video_memory_type = Get-SafeValue $Adapter.VideoMemoryType
                current_refresh_rate = Get-SafeValue $Adapter.CurrentRefreshRate
                max_refresh_rate = Get-SafeValue $Adapter.MaxRefreshRate
                pnp_device_id = Get-SafeValue $Adapter.PNPDeviceID
                type = if ($Adapter.Name -match "Intel|AMD.*Radeon.*Graphics") { "Integrated" } else { "Discrete" }
            }
        }
    }

    $HardwareProfile.gpu = $GpuArray
    $HardwareProfile.metadata.detection_coverage.gpu = "100%"
    Write-Log "GPUs detected: $($GpuArray.Count) adapters"
} catch {
    Write-Log "Error detecting GPU: $($_.Exception.Message)" "ERROR"
    $HardwareProfile.metadata.errors += "GPU detection failed"
    $HardwareProfile.metadata.detection_coverage.gpu = "0%"
}

# === STORAGE INFORMATION ===
Write-Log "Detecting storage devices..."
try {
    # Physical drives
    $PhysicalDisks = Get-CimInstance -ClassName Win32_DiskDrive -ErrorAction Stop
    $DriveArray = @()

    foreach ($Disk in $PhysicalDisks) {
        if ($Disk.Size -gt 0) {
            $DriveArray += @{
                name = Get-SafeValue $Disk.Model
                size_gb = [math]::Round($Disk.Size / 1GB, 0)
                media_type = Get-SafeValue $Disk.MediaType
                interface_type = Get-SafeValue $Disk.InterfaceType
                firmware_revision = Get-SafeValue (Redact-Sensitive $Disk.FirmwareRevision)
                serial_number = "REDACTED"
                partitions = Get-SafeValue $Disk.Partitions
                type = if ($Disk.Model -match "NVMe|NVME") { "NVMe" } elseif ($Disk.InterfaceType -eq "SCSI") { "SATA" } else { "Unknown" }
            }
        }
    }

    # Storage controllers
    $StorageControllers = Get-CimInstance -ClassName Win32_SCSIController -ErrorAction Stop
    $ControllerArray = @()

    foreach ($Controller in $StorageControllers) {
        $ControllerArray += @{
            name = Get-SafeValue $Controller.Name
            manufacturer = Get-SafeValue $Controller.Manufacturer
            driver_name = Get-SafeValue $Controller.Name
            pnp_device_id = Get-SafeValue $Controller.PNPDeviceID
        }
    }

    # BitLocker status
    $BitlockerVolumes = @()
    try {
        $BitlockerInfo = Get-BitLockerVolume -ErrorAction SilentlyContinue
        foreach ($Volume in $BitlockerInfo) {
            $BitlockerVolumes += @{
                mount_point = Get-SafeValue $Volume.MountPoint
                encryption_method = Get-SafeValue $Volume.EncryptionMethod
                volume_status = Get-SafeValue $Volume.VolumeStatus
                protection_status = Get-SafeValue $Volume.ProtectionStatus
            }
        }
    } catch {
        Write-Log "BitLocker detection failed (may not be available)" "WARN"
    }

    $HardwareProfile.storage.drives = $DriveArray
    $HardwareProfile.storage.controllers = $ControllerArray  
    $HardwareProfile.storage.encryption = $BitlockerVolumes
    $HardwareProfile.metadata.detection_coverage.storage = "100%"
    Write-Log "Storage: $($DriveArray.Count) drives detected"
} catch {
    Write-Log "Error detecting storage: $($_.Exception.Message)" "ERROR"
    $HardwareProfile.metadata.errors += "Storage detection failed"
    $HardwareProfile.metadata.detection_coverage.storage = "0%"
}

# === NETWORK INFORMATION ===
Write-Log "Detecting network adapters..."
try {
    $NetworkAdapters = Get-CimInstance -ClassName Win32_NetworkAdapter -ErrorAction Stop | Where-Object { $_.PhysicalAdapter -eq $true }

    $WifiArray = @()
    $EthernetArray = @()
    $BluetoothArray = @()

    foreach ($Adapter in $NetworkAdapters) {
        $AdapterInfo = @{
            name = Get-SafeValue $Adapter.Name
            manufacturer = Get-SafeValue $Adapter.Manufacturer
            mac_address = "XX:XX:XX:XX:XX:XX"  # Always redacted
            pnp_device_id = Get-SafeValue $Adapter.PNPDeviceID
            adapter_type = Get-SafeValue $Adapter.AdapterType
            net_connection_id = Get-SafeValue $Adapter.NetConnectionID
        }

        if ($Adapter.Name -match "Wi-Fi|WiFi|Wireless|802\.11") {
            $WifiArray += $AdapterInfo
        } elseif ($Adapter.Name -match "Ethernet|LAN|Network") {
            $EthernetArray += $AdapterInfo
        } elseif ($Adapter.Name -match "Bluetooth") {
            $BluetoothArray += $AdapterInfo
        }
    }

    $HardwareProfile.network.wifi = $WifiArray
    $HardwareProfile.network.ethernet = $EthernetArray 
    $HardwareProfile.network.bluetooth = $BluetoothArray
    $HardwareProfile.metadata.detection_coverage.network = "100%"
    Write-Log "Network: WiFi=$($WifiArray.Count), Ethernet=$($EthernetArray.Count), Bluetooth=$($BluetoothArray.Count)"
} catch {
    Write-Log "Error detecting network: $($_.Exception.Message)" "ERROR"
    $HardwareProfile.metadata.errors += "Network detection failed"
    $HardwareProfile.metadata.detection_coverage.network = "0%"
}

# === DISPLAY AND VIDEO ===  
Write-Log "Detecting display configuration..."
try {
    $Monitors = Get-CimInstance -ClassName Win32_DesktopMonitor -ErrorAction Stop
    $MonitorArray = @()

    foreach ($Monitor in $Monitors) {
        if ($Monitor.Name -and $Monitor.Name -ne "Default Monitor") {
            $MonitorArray += @{
                name = Get-SafeValue $Monitor.Name
                manufacturer = Get-SafeValue $Monitor.MonitorManufacturer
                screen_width = Get-SafeValue $Monitor.ScreenWidth
                screen_height = Get-SafeValue $Monitor.ScreenHeight
                pnp_device_id = Get-SafeValue $Monitor.PNPDeviceID
            }
        }
    }

