    $DisplaySettings = Get-CimInstance -ClassName Win32_VideoController | Select-Object -First 1

    $HardwareProfile.display_video = @{
        internal_display = @{
            resolution = "$($DisplaySettings.CurrentHorizontalResolution)x$($DisplaySettings.CurrentVerticalResolution)"
            refresh_rate = Get-SafeValue $DisplaySettings.CurrentRefreshRate
            bits_per_pixel = Get-SafeValue $DisplaySettings.CurrentBitsPerPixel
        }
        video_outputs = @()  # Would need specific hardware queries
        external_displays = $MonitorArray
        total_monitors = $MonitorArray.Count
    }
    $HardwareProfile.metadata.detection_coverage.display = "80%"
    Write-Log "Display: $($MonitorArray.Count) monitors detected"
} catch {
    Write-Log "Error detecting displays: $($_.Exception.Message)" "ERROR" 
    $HardwareProfile.metadata.errors += "Display detection failed"
    $HardwareProfile.metadata.detection_coverage.display = "0%"
}

# === USB AND CONNECTIVITY ===
Write-Log "Detecting USB and connectivity..."
try {
    $UsbControllers = Get-CimInstance -ClassName Win32_USBController -ErrorAction Stop
    $UsbArray = @()

    foreach ($Controller in $UsbControllers) {
        $UsbArray += @{
            name = Get-SafeValue $Controller.Name
            manufacturer = Get-SafeValue $Controller.Manufacturer
            pnp_device_id = Get-SafeValue $Controller.PNPDeviceID
            type = if ($Controller.Name -match "3\.") { "USB3" } elseif ($Controller.Name -match "4\.0|USB4") { "USB4" } else { "USB2" }
        }
    }

    $HardwareProfile.connectivity.usb_controllers = $UsbArray
    $HardwareProfile.metadata.detection_coverage.connectivity = "70%"
