    Write-Log "Audio: $($AudioArray.Count) devices detected"
} catch {
    Write-Log "Error detecting audio: $($_.Exception.Message)" "ERROR"
    $HardwareProfile.metadata.errors += "Audio detection failed"
    $HardwareProfile.metadata.detection_coverage.audio = "0%"
}

# === SECURITY INFORMATION ===
Write-Log "Detecting security features..."
try {
    # TPM Information
    try {
        $TpmInfo = Get-CimInstance -ClassName Win32_Tpm -Namespace "Root\CIMV2\Security\MicrosoftTpm" -ErrorAction Stop
        $HardwareProfile.security.tpm = @{
            present = $true
            version = Get-SafeValue $TpmInfo.SpecVersion
            ready = Get-SafeValue $TpmInfo.IsReady
            manufacturer = Get-SafeValue $TpmInfo.ManufacturerIdTxt
        }
    } catch {
        $HardwareProfile.security.tpm = @{
            present = $false
            version = "Not Available"
            ready = $false
        }
    }

    # Secure Boot
    try {
        $SecureBoot = Confirm-SecureBootUEFI -ErrorAction SilentlyContinue
        $HardwareProfile.security.secure_boot = @{
            enabled = $SecureBoot
            status = if ($SecureBoot) { "Enabled" } else { "Disabled" }
        }
    } catch {
        $HardwareProfile.security.secure_boot = @{
            enabled = "Unknown"
            status = "Cannot determine"
        }
    }

