        Write-Host "  Total RAM: $($HardwareProfile.memory.total_gb) GB" -ForegroundColor White
        Write-Host "  Configuration: $($HardwareProfile.memory.configuration)" -ForegroundColor White
        Write-Host "  Speed: $($HardwareProfile.memory.speed_mhz) MHz" -ForegroundColor Gray
        Write-Host "  Modules: $($HardwareProfile.memory.modules_count)" -ForegroundColor Gray
        foreach ($Module in $HardwareProfile.memory.modules) {
