$report | ConvertTo-Json -Depth 4 | Out-File "$env:USERPROFILE\Desktop\PopOS-PreFlight-SystemInfo.json"

# Final Decision
Write-Host "`n=== FINAL DECISION ===" -ForegroundColor Cyan
if ($report.Blockers.Count -gt 0) {
    Write-Host "âŒ BLOCKED: Fix all blockers before proceeding!" -ForegroundColor Red
    Write-Host "Blockers found: $($report.Blockers.Count)" -ForegroundColor Red
} elseif ($report.Warnings.Count -gt 0) {
    Write-Host "âš ï¸ PROCEED WITH CAUTION: Address warnings first" -ForegroundColor Yellow
    Write-Host "Warnings found: $($report.Warnings.Count)" -ForegroundColor Yellow
} else {
    Write-Host "âœ… ALL CLEAR: System ready for Pop!_OS workstation migration!" -ForegroundColor Green
}

Write-Host "`nReport saved to: $reportPath" -ForegroundColor Cyan
Write-Host "System info saved to: PopOS-PreFlight-SystemInfo.json" -ForegroundColor Cyan
Write-Host "`nThis system appears optimized for workstation-grade Linux deployment." -ForegroundColor Green

