#!/usr/bin/env powershell
<#
.SYNOPSIS
Scientific Ubuntu LTS vs Pop!_OS compatibility analysis and scoring engine

.DESCRIPTION
Phase 3: Analyzes hardware inventory against compatibility matrix to generate
scientific recommendation with confidence scores and detailed reasoning.

.PARAMETER HardwareInventoryPath  
Path to JSON hardware inventory file from Phase 1

.PARAMETER CompatibilityMatrixPath
Path to compatibility matrix JSON database

.PARAMETER OutputPath
Path where to save the analysis results

.PARAMETER ShowProgress
Display detailed progress and scoring breakdown

.EXAMPLE
.\Invoke-CompatibilityAnalysis.ps1 -HardwareInventoryPath "hw-inventory.json" -CompatibilityMatrixPath "compatibility-matrix.json" -OutputPath "analysis-results.json" -ShowProgress

.NOTES
Target System: Acer Predator PTX17-71 Workstation
Generated: 2025-08-06  
Phase: 3 - Scoring Engine and Scientific Analysis
#>

param(
    [Parameter(Mandatory=$false)]
    [string]$HardwareInventoryPath = "hw-inventory.json",

    [Parameter(Mandatory=$false)]  
    [string]$CompatibilityMatrixPath = "compatibility-matrix.json",

    [Parameter(Mandatory=$false)]
    [string]$OutputPath = "analysis-results.json",

    [Parameter(Mandatory=$false)]
    [switch]$ShowProgress = $false
)

$ErrorActionPreference = "Stop"

function Write-AnalysisLog {
    param([string]$Message, [string]$Level = "INFO")
    if ($ShowProgress) {
        $Color = switch($Level) {
            "ERROR" { "Red" }
            "WARN" { "Yellow" }  
            "SUCCESS" { "Green" }
            "SCORE" { "Cyan" }
            default { "White" }
        }
        Write-Host "[$Level] $Message" -ForegroundColor $Color
    }
}

Write-AnalysisLog "Starting Ubuntu LTS vs Pop!_OS compatibility analysis..." "SUCCESS"

# Load hardware inventory
if (!(Test-Path $HardwareInventoryPath)) {
    throw "Hardware inventory file not found: $HardwareInventoryPath. Run Get-SystemInventory.ps1 first."
}

try {
    $HardwareData = Get-Content $HardwareInventoryPath -Raw | ConvertFrom-Json
    Write-AnalysisLog "Loaded hardware inventory: $($HardwareData.system.manufacturer) $($HardwareData.system.model)"
} catch {
    throw "Failed to load hardware inventory: $($_.Exception.Message)"
}

# Load compatibility matrix
if (!(Test-Path $CompatibilityMatrixPath)) {
    throw "Compatibility matrix not found: $CompatibilityMatrixPath"
}

try {
    $CompatibilityMatrix = Get-Content $CompatibilityMatrixPath -Raw | ConvertFrom-Json
    Write-AnalysisLog "Loaded compatibility matrix (Version: $($CompatibilityMatrix.compatibility_matrix.metadata.version))"
} catch {
    throw "Failed to load compatibility matrix: $($_.Exception.Message)"
}

# Initialize analysis results
$AnalysisResults = @{
    metadata = @{
        analysis_date = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
        hardware_inventory_file = $HardwareInventoryPath
        compatibility_matrix_file = $CompatibilityMatrixPath
        target_system = "$($HardwareData.system.manufacturer) $($HardwareData.system.model)"
        analysis_version = "1.0"
    }
    hardware_summary = @{
        cpu = $HardwareData.cpu.name
        gpu = ($HardwareData.gpu | ForEach-Object { $_.name }) -join ", "
        memory = "$($HardwareData.memory.total_gb)GB $($HardwareData.memory.configuration)"
        storage = ($HardwareData.storage.drives | ForEach-Object { "$($_.name) ($($_.size_gb)GB)" }) -join ", "
        network = "WiFi: $($HardwareData.network.wifi.Count), Ethernet: $($HardwareData.network.ethernet.Count)"
    }
    scoring_breakdown = @{
        ubuntu_lts = @{
            total_score = 0
            weighted_score = 0
            category_scores = @{}
        }
        popos = @{
            total_score = 0
            weighted_score = 0
            category_scores = @{}  
        }
    }
    compatibility_analysis = @{
        ubuntu_lts = @{
            strengths = @()
            weaknesses = @()
            required_drivers = @()
            known_issues = @()
        }
        popos = @{
            strengths = @()
            weaknesses = @()
            required_drivers = @()
            known_issues = @()
        }
    }
    recommendation = @{
        winner = ""
        confidence_level = ""
        score_difference = 0
        reasoning = @()
        risk_assessment = @()
        next_steps = @()
    }
}

# Scoring weights from compatibility matrix
$Weights = $CompatibilityMatrix.compatibility_matrix.scoring_weights

Write-AnalysisLog "Analyzing compatibility across $($Weights.Keys.Count) categories..." "SCORE"

# === CPU ANALYSIS ===
Write-AnalysisLog "Analyzing CPU compatibility..."
$CpuName = $HardwareData.cpu.name
$CpuCompatibility = $CompatibilityMatrix.compatibility_matrix.cpu_compatibility."Intel Core i9-13900HX"

if ($CpuCompatibility) {
    $UbuntuCpuScore = $CpuCompatibility.ubuntu_lts.score
    $PoposCpuScore = $CpuCompatibility.popos.score

    $AnalysisResults.scoring_breakdown.ubuntu_lts.category_scores.cpu = @{
        raw_score = $UbuntuCpuScore
        weighted_score = $UbuntuCpuScore * $Weights.cpu
        support_level = $CpuCompatibility.ubuntu_lts.support_level
        notes = $CpuCompatibility.ubuntu_lts.notes
    }

    $AnalysisResults.scoring_breakdown.popos.category_scores.cpu = @{
        raw_score = $PoposCpuScore  
        weighted_score = $PoposCpuScore * $Weights.cpu
        support_level = $CpuCompatibility.popos.support_level
        notes = $CpuCompatibility.popos.notes
    }

    # Add to compatibility analysis
    if ($CpuCompatibility.ubuntu_lts.issues.Count -gt 0) {
        $AnalysisResults.compatibility_analysis.ubuntu_lts.known_issues += $CpuCompatibility.ubuntu_lts.issues
    }
    $AnalysisResults.compatibility_analysis.ubuntu_lts.required_drivers += $CpuCompatibility.ubuntu_lts.drivers_needed
    $AnalysisResults.compatibility_analysis.popos.required_drivers += $CpuCompatibility.popos.drivers_needed

    Write-AnalysisLog "CPU Scores - Ubuntu: $UbuntuCpuScore/10, Pop!_OS: $PoposCpuScore/10" "SCORE"
} else {
    Write-AnalysisLog "CPU compatibility data not found for: $CpuName" "WARN"
}

# === GPU ANALYSIS ===  
Write-AnalysisLog "Analyzing GPU compatibility..."
$NvidiaGpu = $HardwareData.gpu | Where-Object { $_.name -match "RTX|GeForce|NVIDIA" } | Select-Object -First 1
$GpuCompatibility = $CompatibilityMatrix.compatibility_matrix.gpu_compatibility."NVIDIA RTX 4090 Laptop"

if ($NvidiaGpu -and $GpuCompatibility) {
    $UbuntuGpuScore = $GpuCompatibility.ubuntu_lts.score
    $PoposGpuScore = $GpuCompatibility.popos.score

    $AnalysisResults.scoring_breakdown.ubuntu_lts.category_scores.gpu = @{
        raw_score = $UbuntuGpuScore
        weighted_score = $UbuntuGpuScore * $Weights.gpu
        support_level = $GpuCompatibility.ubuntu_lts.support_level
        notes = $GpuCompatibility.ubuntu_lts.notes
    }

    $AnalysisResults.scoring_breakdown.popos.category_scores.gpu = @{
        raw_score = $PoposGpuScore
        weighted_score = $PoposGpuScore * $Weights.gpu  
        support_level = $GpuCompatibility.popos.support_level
        notes = $GpuCompatibility.popos.notes
    }

    # GPU is critical for this workstation - add detailed analysis
    if ($GpuCompatibility.ubuntu_lts.issues.Count -gt 0) {
        $AnalysisResults.compatibility_analysis.ubuntu_lts.known_issues += $GpuCompatibility.ubuntu_lts.issues
        $AnalysisResults.compatibility_analysis.ubuntu_lts.weaknesses += "NVIDIA GPU setup complexity"
    }

    if ($PoposGpuScore -gt $UbuntuGpuScore) {
        $AnalysisResults.compatibility_analysis.popos.strengths += "Superior NVIDIA GPU support and automatic configuration"
        $AnalysisResults.compatibility_analysis.ubuntu_lts.weaknesses += "Manual NVIDIA driver installation required"
    }

    $AnalysisResults.compatibility_analysis.ubuntu_lts.required_drivers += $GpuCompatibility.ubuntu_lts.drivers_needed
    $AnalysisResults.compatibility_analysis.popos.required_drivers += $GpuCompatibility.popos.drivers_needed

    Write-AnalysisLog "GPU Scores - Ubuntu: $UbuntuGpuScore/10, Pop!_OS: $PoposGpuScore/10 (Weight: $($Weights.gpu))" "SCORE"
}

# === NETWORK ANALYSIS ===
Write-AnalysisLog "Analyzing network compatibility..."
$KillerWifi = $HardwareData.network.wifi | Where-Object { $_.name -match "Killer|AX1690" } | Select-Object -First 1
$NetworkCompatibility = $CompatibilityMatrix.compatibility_matrix.network_compatibility."Killer Wi-Fi 6E AX1690i"

if ($KillerWifi -and $NetworkCompatibility) {
    $UbuntuNetScore = $NetworkCompatibility.ubuntu_lts.score
    $PoposNetScore = $NetworkCompatibility.popos.score

    $AnalysisResults.scoring_breakdown.ubuntu_lts.category_scores.network = @{
        raw_score = $UbuntuNetScore
        weighted_score = $UbuntuNetScore * $Weights.network
        support_level = $NetworkCompatibility.ubuntu_lts.support_level
        notes = $NetworkCompatibility.ubuntu_lts.notes
    }

    $AnalysisResults.scoring_breakdown.popos.category_scores.network = @{
        raw_score = $PoposNetScore
        weighted_score = $PoposNetScore * $Weights.network
        support_level = $NetworkCompatibility.popos.support_level  
        notes = $NetworkCompatibility.popos.notes
    }

    Write-AnalysisLog "Network Scores - Ubuntu: $UbuntuNetScore/10, Pop!_OS: $PoposNetScore/10" "SCORE"
}

# === WORKSTATION FEATURES ANALYSIS ===
Write-AnalysisLog "Analyzing workstation-grade features..." 
$WorkstationFeatures = $CompatibilityMatrix.compatibility_matrix.workstation_features."Professional_Development"

if ($WorkstationFeatures) {
    $UbuntuWorkScore = $WorkstationFeatures.ubuntu_lts.score  
    $PoposWorkScore = $WorkstationFeatures.popos.score

    $AnalysisResults.scoring_breakdown.ubuntu_lts.category_scores.workstation_features = @{
        raw_score = $UbuntuWorkScore
        weighted_score = $UbuntuWorkScore * $Weights.workstation_features
        support_level = $WorkstationFeatures.ubuntu_lts.support_level
        notes = $WorkstationFeatures.ubuntu_lts.notes
        pros = $WorkstationFeatures.ubuntu_lts.pros
    }

    $AnalysisResults.scoring_breakdown.popos.category_scores.workstation_features = @{
        raw_score = $PoposWorkScore
        weighted_score = $PoposWorkScore * $Weights.workstation_features
        support_level = $WorkstationFeatures.popos.support_level
        notes = $WorkstationFeatures.popos.notes  
        pros = $WorkstationFeatures.popos.pros
    }

    # Add workstation-specific strengths
    $AnalysisResults.compatibility_analysis.ubuntu_lts.strengths += $WorkstationFeatures.ubuntu_lts.pros
    $AnalysisResults.compatibility_analysis.popos.strengths += $WorkstationFeatures.popos.pros

    Write-AnalysisLog "Workstation Scores - Ubuntu: $UbuntuWorkScore/10, Pop!_OS: $PoposWorkScore/10 (Weight: $($Weights.workstation_features))" "SCORE"
}

# Add default scores for categories not explicitly analyzed
$DefaultCategories = @{
    "memory" = @{ ubuntu = 8; popos = 9 }
    "storage" = @{ ubuntu = 9; popos = 10 }  
    "display" = @{ ubuntu = 7; popos = 9 }
    "audio" = @{ ubuntu = 8; popos = 9 }
    "input" = @{ ubuntu = 6; popos = 7 }
    "thermal" = @{ ubuntu = 7; popos = 8 }
    "power" = @{ ubuntu = 7; popos = 9 }
}

foreach ($Category in $DefaultCategories.Keys) {
    if ($Weights.$Category) {
        $UbuntuScore = $DefaultCategories.$Category.ubuntu
        $PoposScore = $DefaultCategories.$Category.popos

        $AnalysisResults.scoring_breakdown.ubuntu_lts.category_scores.$Category = @{
            raw_score = $UbuntuScore
            weighted_score = $UbuntuScore * $Weights.$Category
            support_level = "Estimated"
            notes = "Score estimated based on typical compatibility patterns"
        }

        $AnalysisResults.scoring_breakdown.popos.category_scores.$Category = @{
            raw_score = $PoposScore  
            weighted_score = $PoposScore * $Weights.$Category
            support_level = "Estimated"
            notes = "Score estimated based on Pop!_OS optimization patterns"
        }

        Write-AnalysisLog "$Category Scores - Ubuntu: $UbuntuScore/10, Pop!_OS: $PoposScore/10 (Estimated)" "SCORE"
    }
}

# === CALCULATE FINAL SCORES ===
Write-AnalysisLog "Calculating final weighted scores..." "SUCCESS"

$UbuntuTotalWeighted = 0
$PoposTotalWeighted = 0

foreach ($Category in $AnalysisResults.scoring_breakdown.ubuntu_lts.category_scores.Keys) {
    $UbuntuWeighted = $AnalysisResults.scoring_breakdown.ubuntu_lts.category_scores.$Category.weighted_score
    $PoposWeighted = $AnalysisResults.scoring_breakdown.popos.category_scores.$Category.weighted_score

    $UbuntuTotalWeighted += $UbuntuWeighted
    $PoposTotalWeighted += $PoposWeighted

    Write-AnalysisLog "$Category - Ubuntu: $UbuntuWeighted, Pop!_OS: $PoposWeighted" "SCORE"
}

$AnalysisResults.scoring_breakdown.ubuntu_lts.total_weighted_score = [math]::Round($UbuntuTotalWeighted, 2)
$AnalysisResults.scoring_breakdown.popos.total_weighted_score = [math]::Round($PoposTotalWeighted, 2)

Write-AnalysisLog "Total Weighted Scores - Ubuntu: $($AnalysisResults.scoring_breakdown.ubuntu_lts.total_weighted_score), Pop!_OS: $($AnalysisResults.scoring_breakdown.popos.total_weighted_score)" "SUCCESS"

# === GENERATE RECOMMENDATION ===
$ScoreDifference = [math]::Round($PoposTotalWeighted - $UbuntuTotalWeighted, 2)
$AnalysisResults.recommendation.score_difference = $ScoreDifference

$Thresholds = $CompatibilityMatrix.compatibility_matrix.recommendation_thresholds

if ([math]::Abs($ScoreDifference) -ge $Thresholds.strong_preference) {
    $ConfidenceLevel = "High"
    if ($ScoreDifference -gt 0) {
        $Winner = "Pop!_OS"
        $AnalysisResults.recommendation.reasoning += "Pop!_OS shows significant advantages in NVIDIA GPU support, system optimization, and workstation features"
    } else {
        $Winner = "Ubuntu LTS"
        $AnalysisResults.recommendation.reasoning += "Ubuntu LTS provides superior stability and compatibility for this configuration"
    }
} elseif ([math]::Abs($ScoreDifference) -ge $Thresholds.moderate_preference) {
    $ConfidenceLevel = "Medium"  
    $Winner = if ($ScoreDifference -gt 0) { "Pop!_OS" } else { "Ubuntu LTS" }
    $AnalysisResults.recommendation.reasoning += "Moderate preference based on specific hardware compatibility advantages"
} else {
    $ConfidenceLevel = "Low"
    $Winner = "No Clear Winner"
    $AnalysisResults.recommendation.reasoning += "Both distributions show similar compatibility levels for this hardware configuration"
}

$AnalysisResults.recommendation.winner = $Winner
$AnalysisResults.recommendation.confidence_level = $ConfidenceLevel

# Add specific reasoning based on hardware
if ($Winner -eq "Pop!_OS") {
    $AnalysisResults.recommendation.reasoning += "Critical advantages: Automatic NVIDIA RTX 4090 setup, superior power management, development-optimized defaults"
    $AnalysisResults.recommendation.next_steps += "Use Pop!_OS migration toolkit for automated installation and configuration"
    $AnalysisResults.recommendation.next_steps += "Leverage built-in NVIDIA drivers and CUDA support"
    $AnalysisResults.recommendation.next_steps += "Benefit from optimized thermal and power management"
} elseif ($Winner -eq "Ubuntu LTS") {
    $AnalysisResults.recommendation.reasoning += "Advantages: LTS stability, broader enterprise support, extensive documentation"
    $AnalysisResults.recommendation.next_steps += "Plan for manual NVIDIA driver installation and configuration"
    $AnalysisResults.recommendation.next_steps += "Set up additional power management tools (TLP, PowerTop)"
    $AnalysisResults.recommendation.next_steps += "Configure high refresh rate display manually"
}

# Risk Assessment
if ($Winner -eq "Pop!_OS") {
    $AnalysisResults.recommendation.risk_assessment += "Lower risk: Automated hardware support reduces configuration complexity"
    $AnalysisResults.recommendation.risk_assessment += "Consider: Smaller community compared to Ubuntu, fewer third-party guides"
} else {
    $AnalysisResults.recommendation.risk_assessment += "Medium risk: NVIDIA setup complexity may require troubleshooting"
    $AnalysisResults.recommendation.risk_assessment += "Mitigation: Extensive Ubuntu documentation and community support available"
}

Write-AnalysisLog "=== ANALYSIS COMPLETE ===" "SUCCESS"
Write-AnalysisLog "Ubuntu LTS Score: $($AnalysisResults.scoring_breakdown.ubuntu_lts.total_weighted_score)/10.0" "SCORE"
Write-AnalysisLog "Pop!_OS Score: $($AnalysisResults.scoring_breakdown.popos.total_weighted_score)/10.0" "SCORE"  
Write-AnalysisLog "Recommendation: $Winner (Confidence: $ConfidenceLevel)" "SUCCESS"
Write-AnalysisLog "Score Difference: $ScoreDifference" "SCORE"

# Save analysis results
try {
    $JsonOutput = $AnalysisResults | ConvertTo-Json -Depth 10
    $JsonOutput | Out-File -FilePath $OutputPath -Encoding UTF8
    Write-AnalysisLog "Analysis results saved to: $OutputPath" "SUCCESS"
} catch {
    throw "Failed to save analysis results: $($_.Exception.Message)"
}

if ($ShowProgress) {
    Write-Host "`n=== RECOMMENDATION SUMMARY ===" -ForegroundColor Cyan
    Write-Host "Winner: $Winner" -ForegroundColor $(if($Winner -eq "Pop!_OS"){"Green"}elseif($Winner -eq "Ubuntu LTS"){"Blue"}else{"Yellow"})
    Write-Host "Confidence: $ConfidenceLevel" -ForegroundColor White
    Write-Host "Ubuntu LTS: $($AnalysisResults.scoring_breakdown.ubuntu_lts.total_weighted_score)/10.0" -ForegroundColor Blue
    Write-Host "Pop!_OS: $($AnalysisResults.scoring_breakdown.popos.total_weighted_score)/10.0" -ForegroundColor Green
    Write-Host "Difference: $ScoreDifference points" -ForegroundColor Cyan

    Write-Host "`nKey Reasoning:" -ForegroundColor Yellow
    foreach ($Reason in $AnalysisResults.recommendation.reasoning) {
        Write-Host "â€¢ $Reason" -ForegroundColor White
    }
}

return $AnalysisResults

