# 1. BitLocker Check (BLOCKER if enabled)
Write-Host "`nChecking BitLocker status..." -NoNewline
$bitlocker = Get-BitLockerVolume -MountPoint C: -ErrorAction SilentlyContinue
if ($bitlocker.ProtectionStatus -eq "On") {
    $report.Blockers += "âŒ BitLocker is ACTIVE on C: drive - MUST disable before dual boot"
    Write-Host " BLOCKER!" -ForegroundColor Red
} else {
    $report.GreenLights += "âœ… BitLocker is disabled"
    Write-Host " OK" -ForegroundColor Green
}

# 2. Fast Startup Check
Write-Host "Checking Fast Startup..." -NoNewline
$fastStartup = powercfg /a | Select-String "Fast startup"
if ($fastStartup -match "available") {
    $report.Warnings += "âš ï¸ Fast Startup is enabled - must disable for dual boot"
    Write-Host " WARNING" -ForegroundColor Yellow
} else {
    $report.GreenLights += "âœ… Fast Startup already disabled"
    Write-Host " OK" -ForegroundColor Green
}

# 3. System Integrity
Write-Host "Checking Windows system integrity..." -NoNewline
$sfcResult = sfc /verifyonly | Select-String "violations"
if ($sfcResult) {
    $report.Warnings += "âš ï¸ Windows system files need repair - run 'sfc /scannow'"
    Write-Host " ISSUES FOUND" -ForegroundColor Yellow
} else {
    $report.GreenLights += "âœ… Windows system files intact"
    Write-Host " OK" -ForegroundColor Green
}

# 4. Disk Space Analysis
Write-Host "Analyzing disk space..." -NoNewline
$fDrive = Get-PSDrive F -ErrorAction SilentlyContinue
if ($fDrive.Free -lt 400GB) {
    $report.Blockers += "âŒ Insufficient space on F: drive (need 400GB, have $([math]::Round($fDrive.Free/1GB))GB)"
    Write-Host " INSUFFICIENT" -ForegroundColor Red
} else {
    $report.GreenLights += "âœ… F: drive has $([math]::Round($fDrive.Free/1GB))GB free"
    Write-Host " OK" -ForegroundColor Green
}

# 5. Filesystem Health Check
Write-Host "Checking F: drive filesystem health..." -NoNewline
$driveHealth = Get-PhysicalDisk | Where-Object {$_.FriendlyName -match "NVMe"}
$allHealthy = $true
foreach ($disk in $driveHealth) {
    if ($disk.HealthStatus -ne "Healthy") {
        $allHealthy = $false
        $report.Blockers += "âŒ Drive health issue detected: $($disk.FriendlyName) - $($disk.HealthStatus)"
    }
}

if ($allHealthy) {
    $report.GreenLights += "âœ… All drives report healthy status"
    Write-Host " OK" -ForegroundColor Green

    # Recommend chkdsk
    Write-Host "`nRECOMMENDED: Run 'chkdsk F: /f' before installation" -ForegroundColor Yellow
    $report.Warnings += "âš ï¸ Run 'chkdsk F: /f' to ensure filesystem integrity before installation"
} else {
    Write-Host " ISSUES DETECTED!" -ForegroundColor Red
}

# 6. Workstation Hardware Check
Write-Host "Checking workstation-grade hardware..." -NoNewline
$cpu = Get-WmiObject Win32_Processor
$gpu = Get-WmiObject Win32_VideoController | Where-Object {$_.Name -match "NVIDIA"}
$ram = Get-WmiObject Win32_ComputerSystem

if ($cpu.NumberOfCores -ge 8 -and $ram.TotalPhysicalMemory -ge 16GB -and $gpu) {
    $report.GreenLights += "âœ… Workstation-grade hardware detected (CPU: $($cpu.Name), RAM: $([math]::Round($ram.TotalPhysicalMemory/1GB))GB, GPU: $($gpu.Name))"
    Write-Host " EXCELLENT" -ForegroundColor Green
} else {
    $report.Warnings += "âš ï¸ Hardware may not meet workstation requirements"
    Write-Host " CHECK" -ForegroundColor Yellow
}

# 7. Critical Drivers Backup
Write-Host "Exporting critical drivers list..."
$report.System.Drivers = Get-WmiObject Win32_PnPSignedDriver | 
    Where-Object {$_.DeviceName -match "Network|Display|Storage|Thunderbolt"} |
    Select-Object DeviceName, DriverVersion, DriverDate

# 8. USB & Thunderbolt Devices Check
Write-Host "Checking USB and Thunderbolt devices..." -NoNewline
$usbDevices = Get-PnpDevice -Class USB -PresentOnly | 
    Where-Object {$_.Status -eq 'OK'} |
    Select-Object FriendlyName, DeviceID, Manufacturer

$thunderboltDevices = Get-PnpDevice | 
    Where-Object {$_.FriendlyName -match "Thunderbolt"} |
    Select-Object FriendlyName, DeviceID, Status

$report.System.USBDevices = $usbDevices
$report.System.ThunderboltDevices = $thunderboltDevices

if ($thunderboltDevices.Count -gt 0) {
    $report.GreenLights += "âœ… Thunderbolt controller detected - important for external displays"
    Write-Host " FOUND" -ForegroundColor Green
} else {
    Write-Host " OK" -ForegroundColor Green
}

# 9. Security Certificates Export
Write-Host "Checking for certificates to migrate..."
$certs = Get-ChildItem -Path Cert:\CurrentUser\My -ErrorAction SilentlyContinue | 
    Where-Object {$_.HasPrivateKey} |
    Select-Object Subject, Thumbprint, NotAfter

if ($certs.Count -gt 0) {
    $report.System.Certificates = $certs
    $report.Warnings += "âš ï¸ Found $($certs.Count) certificates with private keys - manual export needed"
}

# 10. Password Manager Detection
Write-Host "Checking for password managers..."
$passwordManagers = @(
    @{Name="Bitwarden"; Path="$env:LOCALAPPDATA\Bitwarden"},
    @{Name="1Password"; Path="$env:LOCALAPPDATA\1Password"},
    @{Name="LastPass"; Path="$env:LOCALAPPDATA\LastPass"},
    @{Name="KeePass"; Path="$env:PROGRAMFILES\KeePass*"}
)

$foundManagers = @()
foreach ($pm in $passwordManagers) {
    if (Test-Path $pm.Path) {
        $foundManagers += $pm.Name
    }
}

if ($foundManagers.Count -gt 0) {
    $report.System.PasswordManagers = $foundManagers
    $report.Warnings += "âš ï¸ Password manager(s) found: $($foundManagers -join ', ') - export vault before migration"
}

# 11. Network Configuration Export
Write-Host "Exporting network configuration..."
$report.System.NetworkConfig = @{
    WiFiProfiles = netsh wlan show profiles | Select-String ":" | 
        ForEach-Object {($_ -split ":")[1].Trim()}
    IPConfig = Get-NetIPConfiguration
    DNSServers = Get-DnsClientServerAddress
}

# 12. Development Tools Check
Write-Host "Checking development environment..."
$devTools = @{
    NodeJS = Get-Command node -ErrorAction SilentlyContinue
    Git = Get-Command git -ErrorAction SilentlyContinue
    Docker = Get-Service Docker -ErrorAction SilentlyContinue
    VSCode = Test-Path "$env:LOCALAPPDATA\Programs\Microsoft VS Code"
}

$report.System.DevelopmentTools = $devTools
if ($devTools.NodeJS -and $devTools.Git) {
    $report.GreenLights += "âœ… Core development tools detected"
}

# 13. Installed Software List
Write-Host "Documenting installed software..."
