#!/usr/bin/env node

/**
 * Simple Architecture Validation Tool
 * Validates repository structure against architectural principles
 * No external dependencies required
 */

const fs = require('fs').promises;
const path = require('path');

class SimpleArchitectureValidator {
    constructor(rootPath) {
        this.rootPath = rootPath;
        this.errors = [];
        this.warnings = [];
        this.info = [];
        
        this.requiredDirectories = [
            'knowledge',
            'examples',
            'templates',
            '.claude/agents',
            '.claude/commands',
            '.claude/hooks',
            '.claude/events',
            '.claude/validation'
        ];
        
        this.requiredFiles = [
            'README.md',
            'CLAUDE.md',
            'knowledge/README.md',
            'examples/README.md',
            'knowledge/fundamentals/ai-software-engineering.md'
        ];
    }
    
    async validate() {
        console.log('ðŸ” Validating Claude Code Ecosystem Architecture...\n');
        
        // Check directory structure
        await this.validateDirectoryStructure();
        
        // Check required files
        await this.validateRequiredFiles();
        
        // Validate component registries
        await this.validateComponentRegistries();
        
        // Validate content structure
        await this.validateContentStructure();
        
        // Generate report
        this.generateReport();
        
        return {
            success: this.errors.length === 0,
            errors: this.errors,
            warnings: this.warnings,
            info: this.info
        };
    }
    
    async validateDirectoryStructure() {
        console.log('ðŸ“ Validating directory structure...');
        
        for (const dir of this.requiredDirectories) {
            const dirPath = path.join(this.rootPath, dir);
            try {
                const stats = await fs.stat(dirPath);
                if (!stats.isDirectory()) {
                    this.errors.push(`${dir} exists but is not a directory`);
                } else {
                    this.info.push(`âœ“ Directory exists: ${dir}`);
                }
            } catch (error) {
                this.errors.push(`Missing required directory: ${dir}`);
            }
        }
    }
    
    async validateRequiredFiles() {
        console.log('ðŸ“„ Validating required files...');
        
        for (const file of this.requiredFiles) {
            const filePath = path.join(this.rootPath, file);
            try {
                await fs.access(filePath);
                this.info.push(`âœ“ File exists: ${file}`);
            } catch (error) {
                this.errors.push(`Missing required file: ${file}`);
            }
        }
    }
    
    async validateComponentRegistries() {
        console.log('ðŸ—‚ï¸ Validating component registries...');
        
        const registries = ['agents', 'hooks', 'commands', 'components'];
        
        for (const registry of registries) {
            const registryPath = path.join(this.rootPath, '.claude', 'registry', `${registry}.json`);
            
            try {
                const content = await fs.readFile(registryPath, 'utf8');
                const data = JSON.parse(content);
                
                // Check required fields
                if (!data.registry_version) {
                    this.errors.push(`${registry}.json missing registry_version field`);
                }
                
                if (!data.last_updated) {
                    this.errors.push(`${registry}.json missing last_updated field`);
                }
                
                this.info.push(`âœ“ Registry valid: ${registry}.json`);
                
            } catch (error) {
                this.errors.push(`Invalid registry ${registry}.json: ${error.message}`);
            }
        }
    }
    
    async validateContentStructure() {
        console.log('ðŸ“š Validating content structure...');
        
        // Check knowledge structure
        const knowledgePath = path.join(this.rootPath, 'knowledge');
        const knowledgeSubdirs = ['fundamentals'];
        
        for (const subdir of knowledgeSubdirs) {
            const subdirPath = path.join(knowledgePath, subdir);
            try {
                const stats = await fs.stat(subdirPath);
                if (stats.isDirectory()) {
                    this.info.push(`âœ“ Knowledge subdirectory exists: ${subdir}`);
                }
            } catch {
                this.warnings.push(`Missing knowledge subdirectory: knowledge/${subdir}`);
            }
        }
        
        // Check examples structure exists
        const examplesPath = path.join(this.rootPath, 'examples');
        try {
            const stats = await fs.stat(examplesPath);
            if (stats.isDirectory()) {
                this.info.push(`âœ“ Examples directory exists`);
            }
        } catch {
            this.errors.push(`Missing examples directory`);
        }
    }
    
    generateReport() {
        console.log('\nðŸ“Š Validation Report\n');
        console.log('=' .repeat(50));
        
        if (this.errors.length === 0) {
            console.log('âœ… Architecture validation PASSED');
        } else {
            console.log('âŒ Architecture validation FAILED');
        }
        
        console.log(`\nErrors: ${this.errors.length}`);
        console.log(`Warnings: ${this.warnings.length}`);
        console.log(`Info: ${this.info.length}`);
        
        if (this.errors.length > 0) {
            console.log('\nðŸš¨ ERRORS:');
            this.errors.forEach(error => console.log(`  âŒ ${error}`));
        }
        
        if (this.warnings.length > 0) {
            console.log('\nâš ï¸ WARNINGS:');
            this.warnings.forEach(warning => console.log(`  âš ï¸ ${warning}`));
        }
        
        if (process.env.VERBOSE) {
            console.log('\nâ„¹ï¸ INFO:');
            this.info.forEach(info => console.log(`  ${info}`));
        }
        
        console.log('\n' + '=' .repeat(50));
    }
}

// CLI interface
if (require.main === module) {
    const rootPath = process.argv[2] || process.cwd();
    const validator = new SimpleArchitectureValidator(rootPath);
    
    validator.validate().then(result => {
        process.exit(result.success ? 0 : 1);
    }).catch(error => {
        console.error('Validation failed:', error);
        process.exit(1);
    });
}

module.exports = { SimpleArchitectureValidator };

