#!/usr/bin/env python3
"""
Repository Knowledge MCP Server
Exposes claude-code-ecosystem research materials as MCP resources and tools
"""

import json
import os
import sys
from pathlib import Path
from typing import Any, Dict, List, Optional

import asyncio
from mcp.server import Server, NotificationOptions
from mcp.server.models import InitializationOptions
import mcp.server.stdio
import mcp.types as types

# Repository structure constants
RESEARCH_DIR = "data/raw/temp/markdown"
RESEARCH_FILES = {
    "untitled-01": "Operational briefing on Claude Code's agentic capabilities",
    "untitled-02": "Study guide covering AI in software engineering fundamentals", 
    "untitled-03": "Executive briefing on AI's transformative impact",
    "untitled-04": "Analysis of agentic AI systems' impact on workflows",
    "untitled-05": "Claude Code memory management features and best practices",
    "untitled-06": "Reserved for future content",
    "untitled-07": "Model Context Protocol (MCP) architecture overview"
}

server = Server("repository-knowledge")

@server.list_resources()
async def handle_list_resources() -> List[types.Resource]:
    """List all available research resources"""
    resources = []

    repo_path = os.environ.get("REPO_PATH", ".")
    research_path = Path(repo_path) / RESEARCH_DIR

    # Add research documents as resources
    for file_key, description in RESEARCH_FILES.items():
        file_path = research_path / f"{file_key.replace('-', '-').title()}.md"
        if file_path.exists() or file_key != "untitled-06":  # Skip empty file
            resources.append(types.Resource(
                uri=f"research://{file_key}",
                name=f"Research Document: {file_key.replace('-', ' ').title()}",
                description=description,
                mimeType="text/markdown"
            ))

    # Add configuration resources
    resources.extend([
        types.Resource(
            uri="config://claude-md",
            name="CLAUDE.md Configuration",
            description="Main project memory and configuration file",
            mimeType="text/markdown"
        ),
        types.Resource(
            uri="config://commands",
            name="Slash Commands",
            description="Available custom slash commands for research workflows",
            mimeType="application/json"
        ),
        types.Resource(
            uri="config://agents", 
            name="Sub-Agent Configurations",
            description="Specialized sub-agent definitions and capabilities",
            mimeType="application/json"
        )
    ])

    return resources

@server.read_resource()
async def handle_read_resource(uri: str) -> str:
    """Read content from repository resources"""
    repo_path = os.environ.get("REPO_PATH", ".")

    if uri.startswith("research://"):
        # Handle research document requests
        file_key = uri.replace("research://", "")
        file_name = f"Untitled-{file_key.split('-')[1].zfill(2)}.md"
        file_path = Path(repo_path) / RESEARCH_DIR / file_name

        if file_path.exists():
            return file_path.read_text(encoding='utf-8')
        else:
            return f"Research document {file_key} not found or empty"

    elif uri == "config://claude-md":
        claude_md_path = Path(repo_path) / "CLAUDE.md"
        if claude_md_path.exists():
            return claude_md_path.read_text(encoding='utf-8')
        else:
            return "CLAUDE.md not found"

    elif uri == "config://commands":
        commands_dir = Path(repo_path) / ".claude" / "commands"
        commands_info = {}

        if commands_dir.exists():
            for cmd_file in commands_dir.glob("*.md"):
                if cmd_file.name != "README.md":
                    commands_info[cmd_file.stem] = {
                        "name": cmd_file.stem,
                        "path": str(cmd_file),
                        "description": f"Slash command: /{cmd_file.stem}"
                    }

        return json.dumps(commands_info, indent=2)

    elif uri == "config://agents":
        agents_dir = Path(repo_path) / ".claude" / "agents"
        agents_info = {}

        if agents_dir.exists():
            for agent_file in agents_dir.glob("*.md"):
                agents_info[agent_file.stem] = {
                    "name": agent_file.stem,
                    "path": str(agent_file),
                    "description": f"Sub-agent: {agent_file.stem}"
                }

        return json.dumps(agents_info, indent=2)

    else:
        raise ValueError(f"Unknown resource URI: {uri}")

@server.list_tools()
async def handle_list_tools() -> List[types.Tool]:
    """List available tools for repository interaction"""
    return [
        types.Tool(
            name="search_research",
            description="Search across all research documents for specific topics or keywords",
            inputSchema={
                "type": "object",
                "properties": {
                    "query": {
                        "type": "string",
                        "description": "Search query or topic to find in research materials"
                    },
                    "file_filter": {
                        "type": "string", 
                        "description": "Optional: specific research file to search (e.g., 'untitled-05')",
                        "enum": list(RESEARCH_FILES.keys())
                    }
                },
                "required": ["query"]
            }
        ),
        types.Tool(
            name="analyze_patterns",
            description="Analyze patterns and connections across multiple research documents",
            inputSchema={
                "type": "object",
                "properties": {
                    "topic": {
                        "type": "string",
                        "description": "Topic to analyze for patterns across research materials"
                    },
                    "analysis_type": {
                        "type": "string",
                        "description": "Type of analysis to perform",
                        "enum": ["connections", "best_practices", "implementation", "optimization"]
                    }
                },
                "required": ["topic", "analysis_type"]
            }
        ),
        types.Tool(
            name="get_citations",
            description="Generate properly formatted citations for research findings",
            inputSchema={
                "type": "object", 
                "properties": {
                    "topic": {
                        "type": "string",
                        "description": "Topic or concept to find citations for"
                    },
                    "format": {
                        "type": "string",
                        "description": "Citation format to use",
                        "enum": ["repository", "academic", "implementation"],
                        "default": "repository"
                    }
                },
                "required": ["topic"]
            }
        )
    ]

@server.call_tool()
async def handle_call_tool(name: str, arguments: Dict[str, Any]) -> List[types.TextContent]:
    """Handle tool execution"""
    repo_path = os.environ.get("REPO_PATH", ".")
    research_path = Path(repo_path) / RESEARCH_DIR

    if name == "search_research":
        query = arguments["query"].lower()
        file_filter = arguments.get("file_filter")
        results = []

        files_to_search = [file_filter] if file_filter else RESEARCH_FILES.keys()

        for file_key in files_to_search:
            if file_key == "untitled-06":  # Skip empty file
                continue

            file_name = f"Untitled-{file_key.split('-')[1].zfill(2)}.md"
            file_path = research_path / file_name

            if file_path.exists():
                content = file_path.read_text(encoding='utf-8')
                if query in content.lower():
                    # Extract relevant sections
                    lines = content.split('\n')
                    relevant_lines = []
                    for i, line in enumerate(lines):
                        if query in line.lower():
                            # Add context around match
                            start = max(0, i-2)
                            end = min(len(lines), i+3)
                            context = '\n'.join(lines[start:end])
                            relevant_lines.append(f"Match in {file_key}:\n{context}\n---")

                    if relevant_lines:
                        results.extend(relevant_lines)

        if results:
            return [types.TextContent(type="text", text=f"Search results for '{query}':\n\n" + '\n\n'.join(results))]
        else:
            return [types.TextContent(type="text", text=f"No results found for '{query}'")]

    elif name == "analyze_patterns":
        topic = arguments["topic"]
        analysis_type = arguments["analysis_type"]

        # This would implement sophisticated pattern analysis
        # For now, provide a framework response
        analysis_result = f"""
Pattern Analysis: {topic} ({analysis_type})

Based on repository research materials:

1. **Cross-Document Connections**: [Analysis would identify connections across research files]
2. **Best Practices Identified**: [Extract documented best practices]  
3. **Implementation Patterns**: [Identify common implementation approaches]
4. **Optimization Opportunities**: [Highlight optimization strategies]

Note: This is a framework implementation. Full pattern analysis would require 
sophisticated NLP processing of the research corpus.
"""

        return [types.TextContent(type="text", text=analysis_result)]

    elif name == "get_citations":
        topic = arguments["topic"]
        citation_format = arguments.get("format", "repository")

        # Generate citations in repository format
        if citation_format == "repository":
            citation_example = f"(per Untitled-XX.md:{topic.lower().replace(' ', '-')})"
        else:
            citation_example = f"Citation format '{citation_format}' for topic '{topic}'"

        return [types.TextContent(type="text", text=f"Citation format for '{topic}': {citation_example}")]

    else:
        raise ValueError(f"Unknown tool: {name}")

async def main():
    # Run the server using stdin/stdout streams
    async with mcp.server.stdio.stdio_server() as (read_stream, write_stream):
        await server.run(
            read_stream,
            write_stream,
            InitializationOptions(
                server_name="repository-knowledge",
                server_version="1.0.0",
                capabilities=server.get_capabilities(
                    notification_options=NotificationOptions(),
                    experimental_capabilities={},
                )
            )
        )

if __name__ == "__main__":
    asyncio.run(main())

