#!/usr/bin/env node

/**
 * Architecture Validation Tool
 * Validates repository structure against architectural principles
 */

const fs = require('fs').promises;
const path = require('path');

// Simple YAML parser for basic frontmatter (no external dependencies)
function parseYaml(yamlContent) {
    const lines = yamlContent.split('\n');
    const result = {};

    for (const line of lines) {
        const match = line.match(/^(\w+):\s*(.+)$/);
        if (match) {
            const [, key, value] = match;
            // Handle arrays
            if (value.startsWith('[') && value.endsWith(']')) {
                result[key] = value.slice(1, -1).split(',').map(v => v.trim().replace(/['"]/g, ''));
            } else {
                result[key] = value.replace(/['"]/g, '');
            }
        }
    }

    return result;
}

class ArchitectureValidator {
    constructor(rootPath) {
        this.rootPath = rootPath;
        this.errors = [];
        this.warnings = [];
        this.info = [];

        this.requiredDirectories = [
            'domains',
            'domains/fundamentals',
            'domains/advanced', 
            'domains/specializations',
            'layers',
            'layers/presentation',
            'layers/application',
            'layers/domain',
            'layers/infrastructure',
            '.claude/registry',
            '.claude/events',
            '.claude/metadata'
        ];

        this.requiredFiles = [
            'domains/README.md',
            'domains/fundamentals/README.md',
            'domains/advanced/README.md',
            'domains/specializations/README.md',
            'layers/README.md',
            '.claude/registry/agents.json',
            '.claude/registry/hooks.json',
            '.claude/registry/commands.json',
            '.claude/registry/components.json',
            '.claude/events/schemas/events.json',
            '.claude/metadata/schemas.yaml'
        ];
    }

    async validate() {
        console.log('ðŸ” Validating Claude Code Ecosystem Architecture...\n');

        // Check directory structure
        await this.validateDirectoryStructure();

        // Check required files
        await this.validateRequiredFiles();

        // Validate component registries
        await this.validateComponentRegistries();

        // Validate metadata schemas
        await this.validateMetadataSchemas();

        // Validate domain structure
        await this.validateDomainStructure();

        // Validate layer separation
        await this.validateLayerSeparation();

        // Check content metadata compliance
        await this.validateContentMetadata();

        // Generate report
        this.generateReport();

        return {
            success: this.errors.length === 0,
            errors: this.errors,
            warnings: this.warnings,
            info: this.info
        };
    }

    async validateDirectoryStructure() {
        console.log('ðŸ“ Validating directory structure...');

        for (const dir of this.requiredDirectories) {
            const dirPath = path.join(this.rootPath, dir);
            try {
                const stats = await fs.stat(dirPath);
                if (!stats.isDirectory()) {
                    this.errors.push(`${dir} exists but is not a directory`);
                } else {
                    this.info.push(`âœ“ Directory exists: ${dir}`);
                }
            } catch (error) {
                this.errors.push(`Missing required directory: ${dir}`);
            }
        }
    }

    async validateRequiredFiles() {
        console.log('ðŸ“„ Validating required files...');

        for (const file of this.requiredFiles) {
            const filePath = path.join(this.rootPath, file);
            try {
                await fs.access(filePath);
                this.info.push(`âœ“ File exists: ${file}`);
            } catch (error) {
                this.errors.push(`Missing required file: ${file}`);
            }
        }
    }

    async validateComponentRegistries() {
        console.log('ðŸ—‚ï¸ Validating component registries...');

        const registries = ['agents', 'hooks', 'commands', 'components'];

        for (const registry of registries) {
            const registryPath = path.join(this.rootPath, '.claude', 'registry', `${registry}.json`);

            try {
                const content = await fs.readFile(registryPath, 'utf8');
                const data = JSON.parse(content);

                // Check required fields
                if (!data.registry_version) {
                    this.errors.push(`${registry}.json missing registry_version field`);
                }

                if (!data.last_updated) {
                    this.errors.push(`${registry}.json missing last_updated field`);
                }

                // Validate structure based on type
                if (registry === 'agents' && data.agents) {
                    this.validateAgentRegistry(data.agents, registry);
                } else if (registry === 'hooks' && data.hooks) {
                    this.validateHookRegistry(data.hooks, registry);
                } else if (registry === 'commands' && data.commands) {
                    this.validateCommandRegistry(data.commands, registry);
                }

                this.info.push(`âœ“ Registry valid: ${registry}.json`);

            } catch (error) {
                this.errors.push(`Invalid registry ${registry}.json: ${error.message}`);
            }
        }
    }

    validateAgentRegistry(agents, registryName) {
        for (const agent of agents) {
            const required = ['id', 'name', 'version', 'file_path', 'domain', 'specialization'];
            for (const field of required) {
                if (!agent[field]) {
                    this.errors.push(`Agent ${agent.id || 'unknown'} missing required field: ${field}`);
                }
            }

            // Check domain values
            if (agent.domain && !['fundamentals', 'advanced', 'specializations'].includes(agent.domain)) {
                this.warnings.push(`Agent ${agent.id} has invalid domain: ${agent.domain}`);
            }

            // Check version format
            if (agent.version && !/^\d+\.\d+\.\d+$/.test(agent.version)) {
                this.warnings.push(`Agent ${agent.id} has invalid version format: ${agent.version}`);
            }
        }
    }

    validateHookRegistry(hooks, registryName) {
        const validTriggers = [
            'UserPromptSubmit', 'PreToolUse', 'PostToolUse', 'Notification',
            'Stop', 'SubagentStop', 'PreCompact', 'SessionStart'
        ];

        for (const hook of hooks) {
            const required = ['id', 'name', 'version', 'file_path', 'trigger_type', 'purpose'];
            for (const field of required) {
                if (!hook[field]) {
                    this.errors.push(`Hook ${hook.id || 'unknown'} missing required field: ${field}`);
                }
            }

            // Check trigger type
            if (hook.trigger_type && !validTriggers.includes(hook.trigger_type)) {
                this.errors.push(`Hook ${hook.id} has invalid trigger_type: ${hook.trigger_type}`);
            }
        }
    }

    validateCommandRegistry(commands, registryName) {
        for (const command of commands) {
            const required = ['id', 'name', 'version', 'file_path', 'command_type', 'description'];
            for (const field of required) {
                if (!command[field]) {
                    this.errors.push(`Command ${command.id || 'unknown'} missing required field: ${field}`);
                }
            }

            // Check command type
            const validTypes = ['slash', 'custom', 'workflow', 'mcp'];
            if (command.command_type && !validTypes.includes(command.command_type)) {
                this.warnings.push(`Command ${command.id} has invalid command_type: ${command.command_type}`);
            }
        }
    }

    async validateMetadataSchemas() {
        console.log('ðŸ“‹ Validating metadata schemas...');

        const schemaPath = path.join(this.rootPath, '.claude', 'metadata', 'schemas.yaml');

        try {
            const content = await fs.readFile(schemaPath, 'utf8');
            const schemas = yaml.load(content);

            const requiredSchemas = [
                'knowledge_article', 'example', 'template', 
                'agent', 'hook', 'command'
            ];

            for (const schemaName of requiredSchemas) {
                if (!schemas[schemaName]) {
                    this.errors.push(`Missing metadata schema: ${schemaName}`);
                } else {
                    this.info.push(`âœ“ Schema exists: ${schemaName}`);
                }
            }

        } catch (error) {
            this.errors.push(`Failed to validate metadata schemas: ${error.message}`);
        }
    }

    async validateDomainStructure() {
        console.log('ðŸ—ï¸ Validating domain structure...');

        const domains = ['fundamentals', 'advanced', 'specializations'];
        const requiredSubdirs = ['knowledge', 'examples', 'templates', 'agents'];

        for (const domain of domains) {
            const domainPath = path.join(this.rootPath, 'domains', domain);

            // Check domain README
            const readmePath = path.join(domainPath, 'README.md');
            try {
                await fs.access(readmePath);
                this.info.push(`âœ“ Domain README exists: ${domain}`);
            } catch {
                this.errors.push(`Missing domain README: domains/${domain}/README.md`);
            }

            // Check required subdirectories
            for (const subdir of requiredSubdirs) {
                const subdirPath = path.join(domainPath, subdir);
                try {
                    const stats = await fs.stat(subdirPath);
                    if (stats.isDirectory()) {
                        this.info.push(`âœ“ Domain subdirectory exists: ${domain}/${subdir}`);
                    }
                } catch {
                    this.warnings.push(`Missing domain subdirectory: domains/${domain}/${subdir}`);
                }
            }
        }
    }

    async validateLayerSeparation() {
        console.log('ðŸ›ï¸ Validating layer separation...');

        const layers = ['presentation', 'application', 'domain', 'infrastructure'];

        for (const layer of layers) {
            const layerPath = path.join(this.rootPath, 'layers', layer);

            try {
                const stats = await fs.stat(layerPath);
                if (stats.isDirectory()) {
                    this.info.push(`âœ“ Layer directory exists: ${layer}`);
                }
            } catch {
                this.warnings.push(`Layer directory missing: layers/${layer}`);
            }
        }
    }

    async validateContentMetadata() {
        console.log('ðŸ“ Validating content metadata compliance...');

        // Check knowledge articles
        await this.validateKnowledgeMetadata();

        // Check examples
        await this.validateExampleMetadata();

        // Check templates
        await this.validateTemplateMetadata();
    }

    async validateKnowledgeMetadata() {
        const knowledgePattern = 'domains/*/knowledge/*.md';
        const knowledgeFiles = await this.globFiles(knowledgePattern);

        for (const file of knowledgeFiles) {
            try {
                const content = await fs.readFile(file, 'utf8');
                const frontmatter = this.extractFrontmatter(content);

                if (!frontmatter) {
                    this.warnings.push(`Knowledge article missing frontmatter: ${file}`);
                    continue;
                }

                const required = ['type', 'domain', 'complexity', 'learning_objectives', 'estimated_time'];
                for (const field of required) {
                    if (!frontmatter[field]) {
                        this.warnings.push(`Knowledge article missing required metadata field '${field}': ${file}`);
                    }
                }

                if (frontmatter.type !== 'knowledge-article') {
                    this.warnings.push(`Knowledge article has incorrect type '${frontmatter.type}': ${file}`);
                }

            } catch (error) {
                this.warnings.push(`Failed to validate metadata for: ${file}`);
            }
        }
    }

    async validateExampleMetadata() {
        const examplePattern = 'domains/*/examples/*/README.md';
        const exampleFiles = await this.globFiles(examplePattern);

        for (const file of exampleFiles) {
            try {
                const content = await fs.readFile(file, 'utf8');
                const frontmatter = this.extractFrontmatter(content);

                if (frontmatter && frontmatter.type === 'example') {
                    const required = ['domain', 'complexity', 'success_criteria', 'estimated_time'];
                    for (const field of required) {
                        if (!frontmatter[field]) {
                            this.warnings.push(`Example missing required metadata field '${field}': ${file}`);
                        }
                    }
                }

            } catch (error) {
                // Skip validation errors for examples
            }
        }
    }

    async validateTemplateMetadata() {
        const templatePattern = 'domains/*/templates/*.md';
        const templateFiles = await this.globFiles(templatePattern);

        for (const file of templateFiles) {
            try {
                const content = await fs.readFile(file, 'utf8');
                const frontmatter = this.extractFrontmatter(content);

                if (frontmatter && frontmatter.type === 'template') {
                    const required = ['template_category', 'target_audience', 'customization_points'];
                    for (const field of required) {
                        if (!frontmatter[field]) {
                            this.warnings.push(`Template missing required metadata field '${field}': ${file}`);
                        }
                    }
                }

            } catch (error) {
                // Skip validation errors for templates
            }
        }
    }

    extractFrontmatter(content) {
        const match = content.match(/^---\r?\n([\s\S]*?)\r?\n---/);
        if (match) {
            try {
                return yaml.load(match[1]);
            } catch {
                return null;
            }
        }
        return null;
    }

    async globFiles(pattern) {
        // Simple glob implementation for this validator
        const parts = pattern.split('/');
        let currentPaths = [this.rootPath];

        for (const part of parts) {
            const nextPaths = [];

            for (const currentPath of currentPaths) {
                if (part === '*') {
                    try {
                        const entries = await fs.readdir(currentPath);
                        for (const entry of entries) {
                            const entryPath = path.join(currentPath, entry);
                            const stats = await fs.stat(entryPath);
                            if (stats.isDirectory()) {
                                nextPaths.push(entryPath);
                            }
                        }
                    } catch {
                        // Skip directories that don't exist
                    }
                } else if (part.includes('*')) {
                    try {
                        const entries = await fs.readdir(currentPath);
                        const regex = new RegExp(part.replace(/\*/g, '.*'));
                        for (const entry of entries) {
                            if (regex.test(entry)) {
                                nextPaths.push(path.join(currentPath, entry));
                            }
                        }
                    } catch {
                        // Skip directories that don't exist
                    }
                } else {
                    nextPaths.push(path.join(currentPath, part));
                }
            }

            currentPaths = nextPaths;
        }

        // Filter to only existing files
        const files = [];
