{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Claude Code Ecosystem Event Schemas",
  "description": "Standardized event definitions for analytics and integration",
  "type": "object",
  "properties": {
    "schema_version": {
      "type": "string",
      "pattern": "^\\d+\\.\\d+\\.\\d+$"
    }
  },
  "$defs": {
    "base_event": {
      "type": "object",
      "properties": {
        "event_id": {
          "type": "string",
          "format": "uuid",
          "description": "Unique event identifier"
        },
        "timestamp": {
          "type": "string",
          "format": "date-time",
          "description": "ISO timestamp when event occurred"
        },
        "session_id": {
          "type": "string",
          "description": "Claude Code session identifier"
        },
        "user_id": {
          "type": "string",
          "description": "User identifier (if available)"
        },
        "event_type": {
          "type": "string",
          "description": "Type of event"
        },
        "source": {
          "type": "string",
          "description": "Component that generated the event"
        }
      },
      "required": ["event_id", "timestamp", "session_id", "event_type", "source"]
    },
    "knowledge_accessed": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "knowledge.accessed"
            },
            "data": {
              "type": "object",
              "properties": {
                "article_path": {
                  "type": "string",
                  "description": "Path to the accessed knowledge article"
                },
                "domain": {
                  "type": "string",
                  "enum": ["fundamentals", "advanced", "specializations"]
                },
                "subdomain": {
                  "type": "string"
                },
                "complexity": {
                  "type": "string",
                  "enum": ["beginner", "intermediate", "advanced", "expert"]
                },
                "access_method": {
                  "type": "string",
                  "enum": ["direct", "search", "navigation", "recommendation"]
                },
                "reading_time": {
                  "type": "integer",
                  "description": "Time spent reading in seconds"
                }
              },
              "required": ["article_path", "domain", "access_method"]
            }
          }
        }
      ]
    },
    "example_started": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "example.started"
            },
            "data": {
              "type": "object",
              "properties": {
                "example_path": {
                  "type": "string"
                },
                "domain": {
                  "type": "string",
                  "enum": ["fundamentals", "advanced", "specializations"]
                },
                "complexity": {
                  "type": "string",
                  "enum": ["beginner", "intermediate", "advanced", "expert"]
                },
                "estimated_duration": {
                  "type": "integer",
                  "description": "Estimated completion time in minutes"
                },
                "prerequisites_met": {
                  "type": "boolean"
                }
              },
              "required": ["example_path", "domain", "complexity"]
            }
          }
        }
      ]
    },
    "example_completed": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "example.completed"
            },
            "data": {
              "type": "object",
              "properties": {
                "example_path": {
                  "type": "string"
                },
                "domain": {
                  "type": "string",
                  "enum": ["fundamentals", "advanced", "specializations"]
                },
                "complexity": {
                  "type": "string",
                  "enum": ["beginner", "intermediate", "advanced", "expert"]
                },
                "actual_duration": {
                  "type": "integer",
                  "description": "Actual completion time in minutes"
                },
                "success": {
                  "type": "boolean",
                  "description": "Whether example was completed successfully"
                },
                "errors_encountered": {
                  "type": "integer",
                  "description": "Number of errors during completion"
                },
                "help_requests": {
                  "type": "integer",
                  "description": "Number of times help was requested"
                }
              },
              "required": ["example_path", "domain", "complexity", "actual_duration", "success"]
            }
          }
        }
      ]
    },
    "agent_delegated": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "agent.delegated"
            },
            "data": {
              "type": "object",
              "properties": {
                "agent_id": {
                  "type": "string"
                },
                "agent_name": {
                  "type": "string"
                },
                "task_description": {
                  "type": "string"
                },
                "delegation_trigger": {
                  "type": "string",
                  "description": "What triggered the delegation"
                },
                "context_size": {
                  "type": "integer",
                  "description": "Size of context passed to agent in tokens"
                },
                "estimated_complexity": {
                  "type": "string",
                  "enum": ["low", "medium", "high", "very_high"]
                }
              },
              "required": ["agent_id", "agent_name", "task_description", "context_size"]
            }
          }
        }
      ]
    },
    "agent_completed": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "agent.completed"
            },
            "data": {
              "type": "object",
              "properties": {
                "agent_id": {
                  "type": "string"
                },
                "agent_name": {
                  "type": "string"
                },
                "task_description": {
                  "type": "string"
                },
                "execution_time": {
                  "type": "integer",
                  "description": "Execution time in milliseconds"
                },
                "tokens_used": {
                  "type": "integer"
                },
                "success": {
                  "type": "boolean"
                },
                "output_quality": {
                  "type": "string",
                  "enum": ["excellent", "good", "acceptable", "poor"],
                  "description": "Subjective quality assessment"
                },
                "error_message": {
                  "type": "string",
                  "description": "Error message if task failed"
                }
              },
              "required": ["agent_id", "agent_name", "task_description", "execution_time", "success"]
            }
          }
        }
      ]
    },
    "optimization_suggested": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "optimization.suggested"
            },
            "data": {
              "type": "object",
              "properties": {
                "component_type": {
                  "type": "string",
                  "enum": ["agent", "hook", "command", "workflow", "knowledge", "example"]
                },
                "component_id": {
                  "type": "string"
                },
                "suggestion_type": {
                  "type": "string",
                  "enum": ["performance", "usability", "content", "structure", "integration"]
                },
                "suggestion": {
                  "type": "string",
                  "description": "Detailed optimization suggestion"
                },
                "confidence": {
                  "type": "number",
                  "minimum": 0,
                  "maximum": 1,
                  "description": "Confidence score for the suggestion"
                },
                "impact": {
                  "type": "string",
                  "enum": ["low", "medium", "high", "critical"],
                  "description": "Expected impact of implementing suggestion"
                },
                "evidence": {
                  "type": "array",
                  "items": {
                    "type": "string"
                  },
                  "description": "Evidence supporting the suggestion"
                }
              },
              "required": ["component_type", "component_id", "suggestion_type", "suggestion", "confidence", "impact"]
            }
          }
        }
      ]
    },
    "hook_executed": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "hook.executed"
            },
            "data": {
              "type": "object",
              "properties": {
                "hook_id": {
                  "type": "string"
                },
                "hook_name": {
                  "type": "string"
                },
                "trigger_type": {
                  "type": "string",
                  "enum": [
                    "UserPromptSubmit", "PreToolUse", "PostToolUse", "Notification",
                    "Stop", "SubagentStop", "PreCompact", "SessionStart"
                  ]
                },
                "tool_name": {
                  "type": "string",
                  "description": "Tool that triggered the hook (if applicable)"
                },
                "execution_time": {
                  "type": "integer",
                  "description": "Hook execution time in milliseconds"
                },
                "exit_code": {
                  "type": "integer",
                  "description": "Hook exit code"
                },
                "blocked_execution": {
                  "type": "boolean",
                  "description": "Whether hook blocked the triggering action"
                },
                "output_size": {
                  "type": "integer",
                  "description": "Size of hook output in bytes"
                }
              },
              "required": ["hook_id", "hook_name", "trigger_type", "execution_time", "exit_code"]
            }
          }
        }
      ]
    }
  },
  "schema_version": "1.0.0"
}

