    analyzeTaskApproach(events) {
        const taskEvents = events.filter(e => e.type.includes('task') || e.type.includes('command'));
        if (taskEvents.length === 0) return { methodical: 0.5, exploratory: 0.5, systematic: 0.5 };

        let methodicalScore = 0;
        let exploratoryScore = 0;
        let systematicScore = 0;

        for (let i = 0; i < taskEvents.length - 1; i++) {
            const current = taskEvents[i];
            const next = taskEvents[i + 1];

            // Check for methodical patterns (sequential, step-by-step)
            if (next.timestamp - current.timestamp < 60000) { // Within 1 minute
                methodicalScore += 0.1;
            }

            // Check for exploratory patterns (diverse tools, frequent changes)
            if (current.data && next.data && current.data.toolsUsed && next.data.toolsUsed) {
                const toolOverlap = current.data.toolsUsed.filter(tool => 
                    next.data.toolsUsed.includes(tool)
                ).length;
                const totalTools = new Set([...current.data.toolsUsed, ...next.data.toolsUsed]).size;

                if (toolOverlap / totalTools < 0.5) {
                    exploratoryScore += 0.1;
                }
            }

            // Check for systematic patterns (consistent approach, similar task types)
            if (current.type === next.type) {
                systematicScore += 0.1;
            }
        }

        // Normalize scores
        const total = methodicalScore + exploratoryScore + systematicScore || 1;
        return {
            methodical: methodicalScore / total,
            exploratory: exploratoryScore / total,
            systematic: systematicScore / total
        };
    }

    updateToolPreferences(events) {
        const toolUsage = new Map();

        events.forEach(event => {
            if (event.data && event.data.toolsUsed) {
                event.data.toolsUsed.forEach(tool => {
                    const usage = toolUsage.get(tool) || { count: 0, successRate: 0, lastUsed: 0 };
                    usage.count++;
                    usage.lastUsed = event.timestamp;
                    if (event.data.success) {
                        usage.successRate = (usage.successRate * (usage.count - 1) + 1) / usage.count;
                    } else {
                        usage.successRate = (usage.successRate * (usage.count - 1)) / usage.count;
                    }
                    toolUsage.set(tool, usage);
                });
            }
        });

        return toolUsage;
    }

    identifyWorkingPatterns(events) {
        if (events.length === 0) return { sessionLength: 0, taskComplexity: 'medium' };

        const sessionDuration = events[events.length - 1].timestamp - events[0].timestamp;
        const sessionLength = sessionDuration / (1000 * 60); // minutes

        const taskComplexities = events
            .filter(e => e.data && e.data.complexity)
            .map(e => e.data.complexity);

        const avgComplexity = taskComplexities.length > 0 
            ? this.calculateAverageComplexity(taskComplexities)
            : 'medium';

        return {
            sessionLength,
            taskComplexity: avgComplexity,
            eventsPerMinute: events.length / Math.max(sessionLength, 1),
            toolSwitchFrequency: this.calculateToolSwitchFrequency(events)
        };
    }

    calculateBehaviorEvolution(sessionId) {
        const session = this.sessions.get(sessionId);
        if (!session || session.events.length < 10) return null;

        const events = session.events;
        const midpoint = Math.floor(events.length / 2);

        const firstHalf = events.slice(0, midpoint);
        const secondHalf = events.slice(midpoint);

        const firstHalfProfile = {
            communication: this.analyzeCommunicationPatterns(firstHalf),
            taskApproach: this.analyzeTaskApproach(firstHalf),
            toolPrefs: this.updateToolPreferences(firstHalf)
        };

        const secondHalfProfile = {
            communication: this.analyzeCommunicationPatterns(secondHalf),
            taskApproach: this.analyzeTaskApproach(secondHalf),
            toolPrefs: this.updateToolPreferences(secondHalf)
        };

        return {
            communicationEvolution: this.calculateEvolution(firstHalfProfile.communication, secondHalfProfile.communication),
            taskApproachEvolution: this.calculateEvolution(firstHalfProfile.taskApproach, secondHalfProfile.taskApproach),
            toolPreferenceEvolution: this.calculateToolPreferenceEvolution(firstHalfProfile.toolPrefs, secondHalfProfile.toolPrefs)
        };
    }

    // Helper methods for calculations
    blendMetrics(current, new_, blendFactor) {
        const result = {};
        for (const key in current) {
            if (typeof current[key] === 'number' && typeof new_[key] === 'number') {
                result[key] = current[key] * (1 - blendFactor) + new_[key] * blendFactor;
            } else {
                result[key] = new_[key] !== undefined ? new_[key] : current[key];
            }
        }
        return result;
    }

    calculateTaskPerformanceTrend(taskType) {
        const metrics = this.taskPerformanceMetrics.get(taskType);
        if (!metrics || metrics.userSatisfactionScores.length < 5) {
            return 'insufficient_data';
        }

        const scores = metrics.userSatisfactionScores;
        const recentScores = scores.slice(-5);
        const olderScores = scores.slice(-10, -5);

        if (olderScores.length === 0) return 'insufficient_data';

        const recentAvg = recentScores.reduce((a, b) => a + b) / recentScores.length;
        const olderAvg = olderScores.reduce((a, b) => a + b) / olderScores.length;

        const difference = recentAvg - olderAvg;

        if (difference > 0.3) return 'improving';
        if (difference < -0.3) return 'declining';
        return 'stable';
    }

    calculateConfidence(analysis) {
        let totalWeight = 0;
        let weightedConfidence = 0;

        Object.values(analysis.insights).forEach(insight => {
            if (insight && typeof insight === 'object') {
                const dataPoints = this.countDataPoints(insight);
                const weight = Math.min(dataPoints / 10, 1); // Max weight of 1 for 10+ data points
                totalWeight += weight;
                weightedConfidence += weight * this.calculateInsightConfidence(insight);
            }
        });

        return totalWeight > 0 ? weightedConfidence / totalWeight : 0.5;
    }

    countDataPoints(insight) {
        let count = 0;
        if (Array.isArray(insight)) count += insight.length;
        else if (typeof insight === 'object') {
            Object.values(insight).forEach(value => {
                if (Array.isArray(value)) count += value.length;
                else if (typeof value === 'number') count += 1;
            });
        }
        return count;
    }

    calculateInsightConfidence(insight) {
        // Base confidence calculation on data richness and consistency
        const dataPoints = this.countDataPoints(insight);
        const baseConfidence = Math.min(dataPoints / 20, 0.9); // Max 0.9 confidence

        // Add noise to prevent overconfidence
        const noise = (Math.random() - 0.5) * 0.1;
        return Math.max(0.1, Math.min(0.95, baseConfidence + noise));
    }

    // Data persistence and cleanup
    async updateAnalyticsCache(event, analysis) {
        const now = Date.now();
        this.analyticsCache.lastUpdate = now;

        // Update user behavior profiles
        if (analysis.insights.userBehavior) {
            this.analyticsCache.userBehaviorProfiles.set(event.sessionId, analysis.insights.userBehavior);
        }

        // Update task success rates
        if (analysis.insights.taskPerformance) {
            const taskType = analysis.insights.taskPerformance.taskType;
            this.analyticsCache.taskSuccessRates.set(taskType, analysis.insights.taskPerformance.successRate);
        }

        // Update tool effectiveness
        if (analysis.insights.toolEffectiveness) {
            Object.entries(analysis.insights.toolEffectiveness).forEach(([tool, stats]) => {
                this.analyticsCache.toolEffectiveness.set(tool, stats);
            });
        }

        // Update workflow efficiency
        if (analysis.insights.workflowPatterns) {
            const patternKey = this.generatePatternKey(analysis.insights.workflowPatterns);
            this.analyticsCache.workflowEfficiency.set(patternKey, analysis.insights.workflowPatterns.patternEffectiveness);
        }
    }

    async loadHistoricalData() {
        try {
            const files = [
                'sessions.json',
                'user_profiles.json',
                'tool_usage.json',
                'task_metrics.json',
                'workflow_patterns.json'
            ];

            for (const file of files) {
                const filePath = path.join(this.config.dataDir, file);
                try {
                    const data = JSON.parse(await fs.readFile(filePath, 'utf8'));
                    this.loadDataFromFile(file, data);
                } catch (error) {
                    if (error.code !== 'ENOENT') {
                        console.error(`Error loading ${file}:`, error);
                    }
                }
            }
        } catch (error) {
            console.error('Error loading historical data:', error);
        }
    }

    loadDataFromFile(filename, data) {
        switch (filename) {
            case 'sessions.json':
                this.sessions = new Map(data);
                break;
            case 'user_profiles.json':
                this.userProfiles = new Map(data);
                break;
            case 'tool_usage.json':
                this.toolUsageStats = new Map(data);
                break;
            case 'task_metrics.json':
                this.taskPerformanceMetrics = new Map(data);
                break;
            case 'workflow_patterns.json':
                this.workflowPatterns = new Map(data);
                break;
        }
    }

    async saveAnalyticsData() {
        try {
            const dataToSave = [
                { filename: 'sessions.json', data: Array.from(this.sessions.entries()) },
                { filename: 'user_profiles.json', data: Array.from(this.userProfiles.entries()) },
                { filename: 'tool_usage.json', data: Array.from(this.toolUsageStats.entries()) },
                { filename: 'task_metrics.json', data: Array.from(this.taskPerformanceMetrics.entries()) },
                { filename: 'workflow_patterns.json', data: Array.from(this.workflowPatterns.entries()) }
            ];

            for (const { filename, data } of dataToSave) {
                const filePath = path.join(this.config.dataDir, filename);
                await fs.writeFile(filePath, JSON.stringify(data, null, 2));
            }
        } catch (error) {
            console.error('Error saving analytics data:', error);
        }
    }

    async cleanupOldData() {
        const cutoffDate = Date.now() - (this.config.retentionDays * 24 * 60 * 60 * 1000);

        // Cleanup old sessions
        for (const [sessionId, session] of this.sessions) {
            if (session.startTime < cutoffDate) {
                this.sessions.delete(sessionId);
            }
        }

        // Cleanup optimization history
        this.optimizationHistory = this.optimizationHistory.filter(
            opt => opt.timestamp > cutoffDate
        );
    }

    // Placeholder methods for pattern analysis (to be fully implemented)
    identifySequencePatterns(events) {
        // Analyze common sequences of events/tools
        return { commonSequences: [], sequenceEffectiveness: {} };
    }

    identifyTimingPatterns(events) {
        // Analyze timing between events
        return { averageInterval: 0, peakTimes: [], patterns: [] };
    }

    identifyToolCombinations(events) {
        // Analyze which tools are commonly used together
        return { commonCombinations: [], combinationEffectiveness: {} };
    }

    identifyTaskProgression(events) {
        // Analyze how tasks progress and evolve
        return { progressionPatterns: [], successPatterns: [] };
    }

    identifyErrorRecoveryPatterns(events) {
        // Analyze how users recover from errors
        return { recoveryStrategies: [], recoveryEffectiveness: {} };
    }

    generatePatternKey(patterns) {
        // Generate a unique key for a pattern combination
        return crypto.createHash('md5')
            .update(JSON.stringify(patterns))
            .digest('hex');
    }

    generateWorkflowRecommendations(patterns) {
        // Generate recommendations based on workflow patterns
        return ['recommendation1', 'recommendation2'];
    }

    compareToolUsage(successfulEvents, failedEvents) {
        // Compare tool usage between successful and failed events
        return [];
    }

    compareTimingPatterns(successfulEvents, failedEvents) {
        // Compare timing patterns between successful and failed events
        return [];
    }

    compareContexts(successfulEvents, failedEvents) {
        // Compare contexts between successful and failed events
        return [];
    }

    compareUserBehaviors(successfulEvents, failedEvents) {
        // Compare user behaviors between successful and failed events
        return [];
    }

    calculateAverageComplexity(complexities) {
        const weights = { 'low': 1, 'medium': 2, 'high': 3, 'very_high': 4 };
        const sum = complexities.reduce((acc, complexity) => acc + (weights[complexity] || 2), 0);
        const avg = sum / complexities.length;

        if (avg <= 1.5) return 'low';
        if (avg <= 2.5) return 'medium';
        if (avg <= 3.5) return 'high';
        return 'very_high';
    }

    calculateToolSwitchFrequency(events) {
        let switches = 0;
        let lastTool = null;

        events.forEach(event => {
            if (event.data && event.data.toolsUsed && event.data.toolsUsed.length > 0) {
                const currentTool = event.data.toolsUsed[0]; // Primary tool
                if (lastTool && lastTool !== currentTool) {
                    switches++;
                }
                lastTool = currentTool;
            }
        });

        return switches / Math.max(events.length - 1, 1);
    }

    calculateEvolution(first, second) {
        const evolution = {};
        for (const key in first) {
            if (typeof first[key] === 'number' && typeof second[key] === 'number') {
                evolution[key] = second[key] - first[key];
            }
        }
        return evolution;
    }

    calculateToolPreferenceEvolution(firstTools, secondTools) {
        const evolution = { increased: [], decreased: [], new: [] };

        // Check for increases/decreases in existing tools
        for (const [tool, firstUsage] of firstTools) {
            const secondUsage = secondTools.get(tool);
            if (secondUsage) {
                const change = secondUsage.count - firstUsage.count;
                if (change > 0) evolution.increased.push({ tool, change });
                else if (change < 0) evolution.decreased.push({ tool, change: Math.abs(change) });
            }
        }

        // Check for new tools
        for (const [tool] of secondTools) {
            if (!firstTools.has(tool)) {
                evolution.new.push(tool);
            }
        }

        return evolution;
    }

    // Analytics reporting methods
    generateUserAnalytics() {
        const analytics = {
            totalUsers: this.userProfiles.size,
            averageSessionLength: 0,
            mostCommonCommunicationStyle: this.getMostCommonCommunicationStyle(),
            averageTaskApproach: this.getAverageTaskApproach(),
            topToolPreferences: this.getTopToolPreferences()
        };

        // Calculate average session length
        let totalDuration = 0;
        let sessionCount = 0;
        for (const session of this.sessions.values()) {
            if (session.events.length > 0) {
                const duration = session.events[session.events.length - 1].timestamp - session.startTime;
                totalDuration += duration;
                sessionCount++;
            }
        }
        analytics.averageSessionLength = sessionCount > 0 ? totalDuration / sessionCount : 0;

        return analytics;
    }

    generateTaskAnalytics() {
        const analytics = {
            totalTaskTypes: this.taskPerformanceMetrics.size,
            overallSuccessRate: 0,
            averageTaskDuration: 0,
            topPerformingTasks: [],
            challengingTasks: []
        };

        let totalTasks = 0;
        let totalSuccesses = 0;
        let totalDuration = 0;
        const taskRates = [];

        for (const [taskType, metrics] of this.taskPerformanceMetrics) {
            totalTasks += metrics.totalTasks;
            totalSuccesses += metrics.successfulTasks;
            totalDuration += metrics.averageDuration * metrics.totalTasks;

            taskRates.push({
                taskType,
                successRate: metrics.successfulTasks / metrics.totalTasks,
                totalTasks: metrics.totalTasks
            });
        }

        analytics.overallSuccessRate = totalTasks > 0 ? totalSuccesses / totalTasks : 0;
        analytics.averageTaskDuration = totalTasks > 0 ? totalDuration / totalTasks : 0;

        taskRates.sort((a, b) => b.successRate - a.successRate);
        analytics.topPerformingTasks = taskRates.slice(0, 5);
        analytics.challengingTasks = taskRates.slice(-5).reverse();

        return analytics;
    }

    generateToolAnalytics() {
        const analytics = {
            totalTools: this.toolUsageStats.size,
            mostUsedTools: [],
            mostEffectiveTools: [],
            toolSuccessRates: {}
        };

        const toolData = [];
        for (const [tool, stats] of this.toolUsageStats) {
            const successRate = stats.successCount / stats.usageCount;
            const avgSatisfaction = stats.userSatisfactionScores.length > 0 
                ? stats.userSatisfactionScores.reduce((a, b) => a + b) / stats.userSatisfactionScores.length 
                : null;

            toolData.push({
                tool,
                usageCount: stats.usageCount,
                successRate,
                avgSatisfaction,
                errorRate: stats.errorCount / stats.usageCount
            });

            analytics.toolSuccessRates[tool] = successRate;
        }

        toolData.sort((a, b) => b.usageCount - a.usageCount);
        analytics.mostUsedTools = toolData.slice(0, 10);

        toolData.sort((a, b) => b.successRate - a.successRate);
        analytics.mostEffectiveTools = toolData.slice(0, 10);

        return analytics;
    }

    generateWorkflowAnalytics() {
        return {
            totalPatterns: this.workflowPatterns.size,
            mostEffectivePatterns: this.getMostEffectivePatterns(),
            commonWorkflowIssues: this.getCommonWorkflowIssues(),
            workflowOptimizationOpportunities: this.getWorkflowOptimizationOpportunities()
        };
    }

    generateOptimizationAnalytics() {
        return {
            totalOptimizations: this.optimizationHistory.length,
            implementedOptimizations: this.optimizationHistory.filter(opt => opt.status === 'implemented').length,
            successfulOptimizations: this.optimizationHistory.filter(opt => opt.status === 'implemented' && opt.result?.success).length,
            topOptimizationTypes: this.getTopOptimizationTypes(),
            optimizationTrends: this.getOptimizationTrends()
        };
    }

    // Helper methods for analytics
    getMostCommonCommunicationStyle() {
        // Implementation for getting most common communication style
        return 'balanced';
    }

    getAverageTaskApproach() {
        // Implementation for getting average task approach
        return { methodical: 0.6, exploratory: 0.4 };
    }

    getTopToolPreferences() {
        // Implementation for getting top tool preferences
        return [];
    }

    getMostEffectivePatterns() {
        // Implementation for getting most effective patterns
        return [];
    }

    getCommonWorkflowIssues() {
        // Implementation for getting common workflow issues
        return [];
    }

    getWorkflowOptimizationOpportunities() {
        // Implementation for getting workflow optimization opportunities
        return [];
    }

    getTopOptimizationTypes() {
        // Implementation for getting top optimization types
        return [];
    }

    getOptimizationTrends() {
        // Implementation for getting optimization trends
        return [];
    }
}

