    analyzeTaskApproach(events) {
        const taskEvents = events.filter(e => e.type.includes('task') || e.type.includes('command'));
        if (taskEvents.length === 0) return { methodical: 0.5, exploratory: 0.5, systematic: 0.5 };

        let methodicalScore = 0;
        let exploratoryScore = 0;
        let systematicScore = 0;

        for (let i = 0; i < taskEvents.length - 1; i++) {
            const current = taskEvents[i];
            const next = taskEvents[i + 1];

            // Check for methodical patterns (sequential, step-by-step)
            if (next.timestamp - current.timestamp < 60000) { // Within 1 minute
                methodicalScore += 0.1;
            }

            // Check for exploratory patterns (diverse tools, frequent changes)
            if (current.data && next.data && current.data.toolsUsed && next.data.toolsUsed) {
                const toolOverlap = current.data.toolsUsed.filter(tool => 
                    next.data.toolsUsed.includes(tool)
                ).length;
                const totalTools = new Set([...current.data.toolsUsed, ...next.data.toolsUsed]).size;

                if (toolOverlap / totalTools < 0.5) {
                    exploratoryScore += 0.1;
                }
            }

            // Check for systematic patterns (consistent approach, similar task types)
            if (current.type === next.type) {
                systematicScore += 0.1;
            }
        }

        // Normalize scores
        const total = methodicalScore + exploratoryScore + systematicScore || 1;
        return {
            methodical: methodicalScore / total,
            exploratory: exploratoryScore / total,
            systematic: systematicScore / total
        };
    }

    updateToolPreferences(events) {
        const toolUsage = new Map();

        events.forEach(event => {
            if (event.data && event.data.toolsUsed) {
                event.data.toolsUsed.forEach(tool => {
                    const usage = toolUsage.get(tool) || { count: 0, successRate: 0, lastUsed: 0 };
                    usage.count++;
                    usage.lastUsed = event.timestamp;
                    if (event.data.success) {
                        usage.successRate = (usage.successRate * (usage.count - 1) + 1) / usage.count;
                    } else {
                        usage.successRate = (usage.successRate * (usage.count - 1)) / usage.count;
                    }
                    toolUsage.set(tool, usage);
                });
            }
        });

        return toolUsage;
    }

    identifyWorkingPatterns(events) {
        if (events.length === 0) return { sessionLength: 0, taskComplexity: 'medium' };

        const sessionDuration = events[events.length - 1].timestamp - events[0].timestamp;
        const sessionLength = sessionDuration / (1000 * 60); // minutes

        const taskComplexities = events
            .filter(e => e.data && e.data.complexity)
            .map(e => e.data.complexity);

        const avgComplexity = taskComplexities.length > 0 
            ? this.calculateAverageComplexity(taskComplexities)
            : 'medium';

        return {
            sessionLength,
            taskComplexity: avgComplexity,
            eventsPerMinute: events.length / Math.max(sessionLength, 1),
            toolSwitchFrequency: this.calculateToolSwitchFrequency(events)
        };
    }

    calculateBehaviorEvolution(sessionId) {
        const session = this.sessions.get(sessionId);
        if (!session || session.events.length < 10) return null;

        const events = session.events;
        const midpoint = Math.floor(events.length / 2);

        const firstHalf = events.slice(0, midpoint);
        const secondHalf = events.slice(midpoint);

        const firstHalfProfile = {
            communication: this.analyzeCommunicationPatterns(firstHalf),
            taskApproach: this.analyzeTaskApproach(firstHalf),
            toolPrefs: this.updateToolPreferences(firstHalf)
        };

        const secondHalfProfile = {
            communication: this.analyzeCommunicationPatterns(secondHalf),
            taskApproach: this.analyzeTaskApproach(secondHalf),
            toolPrefs: this.updateToolPreferences(secondHalf)
        };

        return {
            communicationEvolution: this.calculateEvolution(firstHalfProfile.communication, secondHalfProfile.communication),
            taskApproachEvolution: this.calculateEvolution(firstHalfProfile.taskApproach, secondHalfProfile.taskApproach),
            toolPreferenceEvolution: this.calculateToolPreferenceEvolution(firstHalfProfile.toolPrefs, secondHalfProfile.toolPrefs)
        };
    }

    // Helper methods for calculations
    blendMetrics(current, new_, blendFactor) {
        const result = {};
        for (const key in current) {
            if (typeof current[key] === 'number' && typeof new_[key] === 'number') {
                result[key] = current[key] * (1 - blendFactor) + new_[key] * blendFactor;
            } else {
                result[key] = new_[key] !== undefined ? new_[key] : current[key];
            }
        }
        return result;
    }

    calculateTaskPerformanceTrend(taskType) {
        const metrics = this.taskPerformanceMetrics.get(taskType);
        if (!metrics || metrics.userSatisfactionScores.length < 5) {
            return 'insufficient_data';
        }

        const scores = metrics.userSatisfactionScores;
        const recentScores = scores.slice(-5);
        const olderScores = scores.slice(-10, -5);

        if (olderScores.length === 0) return 'insufficient_data';

        const recentAvg = recentScores.reduce((a, b) => a + b) / recentScores.length;
        const olderAvg = olderScores.reduce((a, b) => a + b) / olderScores.length;

        const difference = recentAvg - olderAvg;

        if (difference > 0.3) return 'improving';
        if (difference < -0.3) return 'declining';
        return 'stable';
    }

    calculateConfidence(analysis) {
        let totalWeight = 0;
        let weightedConfidence = 0;

        Object.values(analysis.insights).forEach(insight => {
            if (insight && typeof insight === 'object') {
                const dataPoints = this.countDataPoints(insight);
                const weight = Math.min(dataPoints / 10, 1); // Max weight of 1 for 10+ data points
                totalWeight += weight;
                weightedConfidence += weight * this.calculateInsightConfidence(insight);
            }
        });

        return totalWeight > 0 ? weightedConfidence / totalWeight : 0.5;
    }

    countDataPoints(insight) {
        let count = 0;
        if (Array.isArray(insight)) count += insight.length;
        else if (typeof insight === 'object') {
            Object.values(insight).forEach(value => {
                if (Array.isArray(value)) count += value.length;
                else if (typeof value === 'number') count += 1;
            });
        }
        return count;
    }

    calculateInsightConfidence(insight) {
        // Base confidence calculation on data richness and consistency
        const dataPoints = this.countDataPoints(insight);
        const baseConfidence = Math.min(dataPoints / 20, 0.9); // Max 0.9 confidence

        // Add noise to prevent overconfidence
        const noise = (Math.random() - 0.5) * 0.1;
        return Math.max(0.1, Math.min(0.95, baseConfidence + noise));
    }

