        // Update tool effectiveness
        if (analysis.insights.toolEffectiveness) {
            Object.entries(analysis.insights.toolEffectiveness).forEach(([tool, stats]) => {
                this.analyticsCache.toolEffectiveness.set(tool, stats);
            });
        }

        // Update workflow efficiency
        if (analysis.insights.workflowPatterns) {
            const patternKey = this.generatePatternKey(analysis.insights.workflowPatterns);
            this.analyticsCache.workflowEfficiency.set(patternKey, analysis.insights.workflowPatterns.patternEffectiveness);
        }
    }

