    calculateAverageComplexity(complexities) {
        const weights = { 'low': 1, 'medium': 2, 'high': 3, 'very_high': 4 };
        const sum = complexities.reduce((acc, complexity) => acc + (weights[complexity] || 2), 0);
        const avg = sum / complexities.length;

        if (avg <= 1.5) return 'low';
        if (avg <= 2.5) return 'medium';
        if (avg <= 3.5) return 'high';
        return 'very_high';
    }

    calculateToolSwitchFrequency(events) {
        let switches = 0;
        let lastTool = null;

        events.forEach(event => {
            if (event.data && event.data.toolsUsed && event.data.toolsUsed.length > 0) {
                const currentTool = event.data.toolsUsed[0]; // Primary tool
                if (lastTool && lastTool !== currentTool) {
                    switches++;
                }
                lastTool = currentTool;
            }
        });

        return switches / Math.max(events.length - 1, 1);
    }

    calculateEvolution(first, second) {
        const evolution = {};
        for (const key in first) {
            if (typeof first[key] === 'number' && typeof second[key] === 'number') {
                evolution[key] = second[key] - first[key];
            }
        }
        return evolution;
    }

    calculateToolPreferenceEvolution(firstTools, secondTools) {
        const evolution = { increased: [], decreased: [], new: [] };

        // Check for increases/decreases in existing tools
        for (const [tool, firstUsage] of firstTools) {
            const secondUsage = secondTools.get(tool);
            if (secondUsage) {
                const change = secondUsage.count - firstUsage.count;
                if (change > 0) evolution.increased.push({ tool, change });
                else if (change < 0) evolution.decreased.push({ tool, change: Math.abs(change) });
            }
        }

        // Check for new tools
        for (const [tool] of secondTools) {
            if (!firstTools.has(tool)) {
                evolution.new.push(tool);
            }
        }

        return evolution;
    }

