            mostCommonCommunicationStyle: this.getMostCommonCommunicationStyle(),
            averageTaskApproach: this.getAverageTaskApproach(),
            topToolPreferences: this.getTopToolPreferences()
        };

        // Calculate average session length
        let totalDuration = 0;
        let sessionCount = 0;
        for (const session of this.sessions.values()) {
            if (session.events.length > 0) {
                const duration = session.events[session.events.length - 1].timestamp - session.startTime;
                totalDuration += duration;
                sessionCount++;
            }
        }
        analytics.averageSessionLength = sessionCount > 0 ? totalDuration / sessionCount : 0;

        return analytics;
    }

    generateTaskAnalytics() {
        const analytics = {
            totalTaskTypes: this.taskPerformanceMetrics.size,
            overallSuccessRate: 0,
            averageTaskDuration: 0,
            topPerformingTasks: [],
            challengingTasks: []
        };

        let totalTasks = 0;
        let totalSuccesses = 0;
        let totalDuration = 0;
        const taskRates = [];

        for (const [taskType, metrics] of this.taskPerformanceMetrics) {
            totalTasks += metrics.totalTasks;
            totalSuccesses += metrics.successfulTasks;
            totalDuration += metrics.averageDuration * metrics.totalTasks;

            taskRates.push({
                taskType,
                successRate: metrics.successfulTasks / metrics.totalTasks,
                totalTasks: metrics.totalTasks
            });
        }

        analytics.overallSuccessRate = totalTasks > 0 ? totalSuccesses / totalTasks : 0;
        analytics.averageTaskDuration = totalTasks > 0 ? totalDuration / totalTasks : 0;

        taskRates.sort((a, b) => b.successRate - a.successRate);
        analytics.topPerformingTasks = taskRates.slice(0, 5);
        analytics.challengingTasks = taskRates.slice(-5).reverse();

        return analytics;
    }

    generateToolAnalytics() {
        const analytics = {
            totalTools: this.toolUsageStats.size,
            mostUsedTools: [],
            mostEffectiveTools: [],
            toolSuccessRates: {}
        };

        const toolData = [];
        for (const [tool, stats] of this.toolUsageStats) {
            const successRate = stats.successCount / stats.usageCount;
            const avgSatisfaction = stats.userSatisfactionScores.length > 0 
                ? stats.userSatisfactionScores.reduce((a, b) => a + b) / stats.userSatisfactionScores.length 
                : null;

            toolData.push({
                tool,
                usageCount: stats.usageCount,
                successRate,
                avgSatisfaction,
                errorRate: stats.errorCount / stats.usageCount
            });

            analytics.toolSuccessRates[tool] = successRate;
        }

        toolData.sort((a, b) => b.usageCount - a.usageCount);
        analytics.mostUsedTools = toolData.slice(0, 10);

        toolData.sort((a, b) => b.successRate - a.successRate);
        analytics.mostEffectiveTools = toolData.slice(0, 10);

        return analytics;
    }

