        async function refreshData() {
            const refreshIcon = document.getElementById('refresh-icon');
            refreshIcon.innerHTML = '<div class="loading"></div>';

            try {
                const response = await fetch('/api/dashboard-data');
                const data = await response.json();
                updateDashboard(data);

                document.getElementById('last-updated').textContent = new Date().toLocaleTimeString();
            } catch (error) {
                console.error('Error refreshing data:', error);
            } finally {
                refreshIcon.innerHTML = 'ğŸ”„';
            }
        }

        function updateDashboard(data) {
            // Update metrics
            document.getElementById('improvement-cycles').textContent = data.metrics.improvementCycles || 0;
            document.getElementById('optimizations-applied').textContent = data.metrics.optimizationsApplied || 0;
            document.getElementById('active-sessions').textContent = data.metrics.activeSessions || 0;

            const uptime = Math.floor((Date.now() - (data.metrics.systemUptime || Date.now())) / (1000 * 60 * 60));
            document.getElementById('system-uptime').textContent = uptime;

            // Update other metrics if available
            if (data.userSatisfaction) {
                document.getElementById('user-satisfaction').textContent = data.userSatisfaction.toFixed(1);
            }

            if (data.performance) {
                document.getElementById('response-time').textContent = (data.performance.responseTime / 1000).toFixed(1);
            }

            if (data.patterns) {
                document.getElementById('patterns-identified').textContent = data.patterns.total || 0;
            }

            if (data.systemHealth) {
                document.getElementById('cpu-usage').textContent = Math.round(data.systemHealth.cpu * 100) + '%';
                document.getElementById('memory-usage').textContent = Math.round(data.systemHealth.memory * 100) + '%';
                document.getElementById('error-rate').textContent = (data.systemHealth.errorRate * 100).toFixed(1) + '%';
                document.getElementById('success-rate').textContent = Math.round(data.systemHealth.successRate * 100) + '%';
            }

            if (data.analytics) {
                document.getElementById('events-processed').textContent = data.analytics.eventsProcessed?.toLocaleString() || '0';
            }
        }

        function startRealtime() {
            if (realtimeEnabled) {
                updateInterval = setInterval(refreshData, 5000); // Update every 5 seconds
            }
        }

        function stopRealtime() {
            if (updateInterval) {
                clearInterval(updateInterval);
                updateInterval = null;
            }
        }

        function toggleRealtime() {
            const statusElement = document.getElementById('realtime-status');

            if (realtimeEnabled) {
                realtimeEnabled = false;
                stopRealtime();
                statusElement.textContent = 'â–¶ï¸ Resume';
            } else {
                realtimeEnabled = true;
                startRealtime();
                statusElement.textContent = 'â¸ï¸ Pause';
            }
        }

