    async loadDashboardData() {
        try {
            const filePath = path.join(this.config.dataDir, 'dashboard_state.json');
            const data = JSON.parse(await fs.readFile(filePath, 'utf8'));

            this.dashboardMetrics = { ...this.dashboardMetrics, ...data.dashboardMetrics };

