/**
 * Claude Code Behavioral Optimization - Analytics Dashboard
 * Revolutionary real-time visualization and monitoring system
 */

const fs = require('fs').promises;
const path = require('path');
const http = require('http');
const crypto = require('crypto');

class AnalyticsDashboard {
    constructor(config = {}) {
        this.config = {
            port: config.port || 8080,
            host: config.host || 'localhost',
            updateInterval: config.updateInterval || 5000, // 5 seconds
            dataRetentionHours: config.dataRetentionHours || 24,
            enableRealtime: config.enableRealtime ?? true,
            enableExport: config.enableExport ?? true,
            dataDir: config.dataDir || path.join(__dirname, '../data/dashboard'),
            ...config
        };

        // Dashboard state
        this.server = null;
        this.isRunning = false;
        this.connections = new Set();

        // Data sources
        this.analyticsEngine = null;
        this.patternRecognition = null;
        this.configManager = null;
        this.selfImprovementLoop = null;

        // Real-time data
        this.realtimeData = {
            metrics: new Map(),
            patterns: new Map(),
            optimizations: new Map(),
            systemHealth: new Map(),
            events: []
        };

        // Dashboard metrics
        this.dashboardMetrics = {
            totalUsers: 0,
            activeSessions: 0,
            improvementCycles: 0,
            optimizationsApplied: 0,
            systemUptime: Date.now(),
            lastUpdate: Date.now()
        };

        this.initialize();
    }

    async initialize() {
        console.log('ğŸ“Š Initializing Revolutionary Analytics Dashboard...');

        await this.ensureDirectories();
        await this.loadDashboardData();

        console.log('âœ… Analytics Dashboard initialized');
        console.log(`ğŸŒ Ready to serve on http://${this.config.host}:${this.config.port}`);
    }

    async ensureDirectories() {
        const dirs = [
            this.config.dataDir,
            path.join(this.config.dataDir, 'metrics'),
            path.join(this.config.dataDir, 'exports'),
            path.join(this.config.dataDir, 'snapshots')
        ];

        for (const dir of dirs) {
            try {
                await fs.mkdir(dir, { recursive: true });
            } catch (error) {
                if (error.code !== 'EEXIST') {
                    console.error(`Error creating directory ${dir}:`, error);
                }
            }
        }
    }

    /**
     * Revolutionary Feature: Start the real-time analytics dashboard
     */
    async start() {
        if (this.isRunning) {
            console.log('âš ï¸ Dashboard is already running');
            return;
        }

        console.log('ğŸš€ Starting Revolutionary Analytics Dashboard...');

        // Create HTTP server
        this.server = http.createServer((req, res) => {
            this.handleRequest(req, res);
        });

        // Start server
        await new Promise((resolve, reject) => {
            this.server.listen(this.config.port, this.config.host, (error) => {
                if (error) {
                    reject(error);
                } else {
                    resolve();
                }
            });
        });

        this.isRunning = true;

        // Start real-time data updates
        if (this.config.enableRealtime) {
            this.startRealtimeUpdates();
        }

        console.log(`âœ… Analytics Dashboard started on http://${this.config.host}:${this.config.port}`);
        console.log('ğŸ”„ Real-time updates enabled');
    }

    async stop() {
        if (!this.isRunning) {
            console.log('âš ï¸ Dashboard is not running');
            return;
        }

        console.log('ğŸ›‘ Stopping analytics dashboard...');

        // Close all connections
        for (const connection of this.connections) {
            connection.destroy();
        }
        this.connections.clear();

        // Stop server
        if (this.server) {
            await new Promise((resolve) => {
                this.server.close(resolve);
            });
        }

        // Stop real-time updates
        if (this.updateInterval) {
            clearInterval(this.updateInterval);
        }

        this.isRunning = false;
        console.log('âœ… Analytics Dashboard stopped');
    }

    async handleRequest(req, res) {
        const url = new URL(req.url, `http://${req.headers.host}`);
        const path = url.pathname;

        // Set CORS headers
        res.setHeader('Access-Control-Allow-Origin', '*');
        res.setHeader('Access-Control-Allow-Methods', 'GET, POST, OPTIONS');
        res.setHeader('Access-Control-Allow-Headers', 'Content-Type');

        try {
            if (req.method === 'OPTIONS') {
                res.writeHead(200);
                res.end();
            } else if (path === '/') {
                await this.serveDashboardHTML(res);
            } else if (path === '/api/dashboard-data') {
                await this.serveDashboardData(res);
            } else if (path === '/api/metrics') {
                await this.serveMetrics(res);
            } else if (path === '/api/patterns') {
                await this.servePatterns(res);
            } else if (path === '/api/optimizations') {
                await this.serveOptimizations(res);
            } else if (path === '/api/system-health') {
                await this.serveSystemHealth(res);
            } else if (path === '/api/realtime') {
                await this.serveRealtimeData(res);
            } else if (path === '/api/export') {
                await this.handleExport(req, res);
            } else if (path.startsWith('/static/')) {
                await this.serveStaticFile(path, res);
            } else {
                res.writeHead(404, { 'Content-Type': 'text/plain' });
                res.end('Not Found');
            }
        } catch (error) {
            console.error('Error handling request:', error);
            res.writeHead(500, { 'Content-Type': 'text/plain' });
            res.end('Internal Server Error');
        }
    }

    async serveDashboardHTML(res) {
        const html = this.generateDashboardHTML();
        res.writeHead(200, { 'Content-Type': 'text/html' });
        res.end(html);
    }

