    async stopDemo() {
        if (!this.isRunning) {
            console.log('âš ï¸ Demo is not running');
            return;
        }

        console.log('\nğŸ›‘ Stopping Behavioral Optimization Demo...');
        this.isRunning = false;

        try {
            // Stop components
            if (this.selfImprovementLoop) {
                await this.selfImprovementLoop.stop();
            }

            if (this.dashboard) {
                await this.dashboard.stop();
            }

            // Save demo data
