/**
 * Claude Code Behavioral Optimization - Demo Orchestrator
 * Revolutionary demonstration of self-improving AI system in action
 */

const fs = require('fs').promises;
const path = require('path');

// Import our optimization components
const { BehavioralEventBus } = require('./bus');
const { UsageAnalyticsEngine } = require('./analytics-engine');
const { PatternRecognitionSystem } = require('./pattern-recognition');
const { AdaptiveConfigurationManager } = require('./adaptive-config');
const { SelfImprovementLoop } = require('./self-improvement-loop');
const { AnalyticsDashboard } = require('./analytics-dashboard');

class BehavioralOptimizationDemo {
    constructor(config = {}) {
        this.config = {
            demoType: config.demoType || 'comprehensive',
            duration: config.duration || 120000, // 2 minutes default
            enableDashboard: config.enableDashboard ?? true,
            dashboardPort: config.dashboardPort || 8080,
            logLevel: config.logLevel || 'info',
            dataDir: config.dataDir || path.join(__dirname, '../data/demo'),
            ...config
        };

        // Demo components
        this.eventBus = null;
        this.analyticsEngine = null;
        this.patternRecognition = null;
        this.configManager = null;
        this.selfImprovementLoop = null;
        this.dashboard = null;

        // Demo state
        this.isRunning = false;
        this.demoStartTime = null;
        this.demoEvents = [];
        this.simulatedUsers = [];
        this.performanceMetrics = [];

        // Demo scenarios
        this.scenarios = new Map();
        this.currentScenario = null;
        this.scenarioStep = 0;

        this.initialize();
    }

    async initialize() {
        console.log('ğŸš€ Initializing Revolutionary Behavioral Optimization Demo...');

        await this.ensureDirectories();
        await this.initializeComponents();
        await this.setupDemoScenarios();

        console.log('âœ… Demo system initialized and ready');
        console.log(`ğŸ“‹ Demo type: ${this.config.demoType}`);
        console.log(`â±ï¸ Duration: ${this.config.duration / 1000} seconds`);
    }

    async ensureDirectories() {
        const dirs = [
            this.config.dataDir,
            path.join(this.config.dataDir, 'scenarios'),
            path.join(this.config.dataDir, 'results'),
            path.join(this.config.dataDir, 'metrics')
        ];

        for (const dir of dirs) {
            try {
                await fs.mkdir(dir, { recursive: true });
            } catch (error) {
                if (error.code !== 'EEXIST') {
                    console.error(`Error creating directory ${dir}:`, error);
                }
            }
        }
    }

    async initializeComponents() {
        console.log('ğŸ”§ Initializing behavioral optimization components...');

        // Initialize event bus
        this.eventBus = new BehavioralEventBus({
            dataDir: path.join(this.config.dataDir, 'events'),
            enableRealtimeProcessing: true
        });

        // Initialize analytics engine
        this.analyticsEngine = new UsageAnalyticsEngine({
            dataDir: path.join(this.config.dataDir, 'analytics'),
            enableRealtimeAnalysis: true
        });

        // Initialize pattern recognition
        this.patternRecognition = new PatternRecognitionSystem({
            dataDir: path.join(this.config.dataDir, 'patterns')
        });

        // Initialize adaptive configuration manager
        this.configManager = new AdaptiveConfigurationManager({
            dataDir: path.join(this.config.dataDir, 'config'),
            enableAutoAdaptation: true
        });

        // Initialize self-improvement loop
        this.selfImprovementLoop = new SelfImprovementLoop({
            cycleInterval: 30000, // 30 seconds for demo
            dataDir: path.join(this.config.dataDir, 'improvement')
        });

        // Initialize dashboard if enabled
        if (this.config.enableDashboard) {
            this.dashboard = new AnalyticsDashboard({
                port: this.config.dashboardPort,
                dataDir: path.join(this.config.dataDir, 'dashboard')
            });

            // Connect dashboard to components
            this.dashboard.setAnalyticsEngine(this.analyticsEngine);
            this.dashboard.setPatternRecognition(this.patternRecognition);
            this.dashboard.setConfigManager(this.configManager);
            this.dashboard.setSelfImprovementLoop(this.selfImprovementLoop);
        }

        console.log('âœ… All components initialized');
    }

    async setupDemoScenarios() {
        // Scenario 1: User Behavior Adaptation
        this.scenarios.set('user_adaptation', {
            name: 'User Behavior Adaptation',
            description: 'Demonstrates how the system learns and adapts to different user communication styles',
            duration: 60000, // 1 minute
            steps: [
                { type: 'simulate_verbose_user', duration: 15000 },
                { type: 'system_analyzes_verbosity', duration: 10000 },
                { type: 'adapt_response_style', duration: 10000 },
                { type: 'simulate_concise_user', duration: 15000 },
                { type: 'demonstrate_adaptation', duration: 10000 }
            ]
        });

        // Scenario 2: Performance Optimization
        this.scenarios.set('performance_optimization', {
            name: 'Performance Optimization',
            description: 'Shows real-time performance monitoring and automatic optimization',
            duration: 45000, // 45 seconds
            steps: [
                { type: 'simulate_performance_degradation', duration: 10000 },
                { type: 'pattern_recognition_triggers', duration: 10000 },
                { type: 'optimization_applied', duration: 15000 },
                { type: 'performance_improvement_measured', duration: 10000 }
            ]
        });

        // Scenario 3: Tool Selection Learning
        this.scenarios.set('tool_learning', {
            name: 'Tool Selection Learning',
            description: 'Demonstrates adaptive tool selection based on success patterns',
            duration: 50000, // 50 seconds
            steps: [
                { type: 'simulate_tool_usage', duration: 15000 },
                { type: 'analyze_tool_effectiveness', duration: 10000 },
                { type: 'update_tool_weights', duration: 10000 },
                { type: 'demonstrate_improved_selection', duration: 15000 }
            ]
        });

        // Scenario 4: Complete Self-Improvement Cycle
        this.scenarios.set('complete_cycle', {
            name: 'Complete Self-Improvement Cycle',
            description: 'Full demonstration of the self-improvement loop in action',
            duration: 90000, // 1.5 minutes
            steps: [
                { type: 'data_collection', duration: 15000 },
                { type: 'pattern_recognition', duration: 20000 },
                { type: 'optimization_generation', duration: 20000 },
                { type: 'safe_implementation', duration: 20000 },
                { type: 'validation_and_learning', duration: 15000 }
            ]
        });

        console.log(`ğŸ“‹ Set up ${this.scenarios.size} demo scenarios`);
    }

    /**
     * Revolutionary Feature: Start the comprehensive demonstration
     */
    async startDemo(scenarioName = null) {
        if (this.isRunning) {
            console.log('âš ï¸ Demo is already running');
            return;
        }

        console.log('ğŸ¬ Starting Revolutionary Behavioral Optimization Demo...');
        console.log('='.repeat(80));

        this.isRunning = true;
        this.demoStartTime = Date.now();

        try {
            // Start dashboard if enabled
            if (this.dashboard) {
                await this.dashboard.start();
                console.log(`ğŸ“Š Dashboard available at: http://localhost:${this.config.dashboardPort}`);
            }

            // Start self-improvement loop
            await this.selfImprovementLoop.start();

            // Determine which scenarios to run
            const scenariosToRun = scenarioName 
                ? [scenarioName] 
                : this.getScenarioSequence();

            console.log(`ğŸ¯ Running scenarios: ${scenariosToRun.join(', ')}`);
            console.log('='.repeat(80));

            // Execute scenarios
            for (const scenario of scenariosToRun) {
                if (!this.isRunning) break; // Early exit if stopped

                await this.executeScenario(scenario);

                // Brief pause between scenarios
                if (scenariosToRun.indexOf(scenario) < scenariosToRun.length - 1) {
                    await this.sleep(5000);
                }
            }

            // Generate final report
            await this.generateDemoReport();

        } catch (error) {
            console.error('âŒ Error during demo execution:', error);
        } finally {
            if (this.config.demoType !== 'continuous') {
                setTimeout(() => this.stopDemo(), 10000); // Auto-stop after 10 seconds
            }
        }
    }

    async executeScenario(scenarioName) {
        const scenario = this.scenarios.get(scenarioName);
        if (!scenario) {
            console.error(`âŒ Unknown scenario: ${scenarioName}`);
            return;
        }

        this.currentScenario = scenarioName;
        this.scenarioStep = 0;

        console.log(`\nğŸ¬ SCENARIO: ${scenario.name}`);
        console.log(`ğŸ“ ${scenario.description}`);
        console.log('-'.repeat(60));

        const scenarioStart = Date.now();

        for (const step of scenario.steps) {
            if (!this.isRunning) break;

            this.scenarioStep++;
            console.log(`\nğŸ“ Step ${this.scenarioStep}/${scenario.steps.length}: ${step.type}`);

            await this.executeScenarioStep(step);

            // Record step completion
            this.demoEvents.push({
                timestamp: Date.now(),
                scenario: scenarioName,
                step: this.scenarioStep,
                type: step.type,
                duration: step.duration
            });
        }

        const scenarioDuration = Date.now() - scenarioStart;
        console.log(`\nâœ… Scenario "${scenario.name}" completed in ${scenarioDuration}ms`);
    }

    async executeScenarioStep(step) {
        const stepStart = Date.now();

        switch (step.type) {
            case 'simulate_verbose_user':
                await this.simulateVerboseUser();
                break;
            case 'system_analyzes_verbosity':
                await this.demonstrateVerbosityAnalysis();
                break;
            case 'adapt_response_style':
                await this.demonstrateResponseAdaptation();
                break;
            case 'simulate_concise_user':
                await this.simulateConciseUser();
                break;
            case 'demonstrate_adaptation':
                await this.demonstrateStyleAdaptation();
                break;
            case 'simulate_performance_degradation':
                await this.simulatePerformanceDegradation();
                break;
            case 'pattern_recognition_triggers':
                await this.demonstratePatternRecognition();
                break;
            case 'optimization_applied':
                await this.demonstrateOptimizationApplication();
                break;
            case 'performance_improvement_measured':
                await this.demonstratePerformanceImprovement();
                break;
            case 'simulate_tool_usage':
                await this.simulateToolUsage();
                break;
            case 'analyze_tool_effectiveness':
                await this.demonstrateToolAnalysis();
