    async filterAndPrioritizeRecommendations(recommendations) {
        // Filter by confidence threshold
        let filtered = recommendations.filter(rec => rec.confidence >= this.config.confidenceThreshold);

        // Calculate priority scores
        filtered = filtered.map(rec => ({
            ...rec,
            priorityScore: this.calculatePriorityScore(rec)
        }));

        // Sort by priority score
        filtered.sort((a, b) => b.priorityScore - a.priorityScore);

        // Limit to max recommendations
        return filtered.slice(0, this.config.maxRecommendations);
    }

    calculatePriorityScore(recommendation) {
        const priorityWeights = { HIGH: 3, MEDIUM: 2, LOW: 1 };
        const difficultyWeights = { low: 3, medium: 2, high: 1 };

        const priorityWeight = priorityWeights[recommendation.priority] || 2;
        const confidenceWeight = recommendation.confidence;
        const difficultyWeight = difficultyWeights[recommendation.implementation?.difficulty] || 2;
        const impactWeight = this.calculateImpactScore(recommendation.impact);

        return (priorityWeight * 0.3 + confidenceWeight * 0.3 + difficultyWeight * 0.2 + impactWeight * 0.2);
    }

    calculateImpactScore(impact) {
        if (!impact) return 0.5;

        const values = Object.values(impact);
        const positiveImpact = values.filter(v => v > 0).reduce((sum, v) => sum + v, 0);
        const negativeImpact = Math.abs(values.filter(v => v < 0).reduce((sum, v) => sum + v, 0));

        return Math.min(positiveImpact - negativeImpact * 0.5, 1);
    }

    calculateOverallConfidence(recommendations) {
        if (recommendations.length === 0) return 0;

        const totalConfidence = recommendations.reduce((sum, rec) => sum + rec.confidence, 0);
        return totalConfidence / recommendations.length;
    }

    calculateTotalScore(recommendations) {
        return recommendations.reduce((sum, rec) => sum + (rec.priorityScore || 0), 0);
    }

