const fs = require('fs').promises;
const path = require('path');

// Import all behavioral optimization components
const { BehavioralEventBus } = require('./bus');
const { UsageAnalyticsEngine } = require('./analytics-engine');
const { PatternRecognitionSystem } = require('./pattern-recognition');
const { AdaptiveConfigurationManager } = require('./adaptive-config');
const { SelfImprovementLoop } = require('./self-improvement-loop');
const { AnalyticsDashboard } = require('./analytics-dashboard');
const { RecommendationsEngine } = require('./recommendations-engine');
const { BehavioralOptimizationDemo } = require('./demo-orchestrator');

/**
 * Main BehavioralOptimizationSystem class - orchestrates all components
 */
class BehavioralOptimizationSystem {
    constructor(config = {}) {
        this.config = {
            dataDir: config.dataDir || path.join(__dirname, '../data/behavioral-optimization'),
            enableDashboard: config.enableDashboard ?? true,
            enableRecommendations: config.enableRecommendations ?? true,
            dashboardPort: config.dashboardPort || 8080,
            cycleInterval: config.cycleInterval || 300000, // 5 minutes
            logLevel: config.logLevel || 'info',
            safeMode: config.safeMode ?? true,
            ...config
        };

        // System components
        this.eventBus = null;
        this.analyticsEngine = null;
        this.patternRecognition = null;
        this.configManager = null;
        this.selfImprovementLoop = null;
        this.dashboard = null;
        this.recommendationsEngine = null;

        // System state
        this.isRunning = false;
        this.startTime = null;
        this.systemMetrics = {
            cyclesCompleted: 0,
            optimizationsApplied: 0,
            patternsDiscovered: 0,
            userSatisfactionImprovement: 0,
            performanceImprovement: 0
        };

        // Event handlers
        this.eventHandlers = new Map();
    }

    /**
     * Initialize the complete behavioral optimization system
     */
    async initialize() {
        console.log('ğŸš€ Initializing Revolutionary Behavioral Optimization System...');
        console.log('='.repeat(80));

        try {
            await this.ensureDirectories();
            await this.initializeComponents();
            await this.setupEventHandlers();
            await this.validateSystem();

            console.log('âœ… Behavioral Optimization System fully initialized');
            console.log(`ğŸ¯ Ready for autonomous self-improvement`);
            console.log(`ğŸ“Š Dashboard: ${this.config.enableDashboard ? `http://localhost:${this.config.dashboardPort}` : 'Disabled'}`);
            console.log('='.repeat(80));

            return true;

        } catch (error) {
            console.error('âŒ Failed to initialize Behavioral Optimization System:', error);
            return false;
        }
    }

    async ensureDirectories() {
        const dirs = [
            this.config.dataDir,
            path.join(this.config.dataDir, 'events'),
            path.join(this.config.dataDir, 'analytics'),
            path.join(this.config.dataDir, 'patterns'),
            path.join(this.config.dataDir, 'config'),
            path.join(this.config.dataDir, 'improvement'),
            path.join(this.config.dataDir, 'dashboard'),
            path.join(this.config.dataDir, 'recommendations'),
            path.join(this.config.dataDir, 'logs')
        ];

        for (const dir of dirs) {
            try {
                await fs.mkdir(dir, { recursive: true });
            } catch (error) {
                if (error.code !== 'EEXIST') {
                    console.error(`Error creating directory ${dir}:`, error);
                }
            }
        }
    }

    async initializeComponents() {
        console.log('ğŸ”§ Initializing system components...');

        // Initialize Event Bus (central nervous system)
        this.eventBus = new BehavioralEventBus({
            dataDir: path.join(this.config.dataDir, 'events'),
            enableRealtimeProcessing: true
        });
        console.log('  âœ… Event Bus initialized');

        // Initialize Analytics Engine
        this.analyticsEngine = new UsageAnalyticsEngine({
            dataDir: path.join(this.config.dataDir, 'analytics'),
            enableRealtimeAnalysis: true
        });
        console.log('  âœ… Analytics Engine initialized');

        // Initialize Pattern Recognition System
        this.patternRecognition = new PatternRecognitionSystem({
            dataDir: path.join(this.config.dataDir, 'patterns'),
            enableDeepLearning: true
        });
        console.log('  âœ… Pattern Recognition System initialized');

        // Initialize Adaptive Configuration Manager
        this.configManager = new AdaptiveConfigurationManager({
            dataDir: path.join(this.config.dataDir, 'config'),
            enableAutoAdaptation: true,
            safetyMode: this.config.safeMode
        });
        console.log('  âœ… Adaptive Configuration Manager initialized');

