  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Claude Code Agent Registry",
  "description": "Central registry for all sub-agents with metadata and discovery information",
  "type": "object",
  "properties": {
    "registry_version": {
      "type": "string",
      "pattern": "^\\d+\\.\\d+\\.\\d+$",
      "description": "Semantic version of the registry format"
    },
    "last_updated": {
      "type": "string",
      "format": "date-time",
      "description": "ISO timestamp of last registry update"
    },
    "agents": {
      "type": "array",
      "items": {
        "$ref": "#/$defs/agent"
      }
    }
  },
  "required": ["registry_version", "last_updated", "agents"],
  "$defs": {
    "agent": {
      "type": "object",
      "properties": {
        "id": {
          "type": "string",
          "description": "Unique agent identifier"
        },
        "name": {
          "type": "string",
          "description": "Human-readable agent name"
        },
        "version": {
          "type": "string",
          "pattern": "^\\d+\\.\\d+\\.\\d+$",
          "description": "Semantic version of the agent"
        },
        "file_path": {
          "type": "string",
          "description": "Relative path to agent definition file"
        },
        "domain": {
          "type": "string",
          "enum": ["fundamentals", "advanced", "specializations"],
          "description": "Primary domain classification"
        },
        "subdomain": {
          "type": "string",
          "description": "Specific subdomain within primary domain"
        },
        "specialization": {
          "type": "string",
          "description": "Agent's area of expertise"
        },
        "complexity": {
          "type": "string",
          "enum": ["beginner", "intermediate", "advanced", "expert"],
          "description": "Required skill level for effective use"
        },
        "delegation_triggers": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Keywords/phrases that should trigger delegation to this agent"
        },
        "tools": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Tools this agent has access to"
        },
        "model": {
          "type": "string",
          "enum": ["haiku", "sonnet", "opus"],
          "default": "sonnet",
          "description": "Preferred Claude model for this agent"
        },
        "estimated_context": {
          "type": "integer",
          "description": "Estimated context window usage in tokens"
        },
        "dependencies": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Other components this agent depends on"
        },
        "tags": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Searchable tags for categorization"
        },
        "active": {
