{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Claude Code Hook Registry",
  "description": "Registry for all behavioral hooks with lifecycle and trigger information",
  "type": "object",
  "properties": {
    "registry_version": {
      "type": "string",
      "pattern": "^\\d+\\.\\d+\\.\\d+$"
    },
    "last_updated": {
      "type": "string",
      "format": "date-time"
    },
    "hooks": {
      "type": "array",
      "items": {
        "$ref": "#/$defs/hook"
      }
    }
  },
  "required": ["registry_version", "last_updated", "hooks"],
  "$defs": {
    "hook": {
      "type": "object",
      "properties": {
        "id": {
          "type": "string",
          "description": "Unique hook identifier"
        },
        "name": {
          "type": "string",
          "description": "Human-readable hook name"
        },
        "version": {
          "type": "string",
          "pattern": "^\\d+\\.\\d+\\.\\d+$"
        },
        "file_path": {
          "type": "string",
          "description": "Relative path to hook script"
        },
        "trigger_type": {
          "type": "string",
          "enum": [
            "UserPromptSubmit", "PreToolUse", "PostToolUse", "Notification",
            "Stop", "SubagentStop", "PreCompact", "SessionStart"
          ],
          "description": "Claude Code lifecycle event that triggers this hook"
        },
        "purpose": {
          "type": "string",
          "description": "Primary purpose and functionality"
        },
        "blocking_capability": {
          "type": "boolean",
          "description": "Whether this hook can block execution with exit code 2"
        },
        "priority": {
          "type": "integer",
          "minimum": 1,
          "maximum": 10,
          "description": "Execution priority (1=highest, 10=lowest)"
        },
        "dependencies": {
          "type": "array",
          "items": {
            "type": "string"
          }
        },
        "environment_variables": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Required environment variables"
        },
        "permissions": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Required system permissions"
        },
        "active": {
          "type": "boolean",
          "default": true
        }
      },
      "required": ["id", "name", "version", "file_path", "trigger_type", "purpose"]
    }
  },
  "registry_version": "1.0.0",
  "last_updated": "2025-01-01T00:00:00Z",
  "hooks": [
    {
      "id": "post-tool-use-analytics",
      "name": "Post Tool Use Analytics",
      "version": "1.0.0",
      "file_path": ".claude/hooks/post-tool-use.sh",
      "trigger_type": "PostToolUse",
      "purpose": "Log tool usage patterns and performance metrics",
      "blocking_capability": false,
      "priority": 5,
      "dependencies": [],
      "environment_variables": ["CLAUDE_SESSION_ID"],
      "permissions": ["write:.claude/logs"],
      "active": true
    },
    {
      "id": "session-summary",
      "name": "Session Summary Generator",
      "version": "1.0.0",
      "file_path": ".claude/hooks/stop.sh",
      "trigger_type": "Stop",
      "purpose": "Generate session summaries and learning insights",
      "blocking_capability": false,
      "priority": 3,
      "dependencies": [],
      "environment_variables": ["CLAUDE_SESSION_ID", "CLAUDE_DURATION"],
      "permissions": ["write:.claude/logs"],
      "active": true
    },
    {
      "id": "safety-validator",
      "name": "Pre-execution Safety Validator",
      "version": "1.0.0",
      "file_path": ".claude/hooks/pre-tool-use.sh",
      "trigger_type": "PreToolUse",
      "purpose": "Validate tool usage for safety and optimization",
      "blocking_capability": true,
      "priority": 1,
      "dependencies": [],
      "environment_variables": ["CLAUDE_TOOL", "CLAUDE_CONTEXT_SIZE"],
      "permissions": ["read:all"],
      "active": true
    }
  ]
}

