{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Claude Code Ecosystem Event Schemas",
  "description": "Standardized event definitions for analytics and integration",
  "type": "object",
  "properties": {
    "schema_version": {
      "type": "string",
      "pattern": "^\\d+\\.\\d+\\.\\d+$"
    }
  },
  "$defs": {
    "base_event": {
      "type": "object",
      "properties": {
        "event_id": {
          "type": "string",
          "format": "uuid",
          "description": "Unique event identifier"
        },
        "timestamp": {
          "type": "string",
          "format": "date-time",
          "description": "ISO timestamp when event occurred"
        },
        "session_id": {
          "type": "string",
          "description": "Claude Code session identifier"
        },
        "user_id": {
          "type": "string",
          "description": "User identifier (if available)"
        },
        "event_type": {
          "type": "string",
          "description": "Type of event"
        },
        "source": {
          "type": "string",
          "description": "Component that generated the event"
        }
      },
      "required": ["event_id", "timestamp", "session_id", "event_type", "source"]
    },
    "knowledge_accessed": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "knowledge.accessed"
            },
            "data": {
              "type": "object",
              "properties": {
                "article_path": {
                  "type": "string",
                  "description": "Path to the accessed knowledge article"
                },
                "domain": {
                  "type": "string",
                  "enum": ["fundamentals", "advanced", "specializations"]
                },
                "subdomain": {
                  "type": "string"
                },
                "complexity": {
                  "type": "string",
                  "enum": ["beginner", "intermediate", "advanced", "expert"]
                },
                "access_method": {
                  "type": "string",
                  "enum": ["direct", "search", "navigation", "recommendation"]
                },
                "reading_time": {
                  "type": "integer",
                  "description": "Time spent reading in seconds"
                }
              },
              "required": ["article_path", "domain", "access_method"]
            }
          }
        }
      ]
    },
    "example_started": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "example.started"
            },
            "data": {
              "type": "object",
              "properties": {
                "example_path": {
                  "type": "string"
                },
                "domain": {
                  "type": "string",
                  "enum": ["fundamentals", "advanced", "specializations"]
                },
                "complexity": {
                  "type": "string",
                  "enum": ["beginner", "intermediate", "advanced", "expert"]
                },
                "estimated_duration": {
                  "type": "integer",
                  "description": "Estimated completion time in minutes"
                },
                "prerequisites_met": {
                  "type": "boolean"
                }
              },
              "required": ["example_path", "domain", "complexity"]
            }
          }
        }
      ]
    },
    "example_completed": {
      "allOf": [
        {"$ref": "#/$defs/base_event"},
        {
          "type": "object",
          "properties": {
            "event_type": {
              "const": "example.completed"
            },
            "data": {
              "type": "object",
              "properties": {
                "example_path": {
                  "type": "string"
