#!/usr/bin/env node

/**
 * Claude Code Ecosystem Event Bus
 * Central event dispatcher for analytics and system integration
 */

const fs = require('fs').promises;
const path = require('path');
const { v4: uuidv4 } = require('uuid');

class EventBus {
    constructor(config = {}) {
        this.config = {
            logDir: config.logDir || '.claude/logs/events',
            maxLogSize: config.maxLogSize || 10 * 1024 * 1024, // 10MB
            retentionDays: config.retentionDays || 30,
            enableConsoleLog: config.enableConsoleLog || false,
            ...config
        };

        this.handlers = new Map();
        this.eventQueue = [];
        this.processing = false;

        this.initializeLogDirectory();
    }

    async initializeLogDirectory() {
        try {
            await fs.mkdir(this.config.logDir, { recursive: true });
        } catch (error) {
            console.error('Failed to create event log directory:', error);
        }
    }

    /**
     * Register an event handler
     * @param {string} eventType - Type of event to handle
     * @param {function} handler - Handler function
     * @param {object} options - Handler options
     */
    on(eventType, handler, options = {}) {
        if (!this.handlers.has(eventType)) {
            this.handlers.set(eventType, []);
        }

        this.handlers.get(eventType).push({
            handler,
            priority: options.priority || 5,
            async: options.async !== false,
            id: uuidv4()
        });

        // Sort by priority (lower number = higher priority)
        this.handlers.get(eventType).sort((a, b) => a.priority - b.priority);
    }

    /**
     * Remove an event handler
     * @param {string} eventType - Type of event
     * @param {string} handlerId - Handler ID returned by on()
     */
    off(eventType, handlerId) {
        if (this.handlers.has(eventType)) {
            const handlers = this.handlers.get(eventType);
            const index = handlers.findIndex(h => h.id === handlerId);
            if (index !== -1) {
                handlers.splice(index, 1);
            }
        }
    }

    /**
     * Emit an event
     * @param {string} eventType - Type of event
     * @param {object} eventData - Event data payload
     * @param {object} metadata - Additional metadata
     */
    async emit(eventType, eventData = {}, metadata = {}) {
        const event = this.createEvent(eventType, eventData, metadata);

        // Log event
        await this.logEvent(event);

        // Add to processing queue
        this.eventQueue.push(event);

        // Process queue if not already processing
        if (!this.processing) {
            this.processQueue();
        }

        return event.event_id;
    }

    /**
     * Create standardized event object
     * @param {string} eventType - Type of event
     * @param {object} eventData - Event data
     * @param {object} metadata - Additional metadata
     */
    createEvent(eventType, eventData, metadata) {
        return {
            event_id: uuidv4(),
            timestamp: new Date().toISOString(),
            session_id: process.env.CLAUDE_SESSION_ID || 'unknown',
            user_id: process.env.USER || process.env.USERNAME || 'unknown',
            event_type: eventType,
            source: metadata.source || 'claude-code-ecosystem',
            data: eventData,
            metadata: {
                node_version: process.version,
                platform: process.platform,
                ...metadata
            }
        };
    }

    /**
     * Process event queue
     */
    async processQueue() {
        this.processing = true;

        while (this.eventQueue.length > 0) {
            const event = this.eventQueue.shift();
            await this.processEvent(event);
        }

        this.processing = false;
    }

    /**
     * Process individual event
     * @param {object} event - Event to process
     */
    async processEvent(event) {
        const handlers = this.handlers.get(event.event_type) || [];
        const handlers_all = this.handlers.get('*') || [];

        const allHandlers = [...handlers, ...handlers_all];

        if (this.config.enableConsoleLog) {
            console.log(`[EventBus] Processing ${event.event_type}:`, event.event_id);
        }

        // Execute handlers
        for (const handlerInfo of allHandlers) {
            try {
                if (handlerInfo.async) {
                    // Non-blocking async execution
                    handlerInfo.handler(event).catch(error => {
                        console.error(`Event handler error for ${event.event_type}:`, error);
                    });
                } else {
                    // Blocking execution
                    await handlerInfo.handler(event);
                }
            } catch (error) {
                console.error(`Event handler error for ${event.event_type}:`, error);
            }
        }
    }

    /**
     * Log event to file system
     * @param {object} event - Event to log
     */
    async logEvent(event) {
        try {
            const logFile = path.join(
                this.config.logDir,
                `${event.event_type.replace('.', '_')}.jsonl`
            );

            const logEntry = JSON.stringify(event) + '\n';
            await fs.appendFile(logFile, logEntry);

            // Check file size and rotate if necessary
            await this.rotateLogIfNeeded(logFile);

        } catch (error) {
            console.error('Failed to log event:', error);
        }
    }

    /**
     * Rotate log file if it exceeds maximum size
     * @param {string} logFile - Path to log file
     */
    async rotateLogIfNeeded(logFile) {
        try {
            const stats = await fs.stat(logFile);
            if (stats.size > this.config.maxLogSize) {
                const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
                const rotatedFile = `${logFile}.${timestamp}`;
                await fs.rename(logFile, rotatedFile);
            }
        } catch (error) {
            // File doesn't exist or other error, ignore
        }
    }

    /**
     * Clean up old log files
     */
    async cleanupLogs() {
        try {
            const files = await fs.readdir(this.config.logDir);
            const cutoffDate = new Date();
            cutoffDate.setDate(cutoffDate.getDate() - this.config.retentionDays);

            for (const file of files) {
                if (file.includes('.jsonl.')) {
                    const filePath = path.join(this.config.logDir, file);
                    const stats = await fs.stat(filePath);

                    if (stats.mtime < cutoffDate) {
                        await fs.unlink(filePath);
                        console.log(`Cleaned up old log file: ${file}`);
                    }
                }
            }
        } catch (error) {
            console.error('Failed to cleanup logs:', error);
        }
    }

    /**
     * Get event statistics
     * @param {string} eventType - Optional event type filter
     * @param {Date} since - Optional date filter
     */
    async getStats(eventType = null, since = null) {
        const stats = {
            total_events: 0,
            events_by_type: {},
            events_by_hour: {},
            latest_event: null,
            oldest_event: null
        };

        try {
            const files = await fs.readdir(this.config.logDir);

            for (const file of files) {
                if (!file.endsWith('.jsonl')) continue;

                const filePath = path.join(this.config.logDir, file);
                const content = await fs.readFile(filePath, 'utf8');
                const lines = content.trim().split('\n').filter(line => line);

                for (const line of lines) {
                    try {
                        const event = JSON.parse(line);
                        const eventDate = new Date(event.timestamp);

                        // Apply filters
                        if (eventType && event.event_type !== eventType) continue;
                        if (since && eventDate < since) continue;

                        stats.total_events++;

                        // Count by type
                        stats.events_by_type[event.event_type] = 
                            (stats.events_by_type[event.event_type] || 0) + 1;

                        // Count by hour
                        const hour = eventDate.toISOString().substring(0, 13);
                        stats.events_by_hour[hour] = 
                            (stats.events_by_hour[hour] || 0) + 1;

                        // Track latest/oldest
                        if (!stats.latest_event || eventDate > new Date(stats.latest_event.timestamp)) {
                            stats.latest_event = event;
                        }
                        if (!stats.oldest_event || eventDate < new Date(stats.oldest_event.timestamp)) {
                            stats.oldest_event = event;
                        }

                    } catch (parseError) {
                        // Skip malformed lines
                    }
                }
            }
        } catch (error) {
            console.error('Failed to generate stats:', error);
        }

        return stats;
    }

    /**
     * Query events with filters
     * @param {object} options - Query options
     */
    async queryEvents(options = {}) {
        const {
            eventType = null,
            since = null,
            until = null,
            limit = 100,
            offset = 0
        } = options;

        const events = [];

        try {
            const files = await fs.readdir(this.config.logDir);

            for (const file of files) {
                if (!file.endsWith('.jsonl')) continue;

                const filePath = path.join(this.config.logDir, file);
                const content = await fs.readFile(filePath, 'utf8');
                const lines = content.trim().split('\n').filter(line => line);

                for (const line of lines) {
                    try {
                        const event = JSON.parse(line);
                        const eventDate = new Date(event.timestamp);

                        // Apply filters
                        if (eventType && event.event_type !== eventType) continue;
                        if (since && eventDate < since) continue;
                        if (until && eventDate > until) continue;

                        events.push(event);

                    } catch (parseError) {
                        // Skip malformed lines
                    }
                }
            }
        } catch (error) {
            console.error('Failed to query events:', error);
        }

        // Sort by timestamp (newest first)
        events.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

        // Apply pagination
        return events.slice(offset, offset + limit);
    }
}

// Export singleton instance
let eventBus = null;

function getEventBus(config = {}) {
    if (!eventBus) {
        eventBus = new EventBus(config);
    }
    return eventBus;
}

// CLI interface
if (require.main === module) {
    const command = process.argv[2];
    const bus = getEventBus();

    switch (command) {
        case 'stats':
            bus.getStats().then(stats => {
                console.log(JSON.stringify(stats, null, 2));
            });
            break;

        case 'query':
            const eventType = process.argv[3];
            bus.queryEvents({ eventType, limit: 10 }).then(events => {
                events.forEach(event => {
                    console.log(`${event.timestamp} [${event.event_type}] ${event.event_id}`);
                });
            });
            break;

        case 'emit':
            const type = process.argv[3];
            const data = JSON.parse(process.argv[4] || '{}');
            bus.emit(type, data).then(eventId => {
                console.log(`Emitted event: ${eventId}`);
            });
            break;

        case 'cleanup':
            bus.cleanupLogs().then(() => {
                console.log('Log cleanup completed');
            });
            break;

        default:
            console.log('Usage: node bus.js [stats|query|emit|cleanup]');
            break;
    }
}

module.exports = { EventBus, getEventBus };

