#!/usr/bin/env python3
"""
Validate all links in Codex resolve to existing files
"""
import re
from pathlib import Path

def main():
    codex_path = Path(".claude/codex/CLAUDE-CODEX.md")

    with open(codex_path, 'r', encoding='utf-8') as f:
        content = f.read()

    # Extract all markdown links
    links = re.findall(r'\[([^\]]+)\]\(([^)]+)\)', content)

    missing_links = []
    total_links = 0

    for title, link in links:
        if link.startswith('.claude/rules/v2/'):
            total_links += 1
            if not Path(link).exists():
                missing_links.append(link)

    if missing_links:
        print(f"Missing links found:")
        for link in missing_links:
            print(f"  - {link}")
        return False
    else:
        print(f"All {total_links} rule links resolve correctly")
        return True

if __name__ == "__main__":
    success = main()
    exit(0 if success else 1)

